/**
 * API
 * Contact form API validation and data processing
 */

import { z } from 'zod';
import { airtableService } from '@/lib/airtable';
import { logger } from '@/lib/logger';
import { resendService } from '@/lib/resend';
import { contactFormSchema, type ContactFormData } from '@/lib/validations';
import { verifyTurnstile } from '@/app/api/contact/contact-api-utils';
import {
  ANIMATION_DURATION_VERY_SLOW,
  COUNT_TEN,
  DAYS_PER_WEEK,
  ONE,
  SECONDS_PER_MINUTE,
  ZERO,
} from '@/constants';

/**
 * Turnstile token
 * Extended contact form schema with Turnstile token
 */
export const contactFormWithTokenSchema = contactFormSchema.extend({
  turnstileToken: z.string().min(ONE, 'Security verification required'),
  submittedAt: z.string(),
});

export type ContactFormWithToken = z.infer<typeof contactFormWithTokenSchema>;

/**
 * 
 * Validate form data
 */
export async function validateFormData(body: unknown, clientIP: string) {
  const validationResult = contactFormWithTokenSchema.safeParse(body);

  if (!validationResult.success) {
    logger.warn('Form validation failed', {
      errors: validationResult.error.issues,
      clientIP,
    });

    const errorMessages = validationResult.error.issues.map(
      (error: z.ZodIssue) => {
        const field = error.path.join('.');
        return `${field}: ${error.message}`;
      },
    );

    return {
      success: false,
      error: 'Validation failed',
      details: errorMessages,
      data: null,
    };
  }

  const formData = validationResult.data;

  // 
  const submittedAt = new Date(formData.submittedAt);
  const now = new Date();
  const timeDiff = now.getTime() - submittedAt.getTime();
  const maxAge = COUNT_TEN * SECONDS_PER_MINUTE * ANIMATION_DURATION_VERY_SLOW; // COUNT_TEN

  if (timeDiff > maxAge || timeDiff < ZERO) {
    logger.warn('Form submission time validation failed', {
      submittedAt: formData.submittedAt,
      timeDiff,
      clientIP,
    });

    return {
      success: false,
      error: 'Form submission expired or invalid',
      details: ['Please refresh the page and try again'],
      data: null,
    };
  }

  // Turnstile token
  const turnstileValid = await verifyTurnstile(
    formData.turnstileToken,
    clientIP,
  );
  if (!turnstileValid) {
    logger.warn('Turnstile verification failed', { clientIP });
    return {
      success: false,
      error: 'Security verification failed',
      details: ['Please complete the security check'],
      data: null,
    };
  }

  return {
    success: true,
    error: null,
    details: null,
    data: formData,
  };
}

/**
 * 
 * Process form submission
 */
export async function processFormSubmission(
  formData: ContactFormData & { turnstileToken: string; submittedAt: string },
) {
  const emailData = {
    firstName: formData.firstName,
    lastName: formData.lastName,
    email: formData.email,
    company: formData.company,
    phone: formData.phone,
    subject: formData.subject,
    message: formData.message,
    submittedAt: formData.submittedAt,
  };

  // 
  const [emailResult, airtableResult] = await Promise.allSettled([
    resendService.sendContactFormEmail(emailData),
    airtableService.createContact({
      firstName: formData.firstName,
      lastName: formData.lastName,
      email: formData.email,
      company: formData.company || '',
      phone: formData.phone || '',
      subject: formData.subject,
      message: formData.message,
      acceptPrivacy: true, // true
      marketingConsent: false, // 
      website: '', // 
    }),
  ]);

  // 
  const emailSuccess = emailResult.status === 'fulfilled';
  const airtableSuccess = airtableResult.status === 'fulfilled';

  if (!emailSuccess) {
    logger.error('Email sending failed', {
      error: emailResult.reason,
      formData: {
        email: formData.email,
        subject: formData.subject,
      },
    });
  }

  if (!airtableSuccess) {
    logger.error('Airtable record creation failed', {
      error: airtableResult.reason,
      formData: {
        email: formData.email,
        subject: formData.subject,
      },
    });
  }

  // 
  const emailMessageId =
    emailSuccess && emailResult.status === 'fulfilled'
      ? emailResult.value
      : null;
  const airtableRecordId =
    airtableSuccess && airtableResult.status === 'fulfilled'
      ? airtableResult.value?.id
      : null;

  // 
  if (emailSuccess || airtableSuccess) {
    logger.info('Contact form submitted successfully', {
      email: formData.email,
      subject: formData.subject,
      emailSuccess,
      airtableSuccess,
      emailMessageId,
      airtableRecordId,
    });

    return {
      success: true,
      emailSent: emailSuccess,
      recordCreated: airtableSuccess,
      emailMessageId,
      airtableRecordId,
    };
  }

  // 
  logger.error('Contact form submission failed completely', {
    emailError: emailResult.status === 'rejected' ? emailResult.reason : null,
    airtableError:
      airtableResult.status === 'rejected' ? airtableResult.reason : null,
    formData: {
      email: formData.email,
      subject: formData.subject,
    },
  });

  throw new Error('Failed to process form submission');
}

/**
 * 
 * Get contact form statistics
 */
export async function getContactFormStats() {
  try {
    if (!airtableService.isReady()) {
      // 
      return {
        success: true,
        data: {
          totalContacts: ZERO,
          newContacts: ZERO,
          completedContacts: ZERO,
          recentContacts: ZERO,
        },
      };
    }

    const stats = await airtableService.getStatistics();

    // 
    const defaultStats = {
      totalContacts: ZERO,
      newContacts: ZERO,
      completedContacts: ZERO,
      recentContacts: ZERO,
    };

    const normalizedStats = stats ? { ...defaultStats, ...stats } : defaultStats;

    return {
      success: true,
      data: normalizedStats,
    };
  } catch (error) {
    logger.error('Failed to get contact form stats', { error });
    throw new Error('Failed to retrieve statistics');
  }
}

/**
 * 
 * Validate admin permissions
 */
export function validateAdminAccess(authHeader: string | null): boolean {
  const adminToken = process.env.ADMIN_API_TOKEN;

  if (!adminToken) {
    logger.warn('Admin API token not configured');
    return false;
  }

  if (!authHeader || !authHeader.startsWith('Bearer ')) {
    return false;
  }

  const token = authHeader.substring(7); // Remove 'Bearer ' prefix
  return token === adminToken;
}

/**
 * 
 * Clean and normalize form data
 */
export function sanitizeFormData(
  data: ContactFormWithToken,
): ContactFormWithToken {
  return {
    ...data,
    firstName: data.firstName.trim(),
    lastName: data.lastName.trim(),
    email: data.email.toLowerCase().trim(),
    company: data.company?.trim() || '',
    phone: data.phone?.trim() || '',
    subject: data.subject?.trim() || '',
    message: data.message.trim(),
  };
}

import { NextRequest, NextResponse } from 'next/server';
import { logger } from '@/lib/logger';
import { env } from '@/lib/env';
import type { CSPReport } from '@/config/security';

/**
 * CSP Report endpoint
 *
 * This endpoint receives Content Security Policy violation reports
 * and logs them for security monitoring and debugging.
 */
const isDevIgnored = () =>
  env.NODE_ENV === 'development' && !env.CSP_REPORT_URI;
const isContentTypeValid = (ct: string | null) =>
  Boolean(ct && ct.includes('application/csp-report'));
const getClientIp = (request: NextRequest) =>
  request.headers.get('x-forwarded-for') ||
  request.headers.get('x-real-ip') ||
  'unknown';
const buildViolationData = (
  request: NextRequest,
  cspReport: CSPReport['csp-report'],
) => ({
  timestamp: new Date().toISOString(),
  documentUri: cspReport['document-uri'],
  referrer: cspReport.referrer,
  violatedDirective: cspReport['violated-directive'],
  effectiveDirective: cspReport['effective-directive'],
  originalPolicy: cspReport['original-policy'],
  blockedUri: cspReport['blocked-uri'],
  lineNumber: cspReport['line-number'],
  columnNumber: cspReport['column-number'],
  sourceFile: cspReport['source-file'],
  statusCode: cspReport['status-code'],
  scriptSample: cspReport['script-sample'],
  disposition: cspReport.disposition,
  userAgent: request.headers.get('user-agent'),
  ip: getClientIp(request),
});
const isSuspiciousReport = (csp: CSPReport['csp-report']) => {
  const patterns = [
    'eval',
    'data:text/html',
    'vbscript:',
    'onload',
    'onerror',
    'onclick',
  ];
  const blocked = csp['blocked-uri']?.toLowerCase() || '';
  const sample = csp['script-sample']?.toLowerCase() || '';
  return patterns.some((p) => blocked.includes(p) || sample.includes(p));
};

async function handleCspRequest(request: NextRequest) {
  try {
    if (isDevIgnored())
      return NextResponse.json({ status: 'ignored' }, { status: 200 });

    const contentType = request.headers.get('content-type');
    if (!isContentTypeValid(contentType)) {
      return NextResponse.json(
        { error: 'Unsupported Media Type' },
        { status: 400 },
      );
    }

    let report: CSPReport;
    try {
      const body = await request.text();
      if (!body.trim()) {
        return NextResponse.json(
          { error: 'Invalid CSP report format' },
          { status: 500 },
        );
      }
      report = JSON.parse(body);
    } catch (jsonError) {
      return NextResponse.json(
        { error: 'Invalid JSON format' },
        { status: 500 },
      );
    }

    const cspReport = report['csp-report'];
    if (!cspReport)
      return NextResponse.json(
        { error: 'Invalid CSP report format' },
        { status: 400 },
      );

    // csp-report
    if (Object.keys(cspReport).length === 0) {
      return NextResponse.json(
        { error: 'Invalid CSP report format' },
        { status: 200 },
      );
    }

    const violationData = buildViolationData(request, cspReport);
    logger.warn(
      'CSP Violation Report:',
      JSON.stringify(violationData, null, 2),
    );

    if (env.NODE_ENV === 'production') {
      logger.error('Production CSP Violation:', violationData);
    }

    if (isSuspiciousReport(cspReport)) {
      logger.error('SUSPICIOUS CSP VIOLATION DETECTED:', violationData);
    }

    return NextResponse.json(
      { status: 'received', timestamp: violationData.timestamp },
      { status: 200 },
    );
  } catch (error) {
    logger.error('Error processing CSP report:', error as unknown);
    return NextResponse.json(
      { error: 'Internal server error' },
      { status: 500 },
    );
  }
}

export function POST(request: NextRequest) {
  return handleCspRequest(request);
}

/**
 * Handle GET requests (for health checks)
 */
export function GET() {
  return NextResponse.json(
    {
      status: 'CSP report endpoint active',
      timestamp: new Date().toISOString(),
    },
    { status: 200 },
  );
}

/**
 * Only allow POST and GET methods
 */
export function OPTIONS() {
  return new NextResponse(null, {
    status: 200,
    headers: {
      'Allow': 'POST, GET, OPTIONS',
      'Access-Control-Allow-Methods': 'POST, GET, OPTIONS',
      'Access-Control-Allow-Headers': 'Content-Type',
    },
  });
}

