            <Button
              _onClick={stopRecording}
              size='sm'
              variant='destructive'
            >
              Stop Recording
            </Button>
          )}
          <Button
            _onClick={clearData}
            size='sm'
            variant='outline'
          >
            Clear
          </Button>
        </div>
      </div>

      {scanData && (
        <div className='space-y-4'>
