  lastRenderTime: number;
}

interface ReactScanGlobal {
  components: Record<string, ReactScanGlobalData>;
}

//  Window  React Scan 
declare global {
  interface Window {
    __REACT_SCAN__?: ReactScanGlobal;
  }
}

export interface ComponentStats {
  name: string;
  renderCount: number;
  renderTime: number;
  lastRenderTime: number;
  avgRenderTime: number;
  efficiency: number;
  timestamp: number;
}

