import { render, screen } from '@testing-library/react';
import userEvent from '@testing-library/user-event';
import { beforeEach, describe, expect, it } from 'vitest';
import {
    ExternalLinkIcon,
    LinkedInIcon,
    SocialIconLink,
    SocialIconMapper,
    TwitterIcon,
} from '../social-icons';

describe('Social Icons Accessibility & Integration - Advanced Tests', () => {
  let user: ReturnType<typeof userEvent.setup>;

  beforeEach(() => {
    user = userEvent.setup();
  });

  describe('Complex Integration Scenarios', () => {
    it('handles advanced accessibility patterns with screen readers', async () => {
      render(
        <section aria-labelledby='social-heading'>
          <h2 id='social-heading'>Connect With Us</h2>
          <div
            role='list'
            aria-label='Social media links'
          >
            <div role='listitem'>
              <SocialIconLink
                href='https://twitter.com/test'
                platform='twitter'
                aria-label='Follow us on Twitter'
              >
                <TwitterIcon />
              </SocialIconLink>
            </div>
            <div role='listitem'>
              <SocialIconLink
                href='https://linkedin.com/in/test'
                platform='linkedin'
                aria-label='Connect with us on LinkedIn'
              >
                <LinkedInIcon />
              </SocialIconLink>
            </div>
          </div>
        </section>,
      );

      const section = screen.getByRole('region');
      const heading = screen.getByRole('heading', { name: 'Connect With Us' });
      const socialList = screen.getByRole('list', {
        name: 'Social media links',
      });
      const listItems = screen.getAllByRole('listitem');

      expect(section).toHaveAttribute('aria-labelledby', 'social-heading');
      expect(heading).toBeInTheDocument();
      expect(socialList).toBeInTheDocument();
      expect(listItems).toHaveLength(2);

      // Test keyboard navigation through the list
      const links = screen.getAllByRole('link');
      for (const link of links) {
        link.focus();
        expect(link).toHaveFocus();
        await user.tab();
      }
    });

    it('handles complex dynamic social media integration', async () => {
      const socialPlatforms = [
        {
          name: 'twitter',
          url: 'https://twitter.com/company',
          icon: TwitterIcon,
        },
        {
          name: 'linkedin',
          url: 'https://linkedin.com/company/test',
          icon: LinkedInIcon,
        },
        {
          name: 'external',
          url: 'https://blog.company.com',
          icon: ExternalLinkIcon,
        },
      ];

      render(
        <div>
          <h3>Follow Our Journey</h3>
          <div className='social-grid'>
            {socialPlatforms.map(({ name, url, icon: IconComponent }) => (
              <div
                key={name}
                className='social-item'
              >
                <SocialIconLink
                  href={url}
                  platform={name}
                  aria-label={`Follow us on ${name}`}
                >
                  <IconComponent />
                </SocialIconLink>
                <span className='platform-name'>{name}</span>
              </div>
            ))}
          </div>
        </div>,
      );

      // Test all platforms are rendered
      const links = screen.getAllByRole('link');
      expect(links).toHaveLength(3);

      // Test each platform has correct attributes
      socialPlatforms.forEach(({ name, url }) => {
        const link = screen.getByRole('link', { name: new RegExp(name, 'i') });
        expect(link).toHaveAttribute('href', url);
        expect(link).toHaveAttribute('target', '_blank');
      });
    });
  });

  describe('External Link Attributes', () => {
    it('handles external link attributes correctly', () => {
      render(
        <SocialIconLink
          href='https://twitter.com/example'
          platform='twitter'
          aria-label='Twitter'
          data-testid='external-link'
        />,
      );

      const link = screen.getByTestId('external-link');
      expect(link).toHaveAttribute('target', '_blank');
      expect(link).toHaveAttribute('rel', 'noopener noreferrer');
    });

    it('supports keyboard navigation between links', async () => {
      render(
        <div>
          <SocialIconLink
            href='https://twitter.com/example'
            platform='twitter'
            aria-label='Twitter profile'
            data-testid='twitter-link'
          />
          <SocialIconLink
            href='https://linkedin.com/in/example'
            platform='linkedin'
            aria-label='LinkedIn profile'
            data-testid='linkedin-link'
          />
        </div>,
      );

      const twitterLink = screen.getByTestId('twitter-link');
      const linkedinLink = screen.getByTestId('linkedin-link');

      // Should be able to tab between links
      await user.tab();
      expect(twitterLink).toHaveFocus();

      await user.tab();
      expect(linkedinLink).toHaveFocus();
    });

    it('supports high contrast mode', () => {
      render(
        <TwitterIcon
          className='forced-colors:text-[ButtonText]'
          data-testid='twitter'
        />,
      );

      const icon = screen.getByTestId('twitter');
      expect(icon).toHaveClass('forced-colors:text-[ButtonText]');
    });

    it('supports reduced motion preferences', () => {
      render(
        <SocialIconLink
          href='https://twitter.com/example'
          platform='twitter'
          aria-label='Twitter'
          className='motion-reduce:transition-none'
          data-testid='social-link'
        />,
      );

      const link = screen.getByTestId('social-link');
      expect(link).toHaveClass('motion-reduce:transition-none');
    });

    it('provides proper focus indicators', () => {
      render(
        <SocialIconLink
          href='https://twitter.com/example'
          platform='twitter'
          aria-label='Twitter'
          className='focus:ring-2 focus:ring-offset-2'
          data-testid='social-link'
        />,
      );

      const link = screen.getByTestId('social-link');
      expect(link).toHaveClass('focus:ring-2', 'focus:ring-offset-2');
    });

    it('handles keyboard activation', async () => {
      render(
        <SocialIconLink
          href='https://twitter.com/example'
          platform='twitter'
          aria-label='Twitter'
          data-testid='social-link'
        />,
      );

      const link = screen.getByTestId('social-link');

      await user.tab();
      expect(link).toHaveFocus();

      // For links, keyboard activation (Enter) triggers navigation
      // We can verify the link is focusable and has correct href
      expect(link).toHaveAttribute('href', 'https://twitter.com/example');
      expect(link).toHaveAttribute('target', '_blank');
    });

    it('supports internationalization', () => {
      render(
        <div lang='es'>
          <SocialIconLink
            href='https://twitter.com/example'
            platform='twitter'
            aria-label='Sguenos en Twitter'
            data-testid='social-link'
          />
        </div>,
      );

      const link = screen.getByTestId('social-link');
      expect(link).toHaveAttribute('aria-label', 'Sguenos en Twitter');
    });

    it('works in navigation menus', () => {
      render(
        <nav aria-label='Social media links'>
          <ul>
            <li>
              <SocialIconLink
                href='https://twitter.com/example'
                platform='twitter'
                aria-label='Twitter'
                data-testid='nav-twitter'
              />
            </li>
            <li>
              <SocialIconLink
                href='https://linkedin.com/in/example'
                platform='linkedin'
                aria-label='LinkedIn'
                data-testid='nav-linkedin'
              />
            </li>
          </ul>
        </nav>,
      );

      const nav = screen.getByRole('navigation');
      expect(nav).toHaveAttribute('aria-label', 'Social media links');

      const twitterLink = screen.getByTestId('nav-twitter');
      const linkedinLink = screen.getByTestId('nav-linkedin');

      expect(twitterLink).toBeInTheDocument();
      expect(linkedinLink).toBeInTheDocument();
    });

    it('works in footer sections', () => {
      render(
        <footer>
          <section aria-labelledby='social-heading'>
            <h3 id='social-heading'>Follow Us</h3>
            <div className='flex space-x-4'>
              <SocialIconLink
                href='https://twitter.com/example'
                platform='twitter'
                aria-label='Twitter'
                data-testid='footer-twitter'
              />
              <SocialIconLink
                href='https://linkedin.com/in/example'
                platform='linkedin'
                aria-label='LinkedIn'
                data-testid='footer-linkedin'
              />
            </div>
          </section>
        </footer>,
      );

      const section = screen.getByRole('region');
      expect(section).toHaveAttribute('aria-labelledby', 'social-heading');

      const heading = screen.getByText('Follow Us');
      expect(heading).toBeInTheDocument();

      const twitterLink = screen.getByTestId('footer-twitter');
      const linkedinLink = screen.getByTestId('footer-linkedin');

      expect(twitterLink).toBeInTheDocument();
      expect(linkedinLink).toBeInTheDocument();
    });

    it('works with dynamic platform mapping', () => {
      const platforms = ['twitter', 'linkedin', 'unknown'];

      render(
        <div>
          {platforms.map((platform) => (
            <SocialIconMapper
              key={platform}
              platform={platform}
              data-testid={`dynamic-${platform}`}
            />
          ))}
        </div>,
      );

      platforms.forEach((platform) => {
        const icon = screen.getByTestId(`dynamic-${platform}`);
        expect(icon).toBeInTheDocument();
      });
    });

    it('handles responsive layouts', () => {
      render(
        <div className='flex flex-col space-y-2 sm:flex-row sm:space-y-0 sm:space-x-4'>
          <SocialIconLink
            href='https://twitter.com/example'
            platform='twitter'
            aria-label='Twitter'
            className='w-full sm:w-auto'
            data-testid='responsive-twitter'
          />
          <SocialIconLink
            href='https://linkedin.com/in/example'
            platform='linkedin'
            aria-label='LinkedIn'
            className='w-full sm:w-auto'
            data-testid='responsive-linkedin'
          />
        </div>,
      );

      const twitterLink = screen.getByTestId('responsive-twitter');
      const linkedinLink = screen.getByTestId('responsive-linkedin');

      expect(twitterLink).toHaveClass('w-full', 'sm:w-auto');
      expect(linkedinLink).toHaveClass('w-full', 'sm:w-auto');
    });

    it('supports theming and customization', () => {
      render(
        <div className='dark'>
          <SocialIconLink
            href='https://twitter.com/example'
            platform='twitter'
            aria-label='Twitter'
            className='text-gray-600 hover:text-blue-500 dark:text-gray-300 dark:hover:text-blue-400'
            data-testid='themed-link'
          />
        </div>,
      );

      const link = screen.getByTestId('themed-link');
      expect(link).toHaveClass(
        'text-gray-600',
        'dark:text-gray-300',
        'hover:text-blue-500',
        'dark:hover:text-blue-400',
      );
    });

    it('works with form integration', () => {
      render(
        <form>
          <fieldset>
            <legend>Social Media Profiles</legend>
            <div>
              <label htmlFor='twitter-url'>Twitter URL</label>
              <input
                id='twitter-url'
                type='url'
              />
              <SocialIconMapper
                platform='twitter'
                className='ml-2'
              />
            </div>
            <div>
              <label htmlFor='linkedin-url'>LinkedIn URL</label>
              <input
                id='linkedin-url'
                type='url'
              />
              <SocialIconMapper
                platform='linkedin'
                className='ml-2'
              />
            </div>
          </fieldset>
        </form>,
      );

      const twitterInput = screen.getByLabelText('Twitter URL');
      const linkedinInput = screen.getByLabelText('LinkedIn URL');

      expect(twitterInput).toBeInTheDocument();
      expect(linkedinInput).toBeInTheDocument();
    });

    it('handles error states gracefully', () => {
      // Test with invalid props
      expect(() => {
        render(
          <div>
            <SocialIconMapper platform='' />
            <SocialIconLink
              href=''
              platform='twitter'
              aria-label='Twitter'
            />
          </div>,
        );
      }).not.toThrow();
    });

    it('supports server-side rendering', () => {
      // Test that components render without client-side JavaScript
      const { container } = render(
        <div>
          <TwitterIcon />
          <LinkedInIcon />
          <ExternalLinkIcon />
          <SocialIconMapper platform='twitter' />
          <SocialIconLink
            href='https://twitter.com/example'
            platform='twitter'
            aria-label='Twitter'
          />
        </div>,
      );

      expect(container.innerHTML).toBeTruthy();
      expect(container.querySelectorAll('svg')).toHaveLength(5);
    });

    it('maintains performance with many icons', () => {
      const manyIcons = Array.from({ length: 50 }, (_, i) => (
        <SocialIconMapper
          key={i}
          platform={i % 2 === 0 ? 'twitter' : 'linkedin'}
          data-testid={`icon-${i}`}
        />
      ));

      const _startTime = performance.now();
      render(<div>{manyIcons}</div>);
      const endTime = performance.now();

      // Should render quickly (less than 100ms for 50 icons)
      expect(endTime - _startTime).toBeLessThan(100);

      // All icons should be present
      expect(screen.getAllByTestId(/^icon-/)).toHaveLength(50);
    });
  });
});

import { act, renderHook } from '@testing-library/react';
import { afterEach, beforeEach, describe, expect, it, vi } from 'vitest';
// import { BYTES_PER_KB } from '@/constants'; // 
import { TEST_BASE_NUMBERS } from '@/constants/test-constants';
import { usePerformanceMonitor } from '@/hooks/use-performance-monitor';

// Mock performance.memory
const mockPerformanceMemory = {
  usedJSHeapSize:
    TEST_BASE_NUMBERS.MEMORY_SIZE_50MB *
    TEST_BASE_NUMBERS.BYTES_PER_KB *
    TEST_BASE_NUMBERS.BYTES_PER_KB, // 50MB
  totalJSHeapSize:
    TEST_BASE_NUMBERS.MEMORY_SIZE_100MB *
    TEST_BASE_NUMBERS.BYTES_PER_KB *
    TEST_BASE_NUMBERS.BYTES_PER_KB, // 100MB
  jsHeapSizeLimit:
    TEST_BASE_NUMBERS.MEMORY_SIZE_200MB *
    TEST_BASE_NUMBERS.BYTES_PER_KB *
    TEST_BASE_NUMBERS.BYTES_PER_KB, // 200MB
};

// Mock navigation timing entry
const mockNavigationTiming = {
  startTime: 0,
  loadEventEnd: 1000,
  domContentLoadedEventEnd: 500,
  responseEnd: 300,
  requestStart: 100,
  connectEnd: 50,
  connectStart: 20,
  domainLookupEnd: 15,
  domainLookupStart: 10,
  fetchStart: 5,
  redirectEnd: 0,
  redirectStart: 0,
  unloadEventEnd: 0,
  unloadEventStart: 0,
  secureConnectionStart: 0,
  responseStart: 250,
  duration: 1000,
  entryType: 'navigation',
  name: 'document',
  initiatorType: 'navigation',
  nextHopProtocol: 'http/1.1',
  workerStart: 0,
  redirectCount: 0,
  type: 'navigate',
  transferSize: 1024,
  encodedBodySize: 512,
  decodedBodySize: 512,
  serverTiming: [],
  toJSON: () => ({}),
} as PerformanceNavigationTiming;

// Mock performance API
Object.defineProperty(global, 'performance', {
  writable: true,
  value: {
    ...performance,
    memory: mockPerformanceMemory,
    now: vi.fn(() => Date.now()),
    getEntriesByType: vi.fn((type: string) => {
      if (type === 'navigation') {
        return [mockNavigationTiming];
      }
      return [];
    }),
    getEntriesByName: vi.fn(() => []),
    mark: vi.fn(),
    measure: vi.fn(),
    clearMarks: vi.fn(),
    clearMeasures: vi.fn(),
    clearResourceTimings: vi.fn(),
    setResourceTimingBufferSize: vi.fn(),
  },
});

// Mock Date.now for consistent timing
const mockDateNow = vi.fn(() => 1000);
vi.stubGlobal('Date', {
  ...Date,
  now: mockDateNow,
});

// Mock setTimeout and clearTimeout with proper timer management
let timerId = 1;
const activeTimers = new Map<number, () => void>();

vi.stubGlobal(
  'setTimeout',
  vi.fn((fn, _delay) => {
    const id = timerId++;
    if (typeof fn === 'function') {
      activeTimers.set(id, fn);
    }
    return id;
  }),
);

vi.stubGlobal(
  'clearTimeout',
  vi.fn((id: number) => {
    activeTimers.delete(id);
  }),
);

// Helper function to manually trigger timers in tests
const triggerTimers = () => {
  const timers = Array.from(activeTimers.values());
  activeTimers.clear();
  timers.forEach(fn => fn());
};

describe('usePerformanceMonitor', () => {
  beforeEach(() => {
    vi.clearAllMocks();

    // Clear active timers
    activeTimers.clear();
    timerId = 1;

    // Reset Date.now mock
    mockDateNow.mockReturnValue(1000);

    // Reset performance.memory mock
    Object.defineProperty(global.performance, 'memory', {
      writable: true,
      value: mockPerformanceMemory,
    });

    // Reset performance.getEntriesByType mock
    vi.mocked(global.performance.getEntriesByType).mockImplementation((type: string) => {
      if (type === 'navigation') {
        return [mockNavigationTiming];
      }
      return [];
    });
  });

  afterEach(() => {
    vi.restoreAllMocks();
  });

  describe('', () => {
    it('should initialize with default state', () => {
      const { result } = renderHook(() => usePerformanceMonitor());

      expect(result.current.isMonitoring).toBe(false);
      expect(result.current.metrics).toBe(null);
      expect(result.current.alerts).toEqual([]);
      expect(result.current.error).toBe(null);
    });

    it('should provide all required functions', () => {
      const { result } = renderHook(() => usePerformanceMonitor());

      expect(typeof result.current.startMonitoring).toBe('function');
      expect(typeof result.current.stopMonitoring).toBe('function');
      expect(typeof result.current.refreshMetrics).toBe('function');
      expect(typeof result.current.clearAlerts).toBe('function');
    });

    it('should initialize with custom config', () => {
      const config = {
        enableAlerts: true,
        alertThresholds: {
          loadTime: 1000,
          renderTime: 500,
          memoryUsage:
            TEST_BASE_NUMBERS.MEMORY_SIZE_100MB *
            TEST_BASE_NUMBERS.BYTES_PER_KB *
            TEST_BASE_NUMBERS.BYTES_PER_KB, // 100MB
        },
        enableAutoBaseline: true,
        monitoringInterval: 5000,
      };

      const { result } = renderHook(() => usePerformanceMonitor(config));

      // HookstartMonitoring
      expect(result.current.isMonitoring).toBe(false);
      expect(result.current.metrics).toBe(null);
      expect(result.current.alerts).toEqual([]);
      expect(result.current.error).toBe(null);
    });
  });

  describe('', () => {
    it('should start monitoring', () => {
      const { result } = renderHook(() => usePerformanceMonitor());

      act(() => {
        result.current.startMonitoring();
      });

      expect(result.current.isMonitoring).toBe(true);
      expect(result.current.metrics).toBe(null);
    });

    it('should stop monitoring', () => {
      const { result } = renderHook(() => usePerformanceMonitor());

      act(() => {
        result.current.startMonitoring();
      });

      expect(result.current.isMonitoring).toBe(true);

      act(() => {
        result.current.stopMonitoring();
      });

      expect(result.current.isMonitoring).toBe(false);
    });

    it('should handle monitoring errors', () => {
      // Mock performance.memory to be undefined to trigger error
      Object.defineProperty(global.performance, 'memory', {
        writable: true,
        value: undefined,
      });

      const { result } = renderHook(() => usePerformanceMonitor());

      act(() => {
        result.current.startMonitoring();
      });

      // Should still start monitoring even without memory API
      expect(result.current.isMonitoring).toBe(true);
    });

    it('should handle unknown errors', () => {
      const { result } = renderHook(() => usePerformanceMonitor());

      act(() => {
        result.current.startMonitoring();
      });

      // Should handle gracefully
      expect(result.current.isMonitoring).toBe(true);
      expect(result.current.error).toBe(null);
    });
  });

  describe('', () => {
    it('should refresh metrics manually', () => {
      const { result } = renderHook(() => usePerformanceMonitor());

      act(() => {
        result.current.startMonitoring();
      });

      act(() => {
        result.current.refreshMetrics();
      });

      // Should have metrics after refresh
      expect(result.current.metrics).toBeTruthy();
      expect(result.current.isMonitoring).toBe(true);
    });

    it('should handle refresh when not monitoring', () => {
      const { result } = renderHook(() => usePerformanceMonitor());

      act(() => {
        result.current.refreshMetrics();
      });

      // Should not crash when not monitoring, and should have metrics after refresh
      expect(result.current.metrics).toEqual({
        loadTime: 1000,
        renderTime: 0,
        memoryUsage: 52428800,
      });
      expect(result.current.error).toBe(null);
    });
  });

  describe('', () => {
    it('should perform automatic monitoring when enabled', () => {
      const { result } = renderHook(() =>
        usePerformanceMonitor({
          enableAutoBaseline: true,
          monitoringInterval: 1000,
        }),
      );

      act(() => {
        result.current.startMonitoring();
      });

      // Should start monitoring
      expect(result.current.isMonitoring).toBe(true);
    });

    it('should not perform automatic monitoring when disabled', () => {
      const { result } = renderHook(() =>
        usePerformanceMonitor({
          enableAutoBaseline: false,
        }),
      );

      act(() => {
        result.current.startMonitoring();
      });

      // Should still start monitoring manually
      expect(result.current.isMonitoring).toBe(true);
    });

    it('should clear interval when monitoring stops', () => {
      const { result } = renderHook(() =>
        usePerformanceMonitor({
          enableAutoBaseline: true,
          monitoringInterval: 1000,
        }),
      );

      act(() => {
        result.current.startMonitoring();
      });

      act(() => {
        result.current.stopMonitoring();
      });

      // Should stop monitoring
      expect(result.current.isMonitoring).toBe(false);
    });
  });

  describe('', () => {
    it('should load alerts from alert system', () => {
      const { result } = renderHook(() =>
        usePerformanceMonitor({
          enableAlerts: true,
          alertThresholds: {
            memoryUsage:
              (TEST_BASE_NUMBERS.MEMORY_SIZE_50MB *
                TEST_BASE_NUMBERS.BYTES_PER_KB *
                TEST_BASE_NUMBERS.BYTES_PER_KB) /
              TEST_BASE_NUMBERS.SMALL_COUNT, // 25MB threshold
          },
        }),
      );

      // Start monitoring to trigger memory check
      act(() => {
        result.current.startMonitoring();
      });

      // Should have alerts array (may be empty initially)
      expect(Array.isArray(result.current.alerts)).toBe(true);
    });

    it('should clear alerts', () => {
      const { result } = renderHook(() => usePerformanceMonitor());

      act(() => {
        result.current.clearAlerts();
      });

      expect(result.current.alerts).toEqual([]);
    });

    it('should handle alert system errors', () => {
      const { result } = renderHook(() => usePerformanceMonitor());

      expect(() => {
        result.current.clearAlerts();
      }).not.toThrow();
    });
  });

  describe('', () => {
    it('should cleanup on unmount', () => {
      const { result, unmount } = renderHook(() =>
        usePerformanceMonitor({
          enableAutoBaseline: true,
          monitoringInterval: 1000,
        }),
      );

      act(() => {
        result.current.startMonitoring();
      });

      expect(result.current.isMonitoring).toBe(true);

      unmount();

      // Should not cause errors after unmount
      expect(() => {
        // Just test that unmount doesn't throw
      }).not.toThrow();
    });

    it('should handle cleanup errors gracefully', () => {
      const { result, unmount } = renderHook(() => usePerformanceMonitor());

      act(() => {
        result.current.startMonitoring();
      });

      // Should handle unmount gracefully
      expect(() => {
        unmount();
      }).not.toThrow();
    });
  });

  describe('', () => {
    it('should handle multiple start monitoring calls', () => {
      const { result } = renderHook(() => usePerformanceMonitor());

      act(() => {
        result.current.startMonitoring();
        result.current.startMonitoring();
        result.current.startMonitoring();
      });

      expect(result.current.isMonitoring).toBe(true);
    });

    it('should handle stop monitoring when not monitoring', () => {
      const { result } = renderHook(() => usePerformanceMonitor());

      expect(() => {
        result.current.stopMonitoring();
      }).not.toThrow();

      expect(result.current.isMonitoring).toBe(false);
    });

    it('should handle refresh when not monitoring', () => {
      const { result } = renderHook(() => usePerformanceMonitor());

      act(() => {
        result.current.refreshMetrics();
      });

      // Should handle gracefully when not monitoring, and should have metrics after refresh
      expect(result.current.metrics).toEqual({
        loadTime: 1000,
        renderTime: 0,
        memoryUsage: 52428800,
      });
      expect(result.current.error).toBe(null);
    });
  });

  describe('', () => {
    it('should handle memory API variations', () => {
      const memoryVariations = [
        { usedJSHeapSize: 0, totalJSHeapSize: 0, jsHeapSizeLimit: 0 },
        {
          usedJSHeapSize: 1000000,
          totalJSHeapSize: 2000000,
          jsHeapSizeLimit: 4000000,
        },
        {
          usedJSHeapSize: undefined,
          totalJSHeapSize: undefined,
          jsHeapSizeLimit: undefined,
        },
      ];

      memoryVariations.forEach((memory) => {
        Object.defineProperty(global.performance, 'memory', {
          writable: true,
          value: memory,
        });

        const { result } = renderHook(() => usePerformanceMonitor());

        act(() => {
          result.current.startMonitoring();
        });

        expect(result.current.isMonitoring).toBe(true);
      });
    });

    it('should handle performance.now errors', () => {
      const originalNow = global.performance.now;

      try {
        global.performance.now = vi.fn(() => {
          throw new Error('Performance.now error');
        });

        const { result } = renderHook(() => usePerformanceMonitor());

        // Performance.now errors will be thrown during startMonitoring
        expect(() => {
          act(() => {
            result.current.startMonitoring();
          });
        }).toThrow('Performance.now error');
      } finally {
        global.performance.now = originalNow;
      }
    });

    it('should handle missing performance object', () => {
      const originalPerformance = global.performance;

      try {
        // @ts-expect-error - Testing edge case
        global.performance = undefined;

        const { result } = renderHook(() => usePerformanceMonitor());

        // Missing performance object will cause errors during startMonitoring
        expect(() => {
          act(() => {
            result.current.startMonitoring();
          });
        }).toThrow();
      } finally {
        global.performance = originalPerformance;
      }
    });
  });

  describe('', () => {
    it('should handle invalid configuration gracefully', () => {
      const invalidConfigs = [
        { monitoringInterval: -1 },
        { monitoringInterval: 0 },
        { alertThresholds: null },
        { enableAlerts: 'invalid' },
      ];

      invalidConfigs.forEach((config) => {
        expect(() => {
          renderHook(() => usePerformanceMonitor(config as any));
        }).not.toThrow();
      });
    });

    it('should handle extremely high monitoring intervals', () => {
      const { result } = renderHook(() =>
        usePerformanceMonitor({
          enableAutoBaseline: true,
          monitoringInterval: 999999999,
        }),
      );

      act(() => {
        result.current.startMonitoring();
      });

      expect(result.current.isMonitoring).toBe(true);
    });

    it('should handle configuration changes during monitoring', () => {
      const { result, rerender } = renderHook(
        ({ config }) => usePerformanceMonitor(config),
        {
          initialProps: {
            config: { enableAutoBaseline: true, monitoringInterval: 1000 },
          },
        },
      );

      act(() => {
        result.current.startMonitoring();
      });

      expect(result.current.isMonitoring).toBe(true);

      // Change configuration
      rerender({
        config: { enableAutoBaseline: false, monitoringInterval: 2000 },
      });

      // Should handle configuration change gracefully
      expect(() => {
        act(() => {
          result.current.refreshMetrics();
        });
      }).not.toThrow();
    });
  });

  describe('', () => {
    it('should handle rapid start/stop cycles', () => {
      const { result } = renderHook(() => usePerformanceMonitor());

      expect(() => {
        act(() => {
          for (let i = 0; i < 10; i++) {
            result.current.startMonitoring();
            result.current.stopMonitoring();
          }
        });
      }).not.toThrow();
    });

    it('should handle concurrent refresh calls', () => {
      const { result } = renderHook(() => usePerformanceMonitor());

      act(() => {
        result.current.startMonitoring();
      });

      expect(() => {
        act(() => {
          // Simulate concurrent refresh calls
          result.current.refreshMetrics();
          result.current.refreshMetrics();
          result.current.refreshMetrics();
        });
      }).not.toThrow();
    });

    it('should handle monitoring state changes during refresh', () => {
      const { result } = renderHook(() => usePerformanceMonitor());

      act(() => {
        result.current.startMonitoring();
      });

      expect(() => {
        act(() => {
          result.current.refreshMetrics();
          result.current.stopMonitoring();
          result.current.refreshMetrics();
        });
      }).not.toThrow();
    });
  });

  describe('', () => {
    it('should handle alert system with missing memory API', () => {
      const performanceWithMemory = globalThis.performance as Performance & {
        memory?: { usedJSHeapSize?: number };
      };
      const originalMemory = performanceWithMemory.memory;
      delete performanceWithMemory.memory;

      const { result } = renderHook(() =>
        usePerformanceMonitor({
          enableAlerts: true,
          alertThresholds: {
            memoryUsage: 1000000, // 1MB threshold
          },
        }),
      );

      act(() => {
        result.current.startMonitoring();
        // measureMemoryUsage is not exposed in the hook return value
        // Instead, we can check if memory monitoring works through metrics
        expect(result.current.metrics?.memoryUsage).toBeUndefined();
      });

      if (originalMemory !== undefined) {
        Object.defineProperty(performanceWithMemory, 'memory', {
          configurable: true,
          writable: true,
          value: originalMemory,
        });
      } else {
        delete performanceWithMemory.memory;
      }
    });

    it('should handle alert threshold edge cases', () => {
      const { result } = renderHook(() =>
        usePerformanceMonitor({
          enableAlerts: true,
          alertThresholds: {
            loadTime: 0, // Zero threshold should trigger alerts
            renderTime: Number.MAX_SAFE_INTEGER, // Very high threshold
            memoryUsage: -1, // Negative threshold (invalid)
          },
        }),
      );

      act(() => {
        result.current.startMonitoring();
        result.current.measureLoadTime();
        result.current.measureRenderTime();
      });

      // Should handle edge cases without crashing
      expect(result.current.isMonitoring).toBe(true);
    });
  });

  describe('', () => {
    it('should handle autoBaseline configuration', () => {
      const { result } = renderHook(() =>
        usePerformanceMonitor({
          enableAutoBaseline: true,
          enableAlerts: true,
          alertThresholds: {
            loadTime: 2500,
            renderTime: 100,
            memoryUsage: 300,
          },
        }),
      );

      expect(result.current.performanceAlertSystem).toBeDefined();
    });

    it('should handle missing alertConfig with autoBaseline', () => {
      const { result } = renderHook(() =>
        usePerformanceMonitor({
          autoBaseline: true,
          // Missing alertConfig
        }),
      );

      expect(result.current.performanceAlertSystem).toBeDefined();
    });
  });

  describe('', () => {
    it('should not accumulate alerts indefinitely', () => {
      const { result } = renderHook(() =>
        usePerformanceMonitor({
          enableAlerts: true,
          maxAlerts: 5, // Limit alerts to 5
        }),
      );

      act(() => {
        result.current.startMonitoring();
      });

      act(() => {
        // Generate many alerts using the alert system directly
        for (let i = 0; i < 10; i++) {
          result.current.performanceAlertSystem.addAlert({
            level: 'warning',
            message: `Test alert ${i}`,
          });
        }
      });

      // Should have limited number of alerts (maxAlerts = 5)
      expect(result.current.alerts.length).toBeGreaterThan(0);
      expect(result.current.alerts.length).toBeLessThanOrEqual(5);
    });

    it('should handle alert clearing', () => {
      const { result } = renderHook(() =>
        usePerformanceMonitor({
          enableAlerts: true,
          alertThresholds: {
            loadTime: 0,
          },
        }),
      );

      act(() => {
        result.current.startMonitoring();
        result.current.measureLoadTime();
        result.current.clearAlerts();
      });

      expect(result.current.alerts).toEqual([]);
    });
  });
});

import React from 'react';
import { act, renderHook } from '@testing-library/react';
import { afterEach, beforeEach, describe, expect, it, vi } from 'vitest';
import { TEST_DELAY_VALUES } from '@/constants/test-constants';
import { useThemeToggle } from '@/hooks/use-theme-toggle';

// View Transitions API will be mocked in vi.hoisted

// Mock functions using vi.hoisted
const {
  mockUseEnhancedTheme,
  mockUseAccessibility,
  mockUseSyncExternalStore,
  mockStartViewTransition,
  mockViewTransition: _mockViewTransition,
  createMockTypeSet,
} = vi.hoisted(() => {
  const createMockTypeSet = (): ViewTransitionTypeSet =>
    new Set<string>() as unknown as ViewTransitionTypeSet;

  const createMockViewTransition = (): ViewTransition => ({
    ready: Promise.resolve(),
    finished: Promise.resolve(),
    updateCallbackDone: Promise.resolve(),
    skipTransition: vi.fn(),
    types: createMockTypeSet(),
  });

  const mockViewTransition = createMockViewTransition();

  const startViewTransitionMock = vi.fn(
    (callback?: ViewTransitionUpdateCallback | StartViewTransitionOptions) => {
      if (typeof callback === 'function') {
        callback();
      }
      return mockViewTransition;
    },
  ) as unknown as NonNullable<Document['startViewTransition']>;

  return {
    mockUseEnhancedTheme: vi.fn(),
    mockUseAccessibility: vi.fn(),
    mockUseSyncExternalStore: vi.fn(),
    mockStartViewTransition: startViewTransitionMock,
    mockViewTransition,
    createMockTypeSet,
  };
});

// Mock dependencies
vi.mock('@/hooks/use-enhanced-theme', () => ({
  useEnhancedTheme: mockUseEnhancedTheme,
}));

vi.mock('@/lib/accessibility', () => ({
  useAccessibility: mockUseAccessibility,
}));

// Mock React.useSyncExternalStore
vi.mock('react', async () => {
  const actual = await vi.importActual('react');
  return {
    ...actual,
    useSyncExternalStore: mockUseSyncExternalStore,
  };
});

describe('useThemeToggle', () => {
  const mockSetThemeWithCircularTransition = vi.fn();
  const mockAnnounceThemeChange = vi.fn();
  const mockAnnounceSwitching = vi.fn();
  const mockHandleKeyboardNavigation = vi.fn();
  const mockGetAriaAttributes = vi.fn();

  beforeEach(() => {
    vi.clearAllMocks();

    // Mock setThemeWithCircularTransition to return proper View Transition object
    mockSetThemeWithCircularTransition.mockImplementation((_theme, _event) => ({
      ready: Promise.resolve(),
      finished: Promise.resolve(),
      updateCallbackDone: Promise.resolve(),
      skipTransition: vi.fn(),
      types: createMockTypeSet(),
    }));

    // Mock useEnhancedTheme
    mockUseEnhancedTheme.mockReturnValue({
      theme: 'light', // 
      setThemeWithCircularTransition: mockSetThemeWithCircularTransition,
      supportsViewTransitions: true, // 
      setTheme: vi.fn(),
      themes: ['light', 'dark', 'system'],
      forcedTheme: undefined,
      resolvedTheme: 'light',
      systemTheme: 'light',
    });

    // Mock useAccessibility
    mockUseAccessibility.mockReturnValue({
      announceThemeChange: mockAnnounceThemeChange,
      announceSwitching: mockAnnounceSwitching,
      prefersReducedMotion: false,
      prefersHighContrast: false,
      handleKeyboardNavigation: mockHandleKeyboardNavigation,
      getAriaAttributes: mockGetAriaAttributes,
    });

    // Mock useSyncExternalStore (mounted state)
    mockUseSyncExternalStore.mockReturnValue(true);

    // Mock getAriaAttributes
    mockGetAriaAttributes.mockReturnValue({
      'aria-label': 'Toggle theme',
      'aria-expanded': false,
      'aria-haspopup': true,
    });

    // Mock setTimeout
    vi.useFakeTimers();

    // Mock View Transitions API safely
    (
      document as Document & {
        startViewTransition?: typeof mockStartViewTransition;
      }
    ).startViewTransition = mockStartViewTransition;
  });

  afterEach(() => {
    vi.useRealTimers();
    vi.restoreAllMocks();
  });

  describe('initialization', () => {
    it('should initialize with correct default values', () => {
      const { result } = renderHook(() => useThemeToggle());

      expect(result.current.theme).toBe('light');
      expect(result.current.isOpen).toBe(false);
      expect(result.current.prefersReducedMotion).toBe(false);
      expect(result.current.prefersHighContrast).toBe(false);
    });

    it('should provide all required functions', () => {
      const { result } = renderHook(() => useThemeToggle());

      expect(typeof result.current.handleThemeChange).toBe('function');
      expect(typeof result.current.handleKeyDown).toBe('function');
      expect(typeof result.current.setIsOpen).toBe('function');
    });

    it('should provide ARIA attributes', () => {
      const { result } = renderHook(() => useThemeToggle());

      expect(result.current.ariaAttributes).toEqual({
        'aria-label': 'Toggle theme',
        'aria-expanded': false,
        'aria-haspopup': true,
      });
    });
  });

  describe('mounted state handling', () => {
    it('should use system theme when not mounted', () => {
      // SSR/CSR
      // 'system'
      // Mock

      const { result } = renderHook(() => useThemeToggle());

      // Hook
      expect(result.current).toHaveProperty('theme');
      expect(result.current).toHaveProperty('handleThemeChange');
      expect(result.current).toHaveProperty('ariaAttributes');

      // ARIA'light''system'
      expect(mockGetAriaAttributes).toHaveBeenCalled();

      // Hook
      // SSR/CSR
    });

    it('should use actual theme when mounted', () => {
      mockUseSyncExternalStore.mockReturnValue(true);

      renderHook(() => useThemeToggle());

      expect(mockGetAriaAttributes).toHaveBeenCalledWith('light', false);
    });

    it('should handle undefined theme when mounted', () => {
      // Mock
      vi.clearAllMocks();

      // mountedtrue
      mockUseSyncExternalStore.mockReturnValue(true);

      mockUseEnhancedTheme.mockReturnValue({
        theme: undefined,
        setThemeWithCircularTransition: mockSetThemeWithCircularTransition,
        supportsViewTransitions: true,
        setTheme: vi.fn(),
        themes: ['light', 'dark', 'system'],
        forcedTheme: undefined,
        resolvedTheme: undefined,
        systemTheme: 'light',
      });

      // Mock useAccessibility for this test
      mockUseAccessibility.mockReturnValue({
        announceThemeChange: mockAnnounceThemeChange,
        announceSwitching: mockAnnounceSwitching,
        prefersReducedMotion: false,
        prefersHighContrast: false,
        handleKeyboardNavigation: mockHandleKeyboardNavigation,
        getAriaAttributes: mockGetAriaAttributes,
      });

      renderHook(() => useThemeToggle());

      // themeundefinedmountedtruecurrentTheme = theme || 'system' = 'system'
      expect(mockGetAriaAttributes).toHaveBeenCalledWith('system', false);
    });
  });

  describe('theme change handling', () => {
    it('should handle theme change without event', () => {
      const { result } = renderHook(() => useThemeToggle());

      act(() => {
        result.current.handleThemeChange('dark');
      });

      expect(mockAnnounceSwitching).toHaveBeenCalled();
      expect(mockSetThemeWithCircularTransition).toHaveBeenCalledWith(
        'dark',
        undefined,
      );
    });

    it('should handle theme change with click event', () => {
      const { result } = renderHook(() => useThemeToggle());
      const mockEvent = {
        clientX: 100,
        clientY: 200,
      } as React.MouseEvent<HTMLElement>;

      act(() => {
        result.current.handleThemeChange('dark', mockEvent);
      });

      expect(mockAnnounceSwitching).toHaveBeenCalled();
      expect(mockSetThemeWithCircularTransition).toHaveBeenCalledWith(
        'dark',
        mockEvent,
      );
    });

    it('should close dropdown after theme change', () => {
      const { result } = renderHook(() => useThemeToggle());

      // Open dropdown first
      act(() => {
        result.current.setIsOpen(true);
      });

      expect(result.current.isOpen).toBe(true);

      // Change theme
      act(() => {
        result.current.handleThemeChange('dark');
      });

      expect(result.current.isOpen).toBe(false);
    });

    it('should announce theme change after delay (normal motion)', () => {
      const { result } = renderHook(() => useThemeToggle());

      act(() => {
        result.current.handleThemeChange('dark');
      });

      expect(mockAnnounceThemeChange).not.toHaveBeenCalled();

      // Fast forward 400ms (normal delay)
      act(() => {
        vi.advanceTimersByTime(TEST_DELAY_VALUES.THEME_CHANGE_DELAY); // Normal delay for theme change announcement
      });

      expect(mockAnnounceThemeChange).toHaveBeenCalledWith('dark');
    });

    it('should announce theme change after reduced delay (reduced motion)', () => {
      mockUseAccessibility.mockReturnValue({
        announceThemeChange: mockAnnounceThemeChange,
        announceSwitching: mockAnnounceSwitching,
        prefersReducedMotion: true,
        prefersHighContrast: false,
        handleKeyboardNavigation: mockHandleKeyboardNavigation,
        getAriaAttributes: mockGetAriaAttributes,
      });

      const { result } = renderHook(() => useThemeToggle());

      act(() => {
        result.current.handleThemeChange('dark');
      });

      expect(mockAnnounceThemeChange).not.toHaveBeenCalled();

      // Fast forward reduced motion delay
      act(() => {
        vi.advanceTimersByTime(TEST_DELAY_VALUES.REDUCED_MOTION_DELAY); // Reduced motion delay
      });

      expect(mockAnnounceThemeChange).toHaveBeenCalledWith('dark');
    });
  });

  describe('keyboard navigation', () => {
    it('should handle keyboard navigation', () => {
      const { result } = renderHook(() => useThemeToggle());
      const mockAction = vi.fn();
      const mockKeyboardEvent = {
        nativeEvent: new KeyboardEvent('keydown', { key: 'Enter' }),
      } as React.KeyboardEvent;

      act(() => {
        result.current.handleKeyDown(mockKeyboardEvent, mockAction);
      });

      expect(mockHandleKeyboardNavigation).toHaveBeenCalledWith(
        mockKeyboardEvent.nativeEvent,
        mockAction,
        expect.any(Function),
      );
    });

    it('should close dropdown on keyboard navigation', () => {
      const { result } = renderHook(() => useThemeToggle());

      // Open dropdown first
      act(() => {
        result.current.setIsOpen(true);
      });

      expect(result.current.isOpen).toBe(true);

      // Simulate keyboard navigation that triggers close
      const mockAction = vi.fn();
      const mockKeyboardEvent = {
        nativeEvent: new KeyboardEvent('keydown', { key: 'Escape' }),
      } as React.KeyboardEvent;

      // Mock handleKeyboardNavigation to call the close function
      mockHandleKeyboardNavigation.mockImplementation(
        (event, _action, closeFunction) => {
          if (event.key === 'Escape') {
            closeFunction();
          }
        },
      );

      act(() => {
        result.current.handleKeyDown(mockKeyboardEvent, mockAction);
      });

      expect(result.current.isOpen).toBe(false);
    });
  });

  describe('state management', () => {
    it('should toggle dropdown state', () => {
      const { result } = renderHook(() => useThemeToggle());

      expect(result.current.isOpen).toBe(false);

      act(() => {
        result.current.setIsOpen(true);
      });

      expect(result.current.isOpen).toBe(true);

      act(() => {
        result.current.setIsOpen(false);
      });

      expect(result.current.isOpen).toBe(false);
    });

    it('should update ARIA attributes when dropdown state changes', () => {
      mockGetAriaAttributes
        .mockReturnValueOnce({
          'aria-label': 'Toggle theme',
          'aria-expanded': false,
          'aria-haspopup': true,
        })
        .mockReturnValueOnce({
          'aria-label': 'Toggle theme',
          'aria-expanded': true,
          'aria-haspopup': true,
        });

      const { result, rerender } = renderHook(() => useThemeToggle());

      expect(result.current.ariaAttributes['aria-expanded']).toBe(false);

      act(() => {
        result.current.setIsOpen(true);
      });

      rerender();

      expect(mockGetAriaAttributes).toHaveBeenCalledWith('light', true);
    });
  });

  describe('accessibility preferences', () => {
    it('should reflect reduced motion preference', () => {
      mockUseAccessibility.mockReturnValue({
        announceThemeChange: mockAnnounceThemeChange,
        announceSwitching: mockAnnounceSwitching,
        prefersReducedMotion: true,
        prefersHighContrast: false,
        handleKeyboardNavigation: mockHandleKeyboardNavigation,
        getAriaAttributes: mockGetAriaAttributes,
      });

      const { result } = renderHook(() => useThemeToggle());

      expect(result.current.prefersReducedMotion).toBe(true);
    });

    it('should reflect high contrast preference', () => {
      mockUseAccessibility.mockReturnValue({
        announceThemeChange: mockAnnounceThemeChange,
        announceSwitching: mockAnnounceSwitching,
        prefersReducedMotion: false,
        prefersHighContrast: true,
        handleKeyboardNavigation: mockHandleKeyboardNavigation,
        getAriaAttributes: mockGetAriaAttributes,
      });

      const { result } = renderHook(() => useThemeToggle());

      expect(result.current.prefersHighContrast).toBe(true);
    });
  });

  describe('View Transitions support', () => {
    it('should reflect View Transitions support', () => {
      // Mock
      vi.clearAllMocks();

      // mountedtrue
      mockUseSyncExternalStore.mockReturnValue(true);

      mockUseEnhancedTheme.mockReturnValue({
        theme: 'light',
        setThemeWithCircularTransition: mockSetThemeWithCircularTransition,
        supportsViewTransitions: false,
        setTheme: vi.fn(),
        themes: ['light', 'dark', 'system'],
        forcedTheme: undefined,
        resolvedTheme: 'light',
        systemTheme: 'light',
      });

      // Mock useAccessibility for this test
      mockUseAccessibility.mockReturnValue({
        announceThemeChange: mockAnnounceThemeChange,
        announceSwitching: mockAnnounceSwitching,
        prefersReducedMotion: false,
        prefersHighContrast: false,
        handleKeyboardNavigation: mockHandleKeyboardNavigation,
        getAriaAttributes: mockGetAriaAttributes,
      });

      const { result } = renderHook(() => useThemeToggle());

      // supportsViewTransitions is no longer part of the hook return value
      expect(typeof result.current.handleThemeChange).toBe('function');
    });
  });

  describe('edge cases', () => {
    it('should handle multiple rapid theme changes', () => {
      const { result } = renderHook(() => useThemeToggle());

      act(() => {
        result.current.handleThemeChange('dark');
        result.current.handleThemeChange('light');
        result.current.handleThemeChange('system');
      });

      const EXPECTED_THEME_CHANGES = 3;
      expect(mockSetThemeWithCircularTransition).toHaveBeenCalledTimes(
        EXPECTED_THEME_CHANGES, // Three theme changes
      );
      expect(mockAnnounceSwitching).toHaveBeenCalledTimes(
        EXPECTED_THEME_CHANGES, // Three announcements
      );
    });

    it('should handle theme change when accessibility functions fail', () => {
      mockAnnounceSwitching.mockImplementation(() => {
        throw new Error('Announce error');
      });

      const { result } = renderHook(() => useThemeToggle());

      expect(() => {
        act(() => {
          result.current.handleThemeChange('dark');
        });
      }).toThrow('Announce error');
    });

    it('should handle keyboard navigation when handler fails', () => {
      mockHandleKeyboardNavigation.mockImplementation(() => {
        throw new Error('Navigation error');
      });

      const { result } = renderHook(() => useThemeToggle());
      const mockAction = vi.fn();
      const mockKeyboardEvent = {
        nativeEvent: new KeyboardEvent('keydown', { key: 'Enter' }),
      } as React.KeyboardEvent;

      expect(() => {
        act(() => {
          result.current.handleKeyDown(mockKeyboardEvent, mockAction);
        });
      }).toThrow('Navigation error');
    });
  });
});

