      );
      const newBaseline = createBaselineFromMetrics(
        createDetailedMetrics({
          page: {
            url: 'https://test.com/new-page',
            referrer: '',
            title: 'New Page',
            timestamp: Date.now(),
          },
        }),
        { id: 'new-baseline' },
      );

