  | 'right-center';

export type DevToolSize = 'small' | 'medium' | 'large';

export interface DevToolConfig {
  id: string;
  name: string;
  position: DevToolPosition;
  size: DevToolSize;
  priority: number; // 1-10, 10 
  zIndex: number;
  collapsible: boolean;
  defaultCollapsed?: boolean;
}

/**
 * 
