import { env } from '../../env.mjs';

// 
export { env };

// 
export function getEnvVar(key: keyof typeof env): string | boolean | undefined {
  return env[key];
}

// 
export function requireEnvVar(key: keyof typeof env): string {
  const value = env[key];
  if (!value || typeof value === 'boolean') {
    throw new Error(`Required environment variable ${key} is not set or is not a string`);
  }
  return value;
}

// 
export const envUtils = {
  isDevelopment: () => env.NODE_ENV === 'development',
  isProduction: () => env.NODE_ENV === 'production',
  isTest: () => env.NODE_ENV === 'test',

  // WhatsApp
  getWhatsAppToken: () => requireEnvVar('WHATSAPP_ACCESS_TOKEN'),
  getWhatsAppPhoneId: () => requireEnvVar('WHATSAPP_PHONE_NUMBER_ID'),

  // Turnstile
  getTurnstileSecret: () => requireEnvVar('TURNSTILE_SECRET_KEY'),
  getTurnstileSiteKey: () => requireEnvVar('NEXT_PUBLIC_TURNSTILE_SITE_KEY'),

  // Resend
  getResendApiKey: () => requireEnvVar('RESEND_API_KEY'),

  // Airtable
  getAirtableToken: () => requireEnvVar('AIRTABLE_API_KEY'),
  getAirtableBaseId: () => requireEnvVar('AIRTABLE_BASE_ID'),
} as const;

/**
 * 
 * 
 */
import { ONE, PERCENTAGE_FULL, ZERO } from '@/constants';
import { routing } from '@/i18n/routing';

// 
const isTestEnvironment = typeof process !== 'undefined' && process.env.NODE_ENV === 'test';

type KnownLocale = 'en' | 'zh';
type TranslationsMap = Partial<Record<KnownLocale, Record<string, unknown>>>;

function isKnownLocale(l: string): l is KnownLocale {
  return l === 'en' || l === 'zh';
}

function getByLocale(
  translations: TranslationsMap,
  locale: string,
): Record<string, unknown> | undefined {
  if (locale === 'en') return translations.en;
  if (locale === 'zh') return translations.zh;
  return undefined;
}

export interface TranslationValidationResult {
  isValid: boolean;
  errors: TranslationError[];
  warnings: TranslationWarning[];
  coverage: number;
  missingKeys: string[];
  inconsistentKeys: string[];
}

export interface TranslationError {
  type: 'missing_key' | 'type_mismatch' | 'invalid_format' | 'empty_value';
  key: string;
  locale: string;
  message: string;
  severity: 'critical' | 'high' | 'medium' | 'low';
}

export interface TranslationWarning {
  type:
    | 'untranslated'
    | 'length_mismatch'
    | 'format_inconsistency'
    | 'placeholder_mismatch';
  key: string;
  locale: string;
  message: string;
  suggestion?: string;
}

/**
 * 
 */
async function loadTranslations(
  errors: TranslationError[],
): Promise<TranslationsMap> {
  const translations: TranslationsMap = {};

  // Mock
  if (isTestEnvironment) {
    try {
      // Mock
      const { getMockTranslation } = await import('./__tests__/mocks/translations');

      for (const locale of routing.locales) {
        const mockData = getMockTranslation(locale);
        if (mockData && isKnownLocale(locale)) {
          // Mock
          if (typeof mockData === 'string') {
            errors.push({
              type: 'invalid_format',
              key: 'translation_file',
              locale,
              message: `Translation file for locale ${locale} contains malformed data: expected object, got string`,
              severity: 'critical',
            });
            continue;
          }

          if (locale === 'en') translations.en = mockData as Record<string, unknown>;
          else if (locale === 'zh') translations.zh = mockData as Record<string, unknown>;
        } else if (mockData === undefined) {
          errors.push({
            type: 'missing_key',
            key: 'translation_file',
            locale,
            message: `Translation file for locale ${locale} not found`,
            severity: 'critical',
          });
        }
      }
    } catch (error) {
      // Mock
      return loadProductionTranslations(errors);
    }
  } else {
    return loadProductionTranslations(errors);
  }

  return translations;
}

/**
 * 
 */
async function loadProductionTranslations(
  errors: TranslationError[],
): Promise<TranslationsMap> {
  const translations: TranslationsMap = {};

  for (const locale of routing.locales) {
    try {
      const messages = await import(`../../messages/${locale}.json`);
      if (!isKnownLocale(locale)) continue;
      if (locale === 'en') translations.en = messages.default;
      else translations.zh = messages.default;
    } catch {
      errors.push({
        type: 'missing_key',
        key: 'translation_file',
        locale,
        message: `Translation file for locale ${locale} not found`,
        severity: 'critical',
      });
    }
  }

  return translations;
}

/**
 * 
 */
export async function validateTranslations(): Promise<TranslationValidationResult> {
  const errors: TranslationError[] = [];
  const warnings: TranslationWarning[] = [];
  const missingKeys: string[] = [];
  const inconsistentKeys: string[] = [];

  try {
    const translations = await loadTranslations(errors);

    // 
    const allKeys = new Set<string>();
    Object.values(translations).forEach((translation) => {
      extractKeys(translation).forEach((key) => allKeys.add(key));
    });

    // 
    validateTranslationCompleteness({
      translations,
      allKeys,
      errors,
      warnings,
      missingKeys,
    });

    //  - 
    const totalKeys = allKeys.size * routing.locales.length;
    const missingCount = missingKeys.length;
    let coverage: number;

    if (allKeys.size === ZERO) {
      // 100%100%
      coverage = PERCENTAGE_FULL;
    } else if (totalKeys > ZERO) {
      coverage = ((totalKeys - missingCount) / totalKeys) * PERCENTAGE_FULL;
    } else {
      coverage = PERCENTAGE_FULL;
    }

    // 
    const criticalErrors = errors.filter((e) => e.severity === 'critical' || e.severity === 'high');
    let isValid = criticalErrors.length === ZERO;

    // 
    // 
    if (allKeys.size === ZERO && Object.keys(translations).length > ZERO) {
      // 
      const allEmpty = Object.values(translations).every(
        (trans) => trans && Object.keys(trans).length === ZERO
      );

      if (allEmpty) {
        // 
        // 
        const hasEmptyFileError = errors.some(e => e.message.includes('All translation files are empty'));
        if (!hasEmptyFileError) {
          // 
          errors.push({
            type: 'empty_value',
            key: 'all_translations',
            locale: 'all',
            message: 'All translation files are empty',
            severity: 'critical',
          });
          isValid = false;
        }
      }
    }

    return {
      isValid,
      errors,
      warnings,
      coverage,
      missingKeys,
      inconsistentKeys,
    };
  } catch (error) {
    return {
      isValid: false,
      errors: [
        {
          type: 'invalid_format',
          key: 'validation',
          locale: 'all',
          message: `Validation failed: ${error instanceof Error ? error.message : 'Unknown error'}`,
          severity: 'critical',
        },
      ],
      warnings: [],
      coverage: ZERO,
      missingKeys: [],
      inconsistentKeys: [],
    };
  }
}

/**
 * 
 */
function extractKeys(obj: Record<string, unknown>, prefix = ''): string[] {
  const keys: string[] = [];

  for (const [key, value] of Object.entries(obj)) {
    const fullKey = prefix ? `${prefix}.${key}` : key;

    if (value && typeof value === 'object' && !Array.isArray(value)) {
      keys.push(...extractKeys(value as Record<string, unknown>, fullKey));
    } else {
      keys.push(fullKey);
    }
  }

  return keys;
}

/**
 * 
 */
function getNestedValue(obj: Record<string, unknown>, path: string): unknown {
  const segments = path.split('.');
  let current: unknown = obj;
  // Unicode
  // 
  const safe = /^[a-z0-9_\-\u00a0-\u024f\u1e00-\u1eff\u2000-\u206f\u2070-\u209f\u20a0-\u20cf\u2100-\u214f\u2190-\u21ff\u2200-\u22ff\u2300-\u23ff\u2460-\u24ff\u2500-\u257f\u2580-\u259f\u25a0-\u25ff\u2600-\u26ff\u2700-\u27bf\u3000-\u303f\u3040-\u309f\u30a0-\u30ff\u3100-\u312f\u3130-\u318f\u3190-\u319f\u31a0-\u31bf\u31c0-\u31ef\u31f0-\u31ff\u3200-\u32ff\u3300-\u33ff\u3400-\u4dbf\u4e00-\u9fff\ua000-\ua48f\ua490-\ua4cf\uac00-\ud7af\uf900-\ufaff\ufe30-\ufe4f\ufe50-\ufe6f\uff00-\uffef]+$/i;
  for (const seg of segments) {
    if (!safe.test(seg)) return undefined;
    if (
      current &&
      typeof current === 'object' &&
      current !== null &&
      !Array.isArray(current)
    ) {
      current = Reflect.get(current as Record<string, unknown>, seg);
    } else {
      return undefined;
    }
  }
  return current;
}

/**
 * 
 */
export function generateTranslationReport(
  result: TranslationValidationResult,
): string {
  const { isValid, errors, warnings, coverage, missingKeys } = result;

  let report = '# \n\n';

  report += `## : ${isValid ? ' ' : ' '}\n`;
  report += `## : ${coverage.toFixed(ONE)}%\n\n`;

  if (errors.length > ZERO) {
    report += '## \n\n';
    errors.forEach((error) => {
      report += `- **${error.severity.toUpperCase()}**: ${error.message} (${error.locale}.${error.key})\n`;
    });
    report += '\n';
  }

  if (warnings.length > ZERO) {
    report += '## \n\n';
    warnings.forEach((warning) => {
      report += `- **${warning.type}**: ${warning.message} (${warning.locale}.${warning.key})\n`;
      if (warning.suggestion) {
        report += `  : ${warning.suggestion}\n`;
      }
    });
    report += '\n';
  }

  if (missingKeys.length > ZERO) {
    report += '## \n\n';
    missingKeys.forEach((key) => {
      report += `- ${key}\n`;
    });
  }

  return report;
}

/**
 * 
 */
function validateTranslationCompleteness(params: {
  translations: TranslationsMap;
  allKeys: Set<string>;
  errors: TranslationError[];
  warnings: TranslationWarning[];
  missingKeys: string[];
}): void {
  const { translations, allKeys, errors, warnings, missingKeys } = params;
  for (const locale of routing.locales) {
    const translation = getByLocale(translations, locale);
    if (!translation) continue;

    const localeKeys = new Set(extractKeys(translation));

    // 
    for (const key of allKeys) {
      if (!localeKeys.has(key)) {
        missingKeys.push(`${locale}.${key}`);
        errors.push({
          type: 'missing_key',
          key,
          locale,
          message: `Missing translation for key: ${key}`,
          severity: 'high',
        });
      }
    }

    // 
    validateTranslationQuality({
      translation,
      locale,
      localeKeys,
      translations,
      errors,
      warnings,
    });
  }
}

/**
 * 
 */
function validateTranslationQuality(params: {
  translation: Record<string, unknown>;
  locale: string;
  localeKeys: Set<string>;
  translations: TranslationsMap;
  errors: TranslationError[];
  warnings: TranslationWarning[];
}): void {
  const { translation, locale, localeKeys, translations, errors, warnings } =
    params;
  for (const key of localeKeys) {
    const value = getNestedValue(translation, key);

    // 
    if (!value || (typeof value === 'string' && value.trim() === '')) {
      errors.push({
        type: 'empty_value',
        key,
        locale,
        message: `Empty translation value for key: ${key}`,
        severity: 'medium',
      });
    }

    // 
    if (typeof value === 'string') {
      checkUntranslatedContent({ value, key, locale, translations, warnings });
      checkPlaceholderConsistency({
        value,
        key,
        locale,
        translations,
        warnings,
      });
    }
  }
}

/**
 * 
 */
function checkUntranslatedContent(params: {
  value: string;
  key: string;
  locale: string;
  translations: TranslationsMap;
  warnings: TranslationWarning[];
}): void {
  const { value, key, locale, translations, warnings } = params;
  const otherLocales = routing.locales.filter((l) => l !== locale);
  for (const otherLocale of otherLocales) {
    const otherTrans = getByLocale(translations, otherLocale) || {};
    const otherValue = getNestedValue(otherTrans, key);
    if (value === otherValue && key !== 'home.title') {
      warnings.push({
        type: 'untranslated',
        key,
        locale,
        message: `Possibly untranslated: same value as ${otherLocale}`,
        suggestion: `Consider translating "${value}" to ${locale}`,
      });
    }
  }
}

/**
 * 
 */
function checkPlaceholderConsistency(params: {
  value: string;
  key: string;
  locale: string;
  translations: TranslationsMap;
  warnings: TranslationWarning[];
}): void {
  const { value, key, locale, translations, warnings } = params;
  const placeholders = value.match(/\{[^}]+\}/g) || [];
  const refLocale: KnownLocale = routing.locales.includes('en') ? 'en' : 'zh';
  const refTrans = getByLocale(translations, refLocale) || {};
  const referencePlaceholders = getNestedValue(refTrans, key);
  if (typeof referencePlaceholders === 'string') {
    const refPlaceholders = referencePlaceholders.match(/\{[^}]+\}/g) || [];

    // 
    if (placeholders.length !== refPlaceholders.length) {
      warnings.push({
        type: 'placeholder_mismatch',
        key,
        locale,
        message: `Placeholder count mismatch: expected ${refPlaceholders.length}, got ${placeholders.length}`,
        suggestion: `Ensure all placeholders are present: ${refPlaceholders.join(', ')}`,
      });
    }

    // 
    const refPlaceholderSet = new Set(refPlaceholders);
    const currentPlaceholderSet = new Set(placeholders);
    const missingPlaceholders = refPlaceholders.filter(p => !currentPlaceholderSet.has(p));
    const extraPlaceholders = placeholders.filter(p => !refPlaceholderSet.has(p));

    if (missingPlaceholders.length > 0 || extraPlaceholders.length > 0) {
      warnings.push({
        type: 'placeholder_mismatch',
        key,
        locale,
        message: `Placeholder name mismatch: missing ${missingPlaceholders.join(', ')}, extra ${extraPlaceholders.join(', ')}`,
        suggestion: `Use exact placeholders: ${refPlaceholders.join(', ')}`,
      });
    }
  }
}

/**
 * 
 */
export function createTranslationSyncChecker() {
  let lastValidation: TranslationValidationResult | null = null;

  return {
    async check(): Promise<TranslationValidationResult> {
      const result = await validateTranslations();
      lastValidation = result;
      return result;
    },

    getLastResult(): TranslationValidationResult | null {
      return lastValidation;
    },

    isHealthy(): boolean {
      return lastValidation?.isValid ?? false;
    },

    getCoverage(): number {
      return lastValidation?.coverage ?? ZERO;
    },
  };
}

/**
 * React Scan 
 *
 *  React 
 * 
 */

import React from 'react';

export interface ReactScanConfig {
  enabled: boolean;
  showToolbar: boolean;
  log: boolean;
  trackUnnecessaryRenders: boolean;
  animationSpeed: 'slow' | 'fast' | 'off';
}

/**
 *  React Scan
 *
 * 
 * 1.  (NODE_ENV === 'development')
 * 2. 
 * 3. 
 */
export const shouldEnableReactScan = (): boolean => {
  // 
  if (process.env.NODE_ENV === 'production') {
    return false;
  }

  // 
  if (process.env.NODE_ENV !== 'development') {
    return false;
  }

  // 
  const explicitlyDisabled =
    process.env.NEXT_PUBLIC_DISABLE_REACT_SCAN === 'true';

  // 
  return !explicitlyDisabled;
};

/**
 *  React Scan 
 */
export const DEFAULT_REACT_SCAN_CONFIG: ReactScanConfig = {
  enabled: shouldEnableReactScan(),
  showToolbar: true,
  log: false, // 
  trackUnnecessaryRenders: true, // 
  animationSpeed: 'fast',
};

/**
 *  React Scan
 *
 * /
 */
export const initReactScan = async (config: Partial<ReactScanConfig> = {}) => {
  // 
  const shouldEnable = shouldEnableReactScan();

  if (!shouldEnable) {
    // 
    if (process.env.NODE_ENV === 'development') {
      console.log(
        ' React Scan disabled (set NEXT_PUBLIC_DISABLE_REACT_SCAN=false to enable)',
      );
    }
    return;
  }

  try {
    //  React Scan
    const { scan } = await import('react-scan');

    const finalConfig = {
      ...DEFAULT_REACT_SCAN_CONFIG,
      ...config,
    };

    scan(finalConfig);

    // 
    console.log(' React Scan initialized with config:', finalConfig);
    console.log(' To disable: set NEXT_PUBLIC_DISABLE_REACT_SCAN=true');
  } catch (error) {
    console.warn('Failed to initialize React Scan:', error);
  }
};

/**
 * React Scan 
 *
 *  React  React Scan
 */
export const ReactScanWrapper = ({
  children,
}: {
  children: React.ReactNode;
}) => {
  if (typeof window !== 'undefined') {
    // 
    initReactScan();
  }

  return children;
};

