}

// i18n
function renderWithI18n(component: React.ReactElement, locale: string = 'en') {
  const messages = locale === 'en' ? mockEnMessages : mockZhMessages;

  return render(
    <NextIntlClientProvider
      locale={locale}
      messages={messages}
    >
      {component}
    </NextIntlClientProvider>,
  );
}

