#!/usr/bin/env tsx

import { execSync } from 'child_process';
import { writeFileSync } from 'fs';

async function continueESLintFixes() {
  const log: string[] = [];

  log.push('üîÑ ÁªßÁª≠Â§ÑÁêÜESLintÈîôËØØ...');
  log.push(`Êó∂Èó¥: ${new Date().toISOString()}`);

  try {
    // 1. Ê£ÄÊü•TypeScriptÁºñËØëÁä∂ÊÄÅ
    try {
      execSync('pnpm type-check', { stdio: 'pipe' });
      log.push('‚úÖ TypeScriptÁºñËØë: Êó†ÈîôËØØ');
    } catch (error) {
      log.push('‚ùå TypeScriptÁºñËØë: ‰ªçÊúâÈîôËØØ');
    }

    // 2. ËøêË°åESLintËá™Âä®‰øÆÂ§ç
    log.push('\nüîß ËøêË°åESLintËá™Âä®‰øÆÂ§ç...');
    try {
      const eslintResult = execSync('pnpm eslint "src/**/*.{ts,tsx}" --fix --max-warnings=0', { 
        stdio: 'pipe',
        encoding: 'utf8'
      });
      log.push('‚úÖ ESLintËá™Âä®‰øÆÂ§çÂÆåÊàê');
    } catch (error) {
      log.push('‚ö†Ô∏è ESLintËá™Âä®‰øÆÂ§çÂÆåÊàêÔºàÂèØËÉΩ‰ªçÊúâÈîôËØØÔºâ');
    }

    // 3. Ê£ÄÊü•Ââ©‰ΩôÈîôËØØ
    try {
      const eslintCheck = execSync('pnpm eslint "src/**/*.{ts,tsx}" --format=compact', { 
        stdio: 'pipe',
        encoding: 'utf8'
      });
      const errorLines = eslintCheck.split('\n').filter(line => 
        line.includes('error') || line.includes('warning')
      );
      log.push(`üìä Ââ©‰ΩôESLintÈóÆÈ¢ò: ${errorLines.length} ‰∏™`);
    } catch (error) {
      const output = error.stdout?.toString() || '';
      const errorLines = output.split('\n').filter(line => 
        line.includes('error') || line.includes('warning')
      );
      log.push(`üìä Ââ©‰ΩôESLintÈóÆÈ¢ò: ${errorLines.length} ‰∏™`);
    }

    // 4. ‰∏ã‰∏ÄÊ≠•ËÆ°Âàí
    log.push('\nüéØ ‰∏ã‰∏ÄÊ≠•Ë°åÂä®:');
    log.push('1. ‚úÖ ASTÈ≠îÊ≥ïÊï∞Â≠óÂ∑•ÂÖ∑Â∑≤‰øÆÂ§ç');
    log.push('2. üîÑ Áõ∏ÂØπË∑ØÂæÑÂØºÂÖ•Â∑•ÂÖ∑Â∑≤ËøêË°å');
    log.push('3. üîÑ ESLintËá™Âä®‰øÆÂ§çÂ∑≤ÊâßË°å');
    log.push('4. üìã ÈúÄË¶ÅÊâãÂä®Â§ÑÁêÜÂâ©‰ΩôÈîôËØØ:');
    log.push('   - ÂÆâÂÖ®ÂØπË±°Ê≥®ÂÖ•ÈóÆÈ¢ò');
    log.push('   - ÊòæÂºèanyÁ±ªÂûã');
    log.push('   - ÂÖ∂‰ªñÊâãÂä®‰øÆÂ§çÈ°π');

  } catch (error) {
    log.push(`‚ùå Â§ÑÁêÜÂ§±Ë¥•: ${error}`);
  }

  // ‰øùÂ≠òÁªìÊûú
  writeFileSync('eslint-fix-progress.txt', log.join('\n'));
  console.log('ËøõÂ∫¶Êä•ÂëäÂ∑≤‰øùÂ≠òÂà∞ eslint-fix-progress.txt');
}

continueESLintFixes();

import path from 'path';
import type { NextConfig } from 'next';
import bundleAnalyzer from '@next/bundle-analyzer';
import createMDX from '@next/mdx';
import { withSentryConfig } from '@sentry/nextjs';
import createNextIntlPlugin from 'next-intl/plugin';
// Avoid importing TS/alias-based runtime modules into next.config to keep config load stable.
function computeSecurityHeaders(nonce?: string) {
  const isEnabled = process.env.SECURITY_HEADERS_ENABLED !== 'false';
  if (!isEnabled) return [] as { key: string; value: string }[];

  const isDevelopment = process.env.NODE_ENV === 'development';
  const isProduction = process.env.NODE_ENV === 'production';

  const cspDirectives: Record<string, string[] | undefined> = {
    'default-src': ["'self'"],
    'script-src': [
      "'self'",
      ...(isDevelopment ? ["'unsafe-inline'", "'unsafe-eval'"] : []),
      ...(nonce ? [`'nonce-${nonce}'`] : []),
      'https://va.vercel-scripts.com',
      'https://js.sentry-cdn.com',
      'https://challenges.cloudflare.com',
      'https://www.googletagmanager.com',
      'https://www.google-analytics.com',
    ],
    'style-src': [
      "'self'",
      ...(isDevelopment ? ["'unsafe-inline'"] : []),
      ...(nonce ? [`'nonce-${nonce}'`] : []),
      'https://fonts.googleapis.com',
    ],
    'img-src': [
      "'self'",
      'data:',
      'https:',
      'https://va.vercel-scripts.com',
      'https://images.unsplash.com',
      'https://via.placeholder.com',
    ],
    'font-src': ["'self'", 'data:', 'https://fonts.gstatic.com'],
    'connect-src': [
      "'self'",
      'https://vitals.vercel-insights.com',
      'https://o4507902318592000.ingest.us.sentry.io',
      ...(isDevelopment ? ['http://localhost:*', 'ws://localhost:*'] : []),
      'https://api.resend.com',
    ],
    'frame-src': ["'none'", 'https://challenges.cloudflare.com'],
    'object-src': ["'none'"],
    'base-uri': ["'self'"],
    'form-action': ["'self'"],
    'frame-ancestors': ["'none'"],
    'upgrade-insecure-requests': isProduction ? [] : undefined,
  };

  const csp = Object.entries(cspDirectives)
    .filter(([, value]) => value !== undefined)
    .map(([key, value]) => {
      if (Array.isArray(value) && value.length > 0) return `${key} ${value.join(' ')}`;
      return key;
    })
    .join('; ');

  return [
    { key: 'X-Frame-Options', value: 'DENY' },
    { key: 'X-Content-Type-Options', value: 'nosniff' },
    { key: 'Referrer-Policy', value: 'strict-origin-when-cross-origin' },
    { key: 'Strict-Transport-Security', value: 'max-age=63072000; includeSubDomains; preload' },
    { key: 'X-XSS-Protection', value: '1; mode=block' },
    { key: 'Content-Security-Policy', value: csp },
    {
      key: 'Permissions-Policy',
      value: ['camera=()', 'microphone=()', 'geolocation=()', 'interest-cohort=()', 'payment=()', 'usb=()'].join(', '),
    },
    { key: 'Cross-Origin-Embedder-Policy', value: 'unsafe-none' },
    { key: 'Cross-Origin-Opener-Policy', value: 'same-origin' },
    { key: 'Cross-Origin-Resource-Policy', value: 'cross-origin' },
  ];
}

const withNextIntl = createNextIntlPlugin('./src/i18n/request.ts');

const withBundleAnalyzer = bundleAnalyzer({
  enabled: process.env['ANALYZE'] === 'true',
});

const withMDX = createMDX({
  // Add markdown plugins here, as desired
  options: {
    remarkPlugins: [],
    rehypePlugins: [],
  },
});

const nextConfig: NextConfig = {
  /* config options here */

  // Configure pageExtensions to include markdown and MDX files
  pageExtensions: ['js', 'jsx', 'md', 'mdx', 'ts', 'tsx'],

  // ESLint ÈÖçÁΩÆ
  eslint: {
    dirs: ['src'],
    // ‰∏¥Êó∂ÊîæÂÆΩÊûÑÂª∫Êúü ESLint ÈòªÂ°ûÔºåPhase 3 Ê∏ÖÈõ∂ÂêéÊÅ¢Â§ç‰∏∫‰∏•Ê†ºÊ®°Âºè
    ignoreDuringBuilds: true,
  },

  // Enable source maps for better error tracking
  productionBrowserSourceMaps: true,

  // Next.js Compiler ÈÖçÁΩÆ
  compiler: {
    // Áîü‰∫ßÁéØÂ¢ÉÁßªÈô§ console ËØ≠Âè•Ôºå‰ΩÜ‰øùÁïô error Âíå warn Á∫ßÂà´
    // ËøôÊúâÂä©‰∫éÂáèÂ∞ëÁîü‰∫ßÁéØÂ¢ÉÁöÑÂåÖÂ§ßÂ∞èÂπ∂ÈÅøÂÖçÊΩúÂú®ÁöÑ‰ø°ÊÅØÊ≥ÑÈú≤
    removeConsole:
      process.env.NODE_ENV === 'production'
        ? {
            exclude: ['error', 'warn'], // ‰øùÁïô console.error Âíå console.warn
          }
        : false, // ÂºÄÂèëÁéØÂ¢É‰øùÁïôÊâÄÊúâ console ËØ≠Âè•
  },

  // Performance optimizations
  experimental: {
    optimizePackageImports: ['lucide-react', '@radix-ui/react-icons'],
    // ÂêØÁî®Next.js 15ÂÆûÈ™åÊÄßtestProxyÁî®‰∫éPlaywrightÊµãËØïÁéØÂ¢É
    testProxy: true,
  },

  // Ëß£ÂÜ≥ Turbopack + OpenTelemetry ‰æùËµñÈóÆÈ¢ò
  // Ëøô‰∫õÂåÖÂ∑≤ÁªèÂú® Next.js 15 ÁöÑÈªòËÆ§Â§ñÈÉ®ÂåÖÂàóË°®‰∏≠
  // ‰ΩÜ Turbopack Âú®Â§ÑÁêÜÂÆÉ‰ª¨Êó∂ÈÅáÂà∞ÈóÆÈ¢òÔºåÊâÄ‰ª•Êàë‰ª¨ÊöÇÊó∂ÁßªÈô§Ëøô‰∏™ÈÖçÁΩÆ
  // ËÆ© Next.js ‰ΩøÁî®ÈªòËÆ§ÁöÑÂ§ñÈÉ®ÂåÖÂ§ÑÁêÜÊñπÂºè

  webpack: (config, { dev, isServer }) => {
    // Path alias configuration for @/ -> src/
    config.resolve.alias = {
      ...config.resolve.alias,
      '@': path.resolve(__dirname, 'src'),
    };

    // ÊúçÂä°Á´ØÂ∞ÜÈÉ®ÂàÜÈáçÂûã‰æùËµñÊ†áËÆ∞‰∏∫ externalÔºåÈÅøÂÖçÊçÜÁªëÂà∞ÈÄöÁî® chunk Ëß¶ÂèëÂàùÂßãÂåñÈ°∫Â∫èÈóÆÈ¢ò
    if (isServer) {
      // ‰øùÊåÅ Node.js ËøêË°åÊó∂‰ªé node_modules Âä®ÊÄÅÂä†ËΩΩ
      // ÈÅøÂÖçÂú®ÊûÑÂª∫Êúü/Êî∂ÈõÜÈò∂ÊÆµÂä†ËΩΩÁ¨¨‰∏âÊñπÂ∫ìÂÜÖÈÉ®Â§çÊùÇ‰æùËµñ
      // Â∞§ÂÖ∂ÊòØ airtable Á≠â SDK
      // ËØ¥ÊòéÔºöcommonjs ÂΩ¢Âºè external ‰∏ç‰ºöÂΩ±ÂìçËøêË°åÊó∂ require/import
      (config.externals ||= [] as any[]).push({ airtable: 'commonjs airtable' });
    }

    // Áîü‰∫ßÁéØÂ¢ÉÂåÖÂ§ßÂ∞è‰ºòÂåñ - ÁªÜÁ≤íÂ∫¶‰ª£Á†ÅÂàÜÂâ≤
    if (!dev && !isServer) {
      config.optimization.splitChunks.cacheGroups = {
        ...config.optimization.splitChunks.cacheGroups,
        // React Ê†∏ÂøÉÂ∫ìÂçïÁã¨ÂàÜÁ¶ª
        react: {
          test: /[\\/]node_modules[\\/](react|react-dom)[\\/]/,
          name: 'react',
          chunks: 'all',
          priority: 20,
          enforce: true,
        },
        // Radix UI ÁªÑ‰ª∂Â∫ìÂàÜÁ¶ª
        radixui: {
          test: /[\\/]node_modules[\\/]@radix-ui[\\/]/,
          name: 'radix-ui',
          chunks: 'all',
          priority: 15,
          enforce: true,
        },
        // Lucide ÂõæÊ†áÂ∫ìÂàÜÁ¶ª
        lucide: {
          test: /[\\/]node_modules[\\/]lucide-react[\\/]/,
          name: 'lucide',
          chunks: 'all',
          priority: 15,
          enforce: true,
        },
        // Sentry ÁõëÊéßÂ∑•ÂÖ∑ÂàÜÁ¶ª
        sentry: {
          test: /[\\/]node_modules[\\/]@sentry[\\/]/,
          name: 'sentry',
          chunks: 'all',
          priority: 15,
          enforce: true,
        },
        // Next.js Áõ∏ÂÖ≥Â∫ìÂàÜÁ¶ª
        nextjs: {
          test: /[\\/]node_modules[\\/](next-intl|@next[\\/]|next-themes|nextjs-toploader)[\\/]/,
          name: 'nextjs-libs',
          chunks: 'all',
          priority: 10,
          enforce: true,
        },
        // MDX Áõ∏ÂÖ≥Â∫ìÂàÜÁ¶ª
        mdx: {
          test: /[\\/]node_modules[\\/](@mdx-js|gray-matter|remark|rehype)[\\/]/,
          name: 'mdx-libs',
          chunks: 'all',
          priority: 12,
          enforce: true,
        },
        // Ë°®ÂçïÂíåÈ™åËØÅÂ∫ìÂàÜÁ¶ª
        forms: {
          test: /[\\/]node_modules[\\/](react-hook-form|@hookform|zod)[\\/]/,
          name: 'form-libs',
          chunks: 'all',
          priority: 12,
          enforce: true,
        },
        // Â∑•ÂÖ∑Â∫ìÂàÜÁ¶ª
        utils: {
          test: /[\\/]node_modules[\\/](clsx|class-variance-authority|tailwind-merge|embla-carousel)[\\/]/,
          name: 'utils',
          chunks: 'all',
          priority: 8,
          enforce: true,
        },
        // ÂÖ∂‰ªñÁ¨¨‰∏âÊñπÂ∫ì
        vendor: {
          test: /[\\/]node_modules[\\/](?!(react|react-dom|@radix-ui|lucide-react|@sentry|next-intl|@next[\\/]|next-themes|nextjs-toploader|@mdx-js|gray-matter|remark|rehype|react-hook-form|@hookform|zod|clsx|class-variance-authority|tailwind-merge|embla-carousel)[\\/])/,
          name: 'vendors',
          chunks: 'all',
          priority: 5,
          enforce: true,
        },
      };
    }
    return config;
  },

  async headers() {
    // Note: This function is async to comply with Next.js API requirements
    // even though we're returning static configuration
    await Promise.resolve(); // Satisfy require-await ESLint rule

    const securityHeaders = computeSecurityHeaders();
    // Prefer CSP from middleware (with nonce). Remove CSP here to avoid duplication/conflicts.
    const headersNoCSP = securityHeaders.filter(
      (h) => h.key !== 'Content-Security-Policy',
    );

    // Next.js 15.4.7+ requires non-empty headers array
    if (headersNoCSP.length === 0) {
      return [];
    }

    return [
      {
        source: '/:path*',
        headers: headersNoCSP,
      },
    ];
  },
};

// Optimized Sentry webpack plugin options for smaller bundle size
const sentryWebpackPluginOptions = {
  // For all available options, see:
  // https://github.com/getsentry/sentry-webpack-plugin#options

  org: process.env['SENTRY_ORG'] || '',
  project: process.env['SENTRY_PROJECT'] || '',

  // Only print logs for uploading source maps in CI
  silent: !process.env['CI'],

  // Optimize for smaller bundle size - disable source map upload in development
  widenClientFileUpload: process.env['NODE_ENV'] === 'production',

  // Automatically tree-shake Sentry logger statements to reduce bundle size
  disableLogger: true,

  // Disable automatic instrumentation to reduce bundle size
  automaticVercelMonitors: false,

  // Additional bundle size optimizations
  hideSourceMaps: true, // Hide source maps from public access

  // Only enable in production to reduce development build time
  enabled: process.env['NODE_ENV'] === 'production',
};

export default withSentryConfig(
  withBundleAnalyzer(withNextIntl(withMDX(nextConfig))),
  sentryWebpackPluginOptions,
);

#!/usr/bin/env node

/**
 * TypeScriptÈîôËØØÂàÜÊûêÂ∑•ÂÖ∑
 * ÂàÜÊûêÈ°πÁõÆ‰∏≠ÁöÑTypeScriptÈîôËØØÁ±ªÂûãÂíåÂàÜÂ∏ÉÔºåÂà∂ÂÆö‰øÆÂ§ç‰ºòÂÖàÁ∫ß
 */

const { execSync } = require('child_process');

// TypeScriptÈîôËØØ‰ª£Á†ÅËØ¥Êòé
const ERROR_DESCRIPTIONS = {
  TS2322: 'Á±ªÂûã‰∏çÂåπÈÖç - Type X is not assignable to type Y',
  TS2339: 'Â±ûÊÄß‰∏çÂ≠òÂú® - Property X does not exist on type Y',
  TS1205: 'ÈáçÊñ∞ÂØºÂá∫ÈîôËØØ - Re-export errors',
  TS6196: 'Êú™‰ΩøÁî®Â£∞Êòé - Declared but never used',
  TS2345: 'ÂèÇÊï∞Á±ªÂûãÈîôËØØ - Argument type mismatch',
  TS6133: 'Êú™‰ΩøÁî®ÂèòÈáè - Variable declared but never read',
  TS1484:
    'verbatimModuleSyntaxÈîôËØØ - Type must be imported using type-only import',
  TS18046: 'ÂèØËÉΩ‰∏∫undefined - Expression is possibly undefined',
  TS1361:
    'import type‰Ωú‰∏∫ÂÄº‰ΩøÁî® - Cannot be used as a value (imported using import type)',
  TS2739: 'Áº∫Â∞ëÂ±ûÊÄß - Type is missing properties',
  TS2351: 'ÊûÑÈÄ†ÂáΩÊï∞ÈáçËΩΩÈîôËØØ - Constructor overload error',
  TS2571: 'ÂØπË±°ÂèØËÉΩ‰∏∫null - Object is possibly null',
  TS2532: 'ÂØπË±°ÂèØËÉΩ‰∏∫undefined - Object is possibly undefined',
  TS2353: 'ÂØπË±°Â≠óÈù¢ÈáèÈîôËØØ - Object literal may only specify known properties',
  TS7006: 'ÈöêÂºèanyÁ±ªÂûã - Parameter implicitly has an any type',
};

// ‰øÆÂ§ç‰ºòÂÖàÁ∫ßÔºà1=ÊúÄÈ´òÔºå5=ÊúÄ‰ΩéÔºâ
const FIX_PRIORITY = {
  TS1361: 1, // import typeÈîôËØØÔºåÂÆπÊòìÊâπÈáè‰øÆÂ§ç
  TS1484: 1, // verbatimModuleSyntaxÈîôËØØÔºåÂÆπÊòìÊâπÈáè‰øÆÂ§ç
  TS6133: 2, // Êú™‰ΩøÁî®ÂèòÈáèÔºåÂÆπÊòì‰øÆÂ§ç
  TS6196: 2, // Êú™‰ΩøÁî®Â£∞ÊòéÔºåÂÆπÊòì‰øÆÂ§ç
  TS7006: 2, // ÈöêÂºèanyÔºåÈúÄË¶ÅÊ∑ªÂä†Á±ªÂûã
  TS2345: 3, // ÂèÇÊï∞Á±ªÂûãÈîôËØØÔºåÈúÄË¶Å‰ªîÁªÜÊ£ÄÊü•
  TS18046: 3, // ÂèØËÉΩ‰∏∫undefinedÔºåÈúÄË¶ÅnullÊ£ÄÊü•
  TS2532: 3, // ÂØπË±°ÂèØËÉΩ‰∏∫undefinedÔºåÈúÄË¶ÅnullÊ£ÄÊü•
  TS2571: 3, // ÂØπË±°ÂèØËÉΩ‰∏∫nullÔºåÈúÄË¶ÅnullÊ£ÄÊü•
  TS2339: 4, // Â±ûÊÄß‰∏çÂ≠òÂú®ÔºåÂèØËÉΩÈúÄË¶ÅÊé•Âè£‰øÆÊîπ
  TS2322: 4, // Á±ªÂûã‰∏çÂåπÈÖçÔºåÈúÄË¶Å‰ªîÁªÜÂàÜÊûê
  TS2739: 4, // Áº∫Â∞ëÂ±ûÊÄßÔºåÈúÄË¶ÅÊé•Âè£‰øÆÊîπ
  TS2353: 4, // ÂØπË±°Â≠óÈù¢ÈáèÈîôËØØÔºåÈúÄË¶ÅÁ±ªÂûãÂÆö‰πâ
  TS2351: 5, // ÊûÑÈÄ†ÂáΩÊï∞ÈáçËΩΩÔºåÂ§çÊùÇ‰øÆÂ§ç
  TS1205: 5, // ÈáçÊñ∞ÂØºÂá∫ÈîôËØØÔºåÊû∂ÊûÑÁõ∏ÂÖ≥
};

/**
 * Ëé∑ÂèñTypeScriptÈîôËØØÁªüËÆ°
 */
function getTypeScriptErrorStats() {
  try {
    console.log('üîç ÂàÜÊûêTypeScriptÈîôËØØ...');

    const output = execSync('pnpm type-check', {
      encoding: 'utf8',
      stdio: 'pipe',
    });
    const errorLines = output
      .split('\n')
      .filter((line) => line.includes('error TS'));

    const errorStats = {};
    const fileStats = {};

    errorLines.forEach((line) => {
      // ÊèêÂèñÈîôËØØ‰ª£Á†Å
      const errorMatch = line.match(/error TS(\d+)/);
      if (errorMatch) {
        const errorCode = `TS${errorMatch[1]}`;
        errorStats[errorCode] = (errorStats[errorCode] || 0) + 1;
      }

      // ÊèêÂèñÊñá‰ª∂Ë∑ØÂæÑ
      const fileMatch = line.match(/^([^(]+)\(/);
      if (fileMatch) {
        const filePath = fileMatch[1];
        fileStats[filePath] = (fileStats[filePath] || 0) + 1;
      }
    });

    return { errorStats, fileStats, totalErrors: errorLines.length };
  } catch (error) {
    // TypeScriptÊúâÈîôËØØÊó∂‰ºöÊäõÂá∫ÂºÇÂ∏∏Ôºå‰ΩÜÊàë‰ª¨ÈúÄË¶ÅÊçïËé∑ËæìÂá∫
    const output = error.stdout || error.stderr || '';
    const errorLines = output
      .split('\n')
      .filter((line) => line.includes('error TS'));

    if (errorLines.length === 0) {
      console.error('Ëé∑ÂèñTypeScriptÈîôËØØÁªüËÆ°Â§±Ë¥•:', error.message);
      return { errorStats: {}, fileStats: {}, totalErrors: 0 };
    }

    const errorStats = {};
    const fileStats = {};

    errorLines.forEach((line) => {
      // ÊèêÂèñÈîôËØØ‰ª£Á†Å
      const errorMatch = line.match(/error TS(\d+)/);
      if (errorMatch) {
        const errorCode = `TS${errorMatch[1]}`;
        errorStats[errorCode] = (errorStats[errorCode] || 0) + 1;
      }

      // ÊèêÂèñÊñá‰ª∂Ë∑ØÂæÑ
      const fileMatch = line.match(/^([^(]+)\(/);
      if (fileMatch) {
        const filePath = fileMatch[1];
        fileStats[filePath] = (fileStats[filePath] || 0) + 1;
      }
    });

    return { errorStats, fileStats, totalErrors: errorLines.length };
  }
}

/**
 * ÂàÜÊûêÈîôËØØ‰ºòÂÖàÁ∫ß
 */
function analyzeErrorPriority(errorStats) {
  const priorityGroups = {
    1: [], // ÊúÄÈ´ò‰ºòÂÖàÁ∫ß
    2: [], // È´ò‰ºòÂÖàÁ∫ß
    3: [], // ‰∏≠Á≠â‰ºòÂÖàÁ∫ß
    4: [], // ‰Ωé‰ºòÂÖàÁ∫ß
    5: [], // ÊúÄ‰Ωé‰ºòÂÖàÁ∫ß
  };

  Object.entries(errorStats).forEach(([errorCode, count]) => {
    const priority = FIX_PRIORITY[errorCode] || 4;
    priorityGroups[priority].push({ errorCode, count, priority });
  });

  return priorityGroups;
}

/**
 * ÂàÜÊûêÊñá‰ª∂ÈîôËØØÂàÜÂ∏É
 */
function analyzeFileDistribution(fileStats) {
  const sortedFiles = Object.entries(fileStats)
    .sort(([, a], [, b]) => b - a)
    .slice(0, 20); // ÂèñÂâç20‰∏™ÈîôËØØÊúÄÂ§öÁöÑÊñá‰ª∂

  const fileCategories = {
    tests: [],
    api: [],
    components: [],
    lib: [],
    app: [],
    other: [],
  };

  sortedFiles.forEach(([filePath, count]) => {
    if (
      filePath.includes('__tests__') ||
      filePath.includes('.test.') ||
      filePath.includes('.spec.')
    ) {
      fileCategories.tests.push({ filePath, count });
    } else if (filePath.includes('/api/')) {
      fileCategories.api.push({ filePath, count });
    } else if (filePath.includes('/components/')) {
      fileCategories.components.push({ filePath, count });
    } else if (filePath.includes('/lib/')) {
      fileCategories.lib.push({ filePath, count });
    } else if (filePath.includes('/app/')) {
      fileCategories.app.push({ filePath, count });
    } else {
      fileCategories.other.push({ filePath, count });
    }
  });

  return fileCategories;
}

/**
 * ÁîüÊàê‰øÆÂ§çÂª∫ËÆÆ
 */
function generateFixSuggestions(priorityGroups, fileCategories) {
  const suggestions = [];

  // ‰ºòÂÖàÁ∫ß1ÔºöÁ´ãÂç≥‰øÆÂ§ç
  if (priorityGroups[1].length > 0) {
    const highPriorityErrors = priorityGroups[1].reduce(
      (sum, item) => sum + item.count,
      0,
    );
    suggestions.push({
      priority: 1,
      title: 'Á´ãÂç≥‰øÆÂ§ç - import typeÂíåÊ®°ÂùóËØ≠Ê≥ïÈîôËØØ',
      description: `${highPriorityErrors}‰∏™ÈîôËØØÔºåÂèØ‰ª•ÊâπÈáèËá™Âä®‰øÆÂ§ç`,
      errors: priorityGroups[1],
      action: 'ËøêË°å node scripts/fix-import-type-issues.js',
    });
  }

  // ‰ºòÂÖàÁ∫ß2ÔºöÊ∏ÖÁêÜÊú™‰ΩøÁî®‰ª£Á†Å
  if (priorityGroups[2].length > 0) {
    const cleanupErrors = priorityGroups[2].reduce(
      (sum, item) => sum + item.count,
      0,
    );
    suggestions.push({
      priority: 2,
      title: '‰ª£Á†ÅÊ∏ÖÁêÜ - Êú™‰ΩøÁî®ÂèòÈáèÂíåÈöêÂºèany',
      description: `${cleanupErrors}‰∏™ÈîôËØØÔºåÈúÄË¶ÅÊâãÂä®Ê∏ÖÁêÜ`,
      errors: priorityGroups[2],
      action: 'ÊâãÂä®ÁßªÈô§Êú™‰ΩøÁî®ÂèòÈáèÔºåÊ∑ªÂä†Á±ªÂûãÊ≥®Ëß£',
    });
  }

  // ÊµãËØïÊñá‰ª∂‰øÆÂ§ç
  if (fileCategories.tests.length > 0) {
    const testErrors = fileCategories.tests.reduce(
      (sum, item) => sum + item.count,
      0,
    );
    suggestions.push({
      priority: 2,
      title: 'ÊµãËØïÊñá‰ª∂‰øÆÂ§ç',
      description: `${testErrors}‰∏™ÊµãËØïÊñá‰ª∂ÈîôËØØÔºå‰∏ªË¶ÅÊòØMockÁ±ªÂûãÈóÆÈ¢ò`,
      files: fileCategories.tests.slice(0, 10),
      action: '‰øÆÂ§çÊµãËØïÊñá‰ª∂‰∏≠ÁöÑMockÁ±ªÂûãÂÆö‰πâ',
    });
  }

  return suggestions;
}

/**
 * ‰∏ªÂáΩÊï∞
 */
function main() {
  console.log('üöÄ ÂºÄÂßãTypeScriptÈîôËØØÂàÜÊûê...\n');

