        });

        this.metrics.eslintIssues = totalIssues;
      } catch (eslintError) {
        // ESLintÂèØËÉΩËøîÂõûÈùûÈõ∂ÈÄÄÂá∫Á†Å‰ΩÜ‰ªçÊúâÊúâÊïàËæìÂá∫
        try {
