            this.metrics.eslintIssues = 2075; // ‰ΩøÁî®Â∑≤Áü•ÁöÑÂΩìÂâçÂÄº
          }
        } catch (parseError) {
          console.warn('‚ö†Ô∏è Êó†Ê≥ïËß£ÊûêESLintËæìÂá∫Ôºå‰ΩøÁî®‰º∞ÁÆóÂÄº');
          this.metrics.eslintIssues = 2075; // ‰ΩøÁî®Â∑≤Áü•ÁöÑÂΩìÂâçÂÄº
        }
      }
    }

    console.log(`‚úÖ ESLintÈóÆÈ¢òÊï∞: ${this.metrics.eslintIssues}`);
    return this.metrics.eslintIssues;
  }

  /**
   * ÁªüËÆ°Êñá‰ª∂Êï∞Èáè
   */
  async analyzeFileCount() {
    console.log('üìÅ ÁªüËÆ°Êñá‰ª∂Êï∞Èáè...');

    const allFiles = glob.sync(METRICS_CONFIG.scanPatterns.all);
    const i18nFiles = glob.sync(METRICS_CONFIG.scanPatterns.i18n);

    this.metrics.totalFiles = allFiles.length;
