      },
    };

    // ‰øùÂ≠òJSONÊä•Âëä
    const jsonPath = path.join(this.outputDir, `metrics-${Date.now()}.json`);
    fs.writeFileSync(jsonPath, JSON.stringify(report, null, 2));

    // ‰øùÂ≠òMarkdownÊä•Âëä
    const mdPath = path.join(this.outputDir, `metrics-${Date.now()}.md`);
