#!/usr/bin/env node

/**
 * ÊâπÈáè‰øÆÂ§çÁõ∏ÂØπË∑ØÂæÑÂØºÂÖ•ÈóÆÈ¢ò
 * Áõ¥Êé•Â§ÑÁêÜÊñá‰ª∂ÂàóË°®ÔºåÈÅøÂÖçESLintËæìÂá∫Ëß£ÊûêÈóÆÈ¢ò
 */

const fs = require('fs');
const path = require('path');

// È°πÁõÆÊ†πÁõÆÂΩï
const PROJECT_ROOT = process.cwd();

// ÈúÄË¶Å‰øÆÂ§çÁöÑÊñá‰ª∂ÂàóË°®Ôºà‰ªéESLintËæìÂá∫‰∏≠ÊèêÂèñÔºâ
const FILES_TO_FIX = [
  'src/lib/locale-storage-history.ts',
  'src/lib/locale-storage-maintenance.ts',
  'src/lib/locale-storage-preference.ts',
  'src/lib/locale-storage-types.ts',
  'src/lib/performance-monitoring-coordinator.ts',
  'src/lib/performance-monitoring-core.ts',
  'src/lib/performance-monitoring-integrations.ts',
];

/**
 * Â∞ÜÁõ∏ÂØπË∑ØÂæÑËΩ¨Êç¢‰∏∫ÁªùÂØπË∑ØÂæÑ
 */
function convertRelativeToAbsolute(filePath, importPath) {
  // Â¶ÇÊûúÂ∑≤ÁªèÊòØÁªùÂØπË∑ØÂæÑÔºåË∑≥Ëøá
  if (importPath.startsWith('@/') || !importPath.startsWith('.')) {
    return importPath;
  }

  // Ëé∑ÂèñÊñá‰ª∂ÊâÄÂú®ÁõÆÂΩï
  const fileDir = path.dirname(filePath);

  // Ëß£ÊûêÁõ∏ÂØπË∑ØÂæÑ
  const absolutePath = path.resolve(fileDir, importPath);

  // Ê£ÄÊü•ÊòØÂê¶Âú®È°πÁõÆÊ†πÁõÆÂΩïÂÜÖ
  if (!absolutePath.startsWith(PROJECT_ROOT)) {
    console.warn(`  Ë≠¶Âëä: Ë∑ØÂæÑË∂ÖÂá∫È°πÁõÆËåÉÂõ¥ ${importPath} -> ${absolutePath}`);
    return importPath; // ‰øùÊåÅÂéüÊ†∑
  }

  // ËΩ¨Êç¢‰∏∫Áõ∏ÂØπ‰∫éÈ°πÁõÆÊ†πÁõÆÂΩïÁöÑË∑ØÂæÑ
  let relativePath = path.relative(PROJECT_ROOT, absolutePath);

  // Â¶ÇÊûúË∑ØÂæÑ‰∏çÂú® src ÁõÆÂΩïÂÜÖÔºå‰ΩøÁî® @/../ ÂâçÁºÄ
  if (!relativePath.startsWith('src/')) {
    return `@/../${relativePath.replace(/\\/g, '/')}`;
  }

  // ÁßªÈô§ src/ ÂâçÁºÄÂπ∂ËΩ¨Êç¢‰∏∫ @/ Ë∑ØÂæÑ
  relativePath = relativePath.substring(4); // ÁßªÈô§ 'src/'
  return `@/${relativePath.replace(/\\/g, '/')}`;
}

/**
 * ‰øÆÂ§çÂçï‰∏™Êñá‰ª∂ÁöÑÂØºÂÖ•
 */
function fixFileImports(filePath) {
  const fullPath = path.join(PROJECT_ROOT, filePath);

  if (!fs.existsSync(fullPath)) {
    console.log(`  Êñá‰ª∂‰∏çÂ≠òÂú®: ${filePath}`);
    return false;
  }

  try {
    const content = fs.readFileSync(fullPath, 'utf8');
    const lines = content.split('\n');
    let modified = false;

    for (let i = 0; i < lines.length; i++) {
      const line = lines[i];

      // ÂåπÈÖçÂêÑÁßçÂØºÂÖ•/ÂØºÂá∫ËØ≠Âè•
      const patterns = [
        /^(\s*import\s+.*?\s+from\s+['"])([^'"]+)(['"].*)/,
        /^(\s*export\s+.*?\s+from\s+['"])([^'"]+)(['"].*)/,
        /^(\s*}\s+from\s+['"])([^'"]+)(['"].*)/,
      ];

      for (const pattern of patterns) {
        const match = line.match(pattern);
        if (match) {
          const [, prefix, importPath, suffix] = match;

          // Âè™Â§ÑÁêÜÁõ∏ÂØπË∑ØÂæÑÂØºÂÖ•
          if (importPath.startsWith('.')) {
            const newPath = convertRelativeToAbsolute(fullPath, importPath);
            if (newPath !== importPath) {
              lines[i] = prefix + newPath + suffix;
              modified = true;
              console.log(`  ${importPath} -> ${newPath}`);
            }
          }
          break; // ÊâæÂà∞ÂåπÈÖçÂ∞±Ë∑≥Âá∫
        }
      }
    }

    if (modified) {
      fs.writeFileSync(fullPath, lines.join('\n'));
      return true;
    }

    return false;
  } catch (error) {
    console.error(`‰øÆÂ§çÊñá‰ª∂Â§±Ë¥• ${filePath}:`, error.message);
    return false;
  }
}

/**
 * ‰∏ªÂáΩÊï∞
 */
function main() {
  console.log('üîß ÂºÄÂßãÊâπÈáè‰øÆÂ§çÁõ∏ÂØπË∑ØÂæÑÂØºÂÖ•ÈóÆÈ¢ò...\n');

  console.log(`ÂáÜÂ§á‰øÆÂ§ç ${FILES_TO_FIX.length} ‰∏™Êñá‰ª∂\n`);

  let fixedCount = 0;

  for (const filePath of FILES_TO_FIX) {
    console.log(`‰øÆÂ§çÊñá‰ª∂: ${filePath}`);

    if (fixFileImports(filePath)) {
      fixedCount++;
    }

    console.log('');
  }

  console.log(`‚úÖ ‰øÆÂ§çÂÆåÊàêÔºÅÂÖ±‰øÆÂ§ç ${fixedCount} ‰∏™Êñá‰ª∂`);
}

if (require.main === module) {
  main();
}

module.exports = { convertRelativeToAbsolute, fixFileImports };

// Simple alias consistency checker for @/* across tsconfig, next.config, and ESLint resolver
// Usage: node scripts/check-config-consistency.js

/* eslint-disable no-console */
const fs = require('fs');

function readJSON(p) {
  return JSON.parse(fs.readFileSync(p, 'utf8'));
}

try {
  const ts = readJSON('tsconfig.json');
  const tsAlias = ts?.compilerOptions?.paths?.['@/*']?.[0];

  const nextConfigSrc = fs.readFileSync('next.config.ts', 'utf8');
  const hasNextAlias = /resolve\.alias[\s\S]*['"]@['"]:\s*path\.resolve\(__dirname,\s*['"]src['"]\)/.test(
    nextConfigSrc,
  );

  const eslintSrc = fs.readFileSync('eslint.config.mjs', 'utf8');
  const hasTsResolver = /['"]import\/resolver['"]:\s*\{[\s\S]*typescript/.test(
    eslintSrc,
  );

  const ok = tsAlias === './src/*' && hasNextAlias && hasTsResolver;
  if (!ok) {
    console.error('Alias consistency failed:', {
      tsAlias,
      hasNextAlias,
      hasTsResolver,
    });
    process.exit(1);
  }

  console.log('Alias consistency OK');
} catch (err) {
  console.error('config:check error', err);
  process.exit(1);
}

// Basic i18n sync check for messages/en.json vs messages/zh.json
// Usage: node scripts/check-i18n-sync.js

/* eslint-disable no-console */
const fs = require('fs');

function flat(obj, pfx = '', out = {}) {
  for (const [k, v] of Object.entries(obj)) {
    const key = pfx ? `${pfx}.${k}` : k;
    if (v && typeof v === 'object' && !Array.isArray(v)) flat(v, key, out);
    else out[key] = true;
  }
  return out;
}

try {
  const en = JSON.parse(fs.readFileSync('messages/en.json', 'utf8'));
  const zh = JSON.parse(fs.readFileSync('messages/zh.json', 'utf8'));

  const enKeys = Object.keys(flat(en));
  const zhKeys = Object.keys(flat(zh));

  const missingInZh = enKeys.filter((k) => !zhKeys.includes(k));
  const extraInZh = zhKeys.filter((k) => !enKeys.includes(k));

  if (missingInZh.length || extraInZh.length) {
    console.error('i18n sync failed', {
      missingInZh,
      extraInZh,
    });
    process.exit(1);
  }

  console.log('i18n sync OK');
} catch (err) {
  console.error('i18n:sync:check error', err);
  process.exit(1);
}

