/**
 * ÈáçÂ§çÂØºÂÖ•Ê∏ÖÁêÜËÑöÊú¨
 * 
 * ÂäüËÉΩÔºö
 * 1. Ê£ÄÊµãÂπ∂ÁßªÈô§ÈáçÂ§çÁöÑimportËØ≠Âè•
 * 2. Áªü‰∏Ä‰ΩøÁî®@/constantsË∑ØÂæÑ
 * 3. Ê∏ÖÁêÜÂÜó‰ΩôÁöÑÂØºÂÖ•
 * 
 * ‰ΩøÁî®ÊñπÊ≥ïÔºö
 * node scripts/clean-duplicate-imports.js
 */

const fs = require('fs');
const path = require('path');

class DuplicateImportCleaner {
  constructor() {
    this.stats = {
      filesProcessed: 0,
      duplicatesFound: 0,
      duplicatesFixed: 0,
      errors: 0
    };
  }

  /**
   * ‰∏ªÊâßË°åÂáΩÊï∞
   */
  async run() {
    console.log('üîß ÂºÄÂßãÊ∏ÖÁêÜÈáçÂ§çÂØºÂÖ•...\n');

    try {
      const files = this.getFilesToProcess();
      console.log(`üìÅ ÊâæÂà∞ ${files.length} ‰∏™Êñá‰ª∂ÈúÄË¶ÅÂ§ÑÁêÜ\n`);

      for (const file of files) {
        await this.processFile(file);
      }

      this.printStats();

    } catch (error) {
      console.error('‚ùå ÊâßË°åÂ§±Ë¥•:', error.message);
      process.exit(1);
    }
  }

  /**
   * Ëé∑ÂèñË¶ÅÂ§ÑÁêÜÁöÑÊñá‰ª∂ÂàóË°®
   */
  getFilesToProcess() {
    const files = [];

    const walkDir = (dir) => {
      const items = fs.readdirSync(dir);

      for (const item of items) {
        const fullPath = path.join(dir, item);
        const stat = fs.statSync(fullPath);

        if (stat.isDirectory()) {
          if (!item.startsWith('.') && item !== 'node_modules' && !item.includes('test')) {
            walkDir(fullPath);
          }
        } else if (stat.isFile()) {
          if ((item.endsWith('.ts') || item.endsWith('.tsx')) && 
              !item.endsWith('.d.ts') && 
              !item.includes('.test.')) {
            files.push(fullPath);
          }
        }
      }
    };

    walkDir('src');
    return files;
  }

  /**
   * Â§ÑÁêÜÂçï‰∏™Êñá‰ª∂
   */
  async processFile(filePath) {
    try {
      const content = fs.readFileSync(filePath, 'utf8');
      const lines = content.split('\n');

      // Êü•ÊâæÈáçÂ§çÂØºÂÖ•
      const importLines = this.findImportLines(lines);
      const duplicates = this.findDuplicates(importLines);

      if (duplicates.length === 0) {
        return;
      }

      this.stats.filesProcessed++;
      this.stats.duplicatesFound += duplicates.length;

      console.log(`üîç Â§ÑÁêÜÊñá‰ª∂: ${filePath}`);
      console.log(`   ÂèëÁé∞ ${duplicates.length} ‰∏™ÈáçÂ§çÂØºÂÖ•`);

      // ‰øÆÂ§çÈáçÂ§çÂØºÂÖ•
      const fixedLines = this.fixDuplicates(lines, duplicates);

      // ÂÜôÂÖ•‰øÆÂ§çÂêéÁöÑÂÜÖÂÆπ
      fs.writeFileSync(filePath, fixedLines.join('\n'), 'utf8');

      this.stats.duplicatesFixed += duplicates.length;
      console.log(`   ‚úÖ Â∑≤‰øÆÂ§ç\n`);

    } catch (error) {
      this.stats.errors++;
      console.error(`‚ùå Â§ÑÁêÜÊñá‰ª∂Â§±Ë¥• ${filePath}:`, error.message);
    }
  }

  /**
   * Êü•ÊâæÂØºÂÖ•Ë°å
   */
  findImportLines(lines) {
    const imports = [];

    for (let i = 0; i < lines.length; i++) {
      const line = lines[i].trim();

      // ÂåπÈÖçimportËØ≠Âè•
      const importMatch = line.match(/^import\s*{\s*([^}]+)\s*}\s*from\s*['"]([^'"]+)['"];?\s*$/);
      if (importMatch) {
        const [, importList, source] = importMatch;
        const identifiers = importList
          .split(',')
          .map(id => id.trim())
          .filter(id => id.length > 0);

        imports.push({
          lineIndex: i,
          line: line,
          source: source,
          identifiers: identifiers
        });
      }
    }

    return imports;
  }

  /**
   * Êü•ÊâæÈáçÂ§çÂØºÂÖ•
   */
  findDuplicates(importLines) {
    const duplicates = [];
    const seenIdentifiers = new Map();

    for (const importLine of importLines) {
      for (const identifier of importLine.identifiers) {
        if (seenIdentifiers.has(identifier)) {
          const existing = seenIdentifiers.get(identifier);
          duplicates.push({
            identifier,
            existing,
            duplicate: importLine
          });
        } else {
          seenIdentifiers.set(identifier, importLine);
        }
      }
    }

    return duplicates;
  }

  /**
   * ‰øÆÂ§çÈáçÂ§çÂØºÂÖ•
   */
  fixDuplicates(lines, duplicates) {
    const linesToRemove = new Set();

    // Ê†áËÆ∞Ë¶ÅÁßªÈô§ÁöÑÈáçÂ§çÂØºÂÖ•Ë°å
    for (const duplicate of duplicates) {
      const { existing, duplicate: dup } = duplicate;

      // ‰ºòÂÖà‰øùÁïô@/constantsË∑ØÂæÑÁöÑÂØºÂÖ•
      let removeImport;
      if (existing.source === '@/constants') {
        removeImport = dup;
      } else if (dup.source === '@/constants') {
        removeImport = existing;
      } else {
        // ÈÉΩ‰∏çÊòØ@/constantsÔºåÁßªÈô§ÂêéÈù¢ÁöÑ
        removeImport = dup;
      }

      linesToRemove.add(removeImport.lineIndex);
    }

    // ÁßªÈô§ÈáçÂ§çÁöÑÂØºÂÖ•Ë°å
    return lines.filter((line, index) => !linesToRemove.has(index));
  }

  /**
   * ËæìÂá∫ÁªüËÆ°‰ø°ÊÅØ
   */
  printStats() {
    console.log('\nüìä Â§ÑÁêÜÁªüËÆ°:');
    console.log(`   Â§ÑÁêÜÊñá‰ª∂Êï∞: ${this.stats.filesProcessed}`);
    console.log(`   ÂèëÁé∞ÈáçÂ§çÂØºÂÖ•: ${this.stats.duplicatesFound}`);
    console.log(`   ‰øÆÂ§çÈáçÂ§çÂØºÂÖ•: ${this.stats.duplicatesFixed}`);
    console.log(`   ÈîôËØØÊï∞: ${this.stats.errors}`);

    if (this.stats.errors === 0) {
      console.log('\n‚úÖ ÈáçÂ§çÂØºÂÖ•Ê∏ÖÁêÜÂÆåÊàêÔºÅ');
    } else {
      console.log('\n‚ö†Ô∏è  ÈÉ®ÂàÜÊñá‰ª∂Â§ÑÁêÜÂ§±Ë¥•ÔºåËØ∑Ê£ÄÊü•ÈîôËØØ‰ø°ÊÅØ');
    }
  }
}

// ÊâßË°åËÑöÊú¨
if (require.main === module) {
  const cleaner = new DuplicateImportCleaner();
  cleaner.run().catch(console.error);
}

module.exports = DuplicateImportCleaner;

#!/usr/bin/env tsx
/**
 * Áõ∏ÂØπË∑ØÂæÑÂØºÂÖ•‰øÆÂ§çÂ∑•ÂÖ∑
 * Â∞Ü src ÂÜÖÁöÑÁõ∏ÂØπË∑ØÂæÑÂØºÂÖ• (./../...) Êîπ‰∏∫ @/ Âà´ÂêçÂØºÂÖ•
 */

import { Project, SourceFile, ImportDeclaration, Node } from 'ts-morph';
import { resolve, relative, dirname, join } from 'path';
import { existsSync, writeFileSync } from 'fs';

interface ImportFixResult {
  filePath: string;
  fixedImports: number;
  errors: string[];
}

class AliasImportFixer {
  private project: Project;
  private srcPath: string;
  private results: ImportFixResult[] = [];

  constructor() {
    this.project = new Project({
      tsConfigFilePath: 'tsconfig.json',
    });
    this.srcPath = resolve(process.cwd(), 'src');
  }

  /**
   * Ê£ÄÊü•ÂØºÂÖ•Ë∑ØÂæÑÊòØÂê¶ÊåáÂêë src ÂÜÖÈÉ®
   */
  private isInternalSrcImport(importPath: string, currentFilePath: string): boolean {
    if (!importPath.startsWith('.')) {
      return false; // ‰∏çÊòØÁõ∏ÂØπË∑ØÂæÑ
    }

    const currentDir = dirname(currentFilePath);
    const resolvedPath = resolve(currentDir, importPath);

    // Ê£ÄÊü•Ëß£ÊûêÂêéÁöÑË∑ØÂæÑÊòØÂê¶Âú® src ÁõÆÂΩïÂÜÖ
    return resolvedPath.startsWith(this.srcPath);
  }

  /**
   * Â∞ÜÁõ∏ÂØπË∑ØÂæÑËΩ¨Êç¢‰∏∫ @/ Âà´ÂêçË∑ØÂæÑ
   */
  private convertToAliasPath(importPath: string, currentFilePath: string): string {
    const currentDir = dirname(currentFilePath);
    const resolvedPath = resolve(currentDir, importPath);
    const relativePath = relative(this.srcPath, resolvedPath);

    // Á°Æ‰øùË∑ØÂæÑ‰ΩøÁî®Ê≠£ÊñúÊù†ÔºàUnixÈ£éÊ†ºÔºâ
    const normalizedPath = relativePath.replace(/\\/g, '/');

    return `@/${normalizedPath}`;
  }

  /**
   * È™åËØÅËΩ¨Êç¢ÂêéÁöÑË∑ØÂæÑÊòØÂê¶ÊúâÊïà
   */
  private validateAliasPath(aliasPath: string): boolean {
    // ÁßªÈô§ @/ ÂâçÁºÄÔºåËé∑ÂèñÂÆûÈôÖÊñá‰ª∂Ë∑ØÂæÑ
    const actualPath = aliasPath.replace(/^@\//, '');
    const fullPath = join(this.srcPath, actualPath);

    // Ê£ÄÊü•Êñá‰ª∂ÊòØÂê¶Â≠òÂú®ÔºàÂèØËÉΩÊúâÊâ©Â±ïÂêçÔºâ
    const possibleExtensions = ['', '.ts', '.tsx', '.js', '.jsx'];

    for (const ext of possibleExtensions) {
      if (existsSync(fullPath + ext)) {
        return true;
      }
      // Ê£ÄÊü•ÊòØÂê¶ÊòØÁõÆÂΩïÔºàindexÊñá‰ª∂Ôºâ
      if (existsSync(join(fullPath + ext, 'index.ts')) ||
          existsSync(join(fullPath + ext, 'index.tsx'))) {
        return true;
      }
    }

    return false;
  }

  /**
   * ‰øÆÂ§çÂçï‰∏™Êñá‰ª∂ÁöÑÂØºÂÖ•
   */
  private fixFileImports(sourceFile: SourceFile): ImportFixResult {
    const filePath = sourceFile.getFilePath();
    const result: ImportFixResult = {
      filePath: relative(process.cwd(), filePath),
      fixedImports: 0,
      errors: []
    };

    // Âè™Â§ÑÁêÜ src ÁõÆÂΩïÂÜÖÁöÑÊñá‰ª∂
    if (!filePath.startsWith(this.srcPath)) {
      return result;
    }

    const importDeclarations = sourceFile.getImportDeclarations();

    for (const importDecl of importDeclarations) {
      const moduleSpecifier = importDecl.getModuleSpecifierValue();

      if (this.isInternalSrcImport(moduleSpecifier, filePath)) {
        try {
          const aliasPath = this.convertToAliasPath(moduleSpecifier, filePath);

          // È™åËØÅËΩ¨Êç¢ÂêéÁöÑË∑ØÂæÑ
          if (this.validateAliasPath(aliasPath)) {
            importDecl.setModuleSpecifier(aliasPath);
            result.fixedImports++;
            console.log(`  ‚úì ${moduleSpecifier} ‚Üí ${aliasPath}`);
          } else {
            result.errors.push(`Êó†Ê≥ïÈ™åËØÅË∑ØÂæÑ: ${moduleSpecifier} ‚Üí ${aliasPath}`);
          }
        } catch (error) {
          result.errors.push(`ËΩ¨Êç¢Â§±Ë¥•: ${moduleSpecifier} - ${error}`);
        }
      }
    }

    return result;
  }

  /**
   * ÊâπÈáè‰øÆÂ§çÊâÄÊúâÊñá‰ª∂
   */
  public async fixAllImports(dryRun: boolean = false): Promise<void> {
    console.log('üîß ÂºÄÂßã‰øÆÂ§çÁõ∏ÂØπË∑ØÂæÑÂØºÂÖ•...');
    console.log(`üìÅ Ê∫êÁ†ÅÁõÆÂΩï: ${this.srcPath}`);
    console.log(`üîç Ê®°Âºè: ${dryRun ? 'Âπ≤Ë∑ëÊ®°Âºè' : 'ÂÜôÂÖ•Ê®°Âºè'}`);
    console.log();

    // Ëé∑ÂèñÊâÄÊúâ TypeScript Êñá‰ª∂
    const sourceFiles = this.project.getSourceFiles([
      'src/**/*.ts',
      'src/**/*.tsx'
    ]);

    console.log(`üìä ÊâæÂà∞ ${sourceFiles.length} ‰∏™Êñá‰ª∂`);
    console.log();

    let totalFixed = 0;
    let totalErrors = 0;

    for (const sourceFile of sourceFiles) {
      const result = this.fixFileImports(sourceFile);

      if (result.fixedImports > 0 || result.errors.length > 0) {
        console.log(`üìÑ ${result.filePath}:`);

        if (result.fixedImports > 0) {
          console.log(`  ‚úÖ ‰øÆÂ§ç ${result.fixedImports} ‰∏™ÂØºÂÖ•`);
          totalFixed += result.fixedImports;
        }

        if (result.errors.length > 0) {
          console.log(`  ‚ùå ${result.errors.length} ‰∏™ÈîôËØØ:`);
          result.errors.forEach(error => console.log(`    - ${error}`));
          totalErrors += result.errors.length;
        }

        console.log();
      }

      this.results.push(result);
    }

    // ‰øùÂ≠òÊõ¥Êîπ
    if (!dryRun && totalFixed > 0) {
      console.log('üíæ ‰øùÂ≠òÊõ¥Êîπ...');
      await this.project.save();
      console.log('‚úÖ ‰øùÂ≠òÂÆåÊàê');
    }

    // ËæìÂá∫ÊÄªÁªì
    console.log('üìä ‰øÆÂ§çÊÄªÁªì:');
    console.log(`  ‰øÆÂ§çÂØºÂÖ•: ${totalFixed} ‰∏™`);
    console.log(`  ÈîôËØØÊï∞Èáè: ${totalErrors} ‰∏™`);
    console.log(`  Â§ÑÁêÜÊñá‰ª∂: ${this.results.filter(r => r.fixedImports > 0).length} ‰∏™`);

    if (dryRun) {
      console.log('\nüîç ËøôÊòØÂπ≤Ë∑ëÊ®°ÂºèÔºåÊú™ÂÆûÈôÖ‰øÆÊîπÊñá‰ª∂');
    }
  }

  /**
   * Ëé∑Âèñ‰øÆÂ§çÁªìÊûú
   */
  public getResults(): ImportFixResult[] {
    return this.results;
  }
}

async function main() {
  const args = process.argv.slice(2);
  const dryRun = args.includes('--dry-run');

  const fixer = new AliasImportFixer();

  try {
    await fixer.fixAllImports(dryRun);

    // ‰øùÂ≠òÁªìÊûúÂà∞Êñá‰ª∂
    const results = fixer.getResults();
    const summary = {
      timestamp: new Date().toISOString(),
      dryRun,
      totalFiles: results.length,
      filesWithFixes: results.filter(r => r.fixedImports > 0).length,
      totalFixes: results.reduce((sum, r) => sum + r.fixedImports, 0),
      results
    };

    writeFileSync('alias-imports-result.json', JSON.stringify(summary, null, 2));
    console.log('ÁªìÊûúÂ∑≤‰øùÂ≠òÂà∞ alias-imports-result.json');

  } catch (error) {
    console.error('‚ùå ‰øÆÂ§çÂ§±Ë¥•:', error);
    process.exit(1);
  }
}

if (require.main === module) {
  main();
}

#!/usr/bin/env node

/**
 * Êî∂ÈõÜÁº∫Â§±Â∏∏ÈáèËÑöÊú¨
 * 
 * ÂäüËÉΩÔºö
 * 1. ‰ªéTypeScriptÈîôËØØ‰∏≠ÊèêÂèñÁº∫Â§±ÁöÑÂ∏∏Èáè
 * 2. ÂàÜÊûêÂ∏∏ÈáèÁ±ªÂûãÂπ∂ÁîüÊàêÂÆö‰πâ
 * 3. Ê∑ªÂä†Âà∞Áõ∏Â∫îÁöÑÂ∏∏ÈáèÊñá‰ª∂‰∏≠
 * 
 * ‰ΩøÁî®ÊñπÊ≥ïÔºö
 * node scripts/collect-missing-constants.js
 */

const fs = require('fs');
const { execSync } = require('child_process');

class MissingConstantsCollector {
  constructor() {
    this.missingConstants = new Map();
  }

  async run() {
    console.log('üîß ÂºÄÂßãÊî∂ÈõÜÁº∫Â§±Â∏∏Èáè...\n');

    try {
      // ‰ªéTypeScriptÈîôËØØ‰∏≠ÊèêÂèñÁº∫Â§±Â∏∏Èáè
      this.extractMissingConstants();

      // ÂàÜÊûêÂπ∂ÂàÜÁ±ªÂ∏∏Èáè
      this.categorizeConstants();

      // Ê∑ªÂä†Â∏∏ÈáèÂà∞Áõ∏Â∫îÊñá‰ª∂
      this.addConstantsToFiles();

      console.log('\n‚úÖ Áº∫Â§±Â∏∏ÈáèÊî∂ÈõÜÂÆåÊàêÔºÅ');

    } catch (error) {
      console.error('‚ùå ÊâßË°åÂ§±Ë¥•:', error.message);
      process.exit(1);
    }
  }

  extractMissingConstants() {
    console.log('üìä ‰ªéTypeScriptÈîôËØØ‰∏≠ÊèêÂèñÁº∫Â§±Â∏∏Èáè...');

    try {
      const output = execSync('pnpm type-check 2>&1', { encoding: 'utf8' });
      const lines = output.split('\n');

      for (const line of lines) {
        const match = line.match(/Cannot find name '([^']+)'/);
        if (match) {
          const constantName = match[1];
          if (!this.missingConstants.has(constantName)) {
            this.missingConstants.set(constantName, {
              name: constantName,
              count: 1,
              category: this.categorizeConstant(constantName),
              value: this.inferValue(constantName)
            });
          } else {
            this.missingConstants.get(constantName).count++;
          }
        }
      }

      console.log(`   ÂèëÁé∞ ${this.missingConstants.size} ‰∏™Áº∫Â§±Â∏∏Èáè`);

    } catch (error) {
      console.log('   TypeScriptÊ£ÄÊü•ÂÆåÊàêÔºåÁªßÁª≠Â§ÑÁêÜ...');
    }
  }

  categorizeConstant(name) {
    if (name.startsWith('MAGIC_0_')) return 'decimal';
    if (name.startsWith('MAGIC_')) return 'count';
    if (name.includes('_PER_')) return 'time';
    if (name.includes('PERCENTAGE_')) return 'decimal';
    if (name.includes('DAYS_')) return 'time';
    if (name.includes('_MS')) return 'time';
    return 'count';
  }

  inferValue(name) {
    // Êé®Êñ≠Â∏∏ÈáèÂÄº
    if (name === 'DAYS_PER_MONTH') return '30';
    if (name === 'PERCENTAGE_QUARTER') return '25';
    if (name === 'MAGIC_0_5') return '0.5';
    if (name === 'MAGIC_0_7') return '0.7';
    if (name === 'MAGIC_0_9') return '0.9';
    if (name === 'MAGIC_17') return '17';
    if (name === 'MAGIC_18') return '18';
    if (name === 'MAGIC_22') return '22';
    if (name === 'MAGIC_999') return '999';
    if (name === 'MINUTE_MS') return '60000';

    // ‰ªéÂêçÁß∞‰∏≠ÊèêÂèñÊï∞Â≠ó
    const numberMatch = name.match(/MAGIC_(\d+(?:_\d+)*)/);
    if (numberMatch) {
      return numberMatch[1].replace(/_/g, '.');
    }

    return '1'; // ÈªòËÆ§ÂÄº
  }

  categorizeConstants() {
    console.log('\nüìã ÂàÜÁ±ªÂ∏∏Èáè:');

    const categories = {
      decimal: [],
      count: [],
      time: []
    };

    for (const [name, info] of this.missingConstants) {
      categories[info.category].push(info);
    }

    for (const [category, constants] of Object.entries(categories)) {
      if (constants.length > 0) {
        console.log(`   ${category}: ${constants.map(c => c.name).join(', ')}`);
      }
    }
  }

  addConstantsToFiles() {
    console.log('\nüìù Ê∑ªÂä†Â∏∏ÈáèÂà∞Êñá‰ª∂...');

    const categories = {
      decimal: 'src/constants/decimal.ts',
      count: 'src/constants/count.ts',
      time: 'src/constants/time.ts'
    };

    for (const [category, filePath] of Object.entries(categories)) {
      const constants = Array.from(this.missingConstants.values())
        .filter(c => c.category === category);

      if (constants.length > 0) {
        this.addConstantsToFile(filePath, constants);
      }
    }
  }

  addConstantsToFile(filePath, constants) {
    console.log(`   Ê∑ªÂä†Âà∞ ${filePath}: ${constants.map(c => c.name).join(', ')}`);

    try {
      let content = fs.readFileSync(filePath, 'utf8');

      // Âú®Êñá‰ª∂Êú´Â∞æÊ∑ªÂä†Êñ∞Â∏∏Èáè
      const newConstants = constants.map(c => 
        `export const ${c.name} = ${c.value};`
      ).join('\n');

      // Ê£ÄÊü•Â∏∏ÈáèÊòØÂê¶Â∑≤Â≠òÂú®
      const existingConstants = constants.filter(c => 
        content.includes(`${c.name} =`) || content.includes(`${c.name}:`)
      );

      const newConstantsToAdd = constants.filter(c => 
        !content.includes(`${c.name} =`) && !content.includes(`${c.name}:`)
      );

      if (existingConstants.length > 0) {
        console.log(`     Ë∑≥ËøáÂ∑≤Â≠òÂú®ÁöÑÂ∏∏Èáè: ${existingConstants.map(c => c.name).join(', ')}`);
      }

      if (newConstantsToAdd.length > 0) {
        content += '\n\n// Ëá™Âä®Ê∑ªÂä†ÁöÑÁº∫Â§±Â∏∏Èáè\n';
        content += newConstantsToAdd.map(c => 
          `export const ${c.name} = ${c.value};`
        ).join('\n');

        fs.writeFileSync(filePath, content, 'utf8');
        console.log(`     ‚úÖ Ê∑ªÂä†‰∫Ü ${newConstantsToAdd.length} ‰∏™Êñ∞Â∏∏Èáè`);
      }

    } catch (error) {
      console.error(`     ‚ùå Â§ÑÁêÜÊñá‰ª∂Â§±Ë¥•: ${error.message}`);
    }
  }
}

// ÊâßË°åËÑöÊú¨
if (require.main === module) {
  const collector = new MissingConstantsCollector();
  collector.run().catch(console.error);
}

module.exports = MissingConstantsCollector;

