      securityPassed = false;
    }
  });

  // ÊÄªÁªì
  console.log('\nüìã Ê£ÄÊü•ÁªìÊûú:');
  console.log(
    `  ÂÖ®Â±ÄË¶ÜÁõñÁéá: ${globalResults.passed ? '‚úÖ ÈÄöËøá' : '‚ùå Êú™ËææÊ†á'}`,
