      console.log(`üìä Â∞±Áª™ÂàÜÊï∞: ${this.readiness.score}/100`);

      const confidenceIcons = {
        high: 'üü¢ È´ò‰ø°ÂøÉ',
        medium: 'üü° ‰∏≠Á≠â‰ø°ÂøÉ',
        low: 'üü† ‰Ωé‰ø°ÂøÉ',
      };

      console.log(`üéØ ‰ø°ÂøÉÂ∫¶: ${confidenceIcons[this.readiness.confidence]}`);
    } else {
      console.log('‚ùå ÊöÇ‰∏çÂª∫ËÆÆÈÉ®ÁΩ≤');
      console.log(`üìä Â∞±Áª™ÂàÜÊï∞: ${this.readiness.score}/100 (ÈúÄË¶Å‚â•80)`);
    }

    // ÊòæÁ§∫ÈòªÂ°ûÊÄßÈóÆÈ¢ò
    if (this.readiness.blockers.length > 0) {
      console.log('\nüö® ÂøÖÈ°ª‰øÆÂ§çÁöÑÈóÆÈ¢ò:');
      this.readiness.blockers.forEach((blocker, index) => {
        console.log(`${index + 1}. ${blocker.message}`);
        console.log(`   üí° Ëß£ÂÜ≥ÊñπÊ°à: ${blocker.action}`);
