    }

    // ÊòæÁ§∫Ë≠¶Âëä
    if (this.readiness.warnings.length > 0) {
      console.log('\n‚ö†Ô∏è  Âª∫ËÆÆ‰øÆÂ§çÁöÑÈóÆÈ¢ò:');
      this.readiness.warnings.forEach((warning, index) => {
        console.log(`${index + 1}. ${warning.message}`);
        console.log(`   üí° Âª∫ËÆÆ: ${warning.action}`);
      });
    }

    // ÊòæÁ§∫‰∏ã‰∏ÄÊ≠•Âª∫ËÆÆ
    console.log('\nüìã ‰∏ã‰∏ÄÊ≠•:');
    if (this.readiness.ready) {
      console.log('üéâ ÂèØ‰ª•ÊâßË°åÈÉ®ÁΩ≤ÂëΩ‰ª§');
      console.log('üìä Êü•ÁúãËØ¶ÁªÜÊä•Âëä: pnpm report');
    } else {
      console.log('üîß ‰øÆÂ§ç‰∏äËø∞ÈóÆÈ¢òÂêéÈáçÊñ∞Ê£ÄÊü•');
      console.log('üè• Êü•ÁúãÈ°πÁõÆÂÅ•Â∫∑: pnpm health');
      console.log('üìä Êü•ÁúãËØ¶ÁªÜÊä•Âëä: pnpm report');
    }
  }

  /**
   * ÊâßË°åÈÉ®ÁΩ≤Â∞±Áª™Ê£ÄÊü•
   */
  async checkReadiness() {
    console.log('üéØ ÈÉ®ÁΩ≤Â∞±Áª™Âø´ÈÄüÊ£ÄÊü•');
    console.log('='.repeat(30));

    const success = await this.runDeploymentChecks();
