#!/usr/bin/env node
/*
 * ‰øÆÂ§çÊâÄÊúâimportËØ≠Âè•
 * - ÂàÜÊûêÊØè‰∏™Êñá‰ª∂‰ΩøÁî®ÁöÑÂ∏∏Èáè
 * - ÁîüÊàêÊ≠£Á°ÆÁöÑimportËØ≠Âè•
 */

const fs = require('fs');
const path = require('path');
const { execSync } = require('child_process');

function getAllAvailableConstants() {
  try {
    const constantsContent = fs.readFileSync(
      'src/constants/magic-numbers.ts',
      'utf8',
    );
    const constants = new Set();

    const matches = constantsContent.matchAll(
      /export const ([A-Z_][A-Z0-9_]*)/g,
    );
    for (const match of matches) {
      constants.add(match[1]);
    }

    return constants;
  } catch (error) {
    console.error('‚ùå Êó†Ê≥ïËØªÂèñÂ∏∏ÈáèÊñá‰ª∂:', error.message);
    return new Set();
  }
}

function findFilesWithMagicConstants() {
  try {
    const result = execSync(
      'find src/ -name "*.ts" -o -name "*.tsx" | xargs grep -l "MAGIC_\\|ANIMATION_\\|SIZE_\\|OPACITY_\\|COUNT_\\|PERCENTAGE_\\|OFFSET_\\|HEX_\\|DECIMAL_\\|BYTES_\\|SECONDS_\\|HOURS_\\|DAYS_"',
      {
        encoding: 'utf8',
        shell: true,
      },
    );
    return result
      .trim()
      .split('\n')
      .filter((file) => file.length > 0);
  } catch (error) {
    console.log('‚ö†Ô∏è  Ê≤°ÊúâÊâæÂà∞‰ΩøÁî®Â∏∏ÈáèÁöÑÊñá‰ª∂');
    return [];
  }
}

function getUsedConstantsInFile(filePath, availableConstants) {
  try {
    const content = fs.readFileSync(filePath, 'utf8');
    const usedConstants = new Set();

    // Êü•ÊâæÊâÄÊúâÂèØËÉΩÁöÑÂ∏∏Èáè‰ΩøÁî®
    for (const constant of availableConstants) {
      const regex = new RegExp(`\\b${constant}\\b`, 'g');
      if (regex.test(content)) {
        usedConstants.add(constant);
      }
    }

    return usedConstants;
  } catch (error) {
    console.error(`‚ùå Êó†Ê≥ïËØªÂèñÊñá‰ª∂ ${filePath}:`, error.message);
    return new Set();
  }
}

function fixImportInFile(filePath, usedConstants) {
  try {
    let content = fs.readFileSync(filePath, 'utf8');
    let modified = false;

    // ÁßªÈô§Áé∞ÊúâÁöÑmagic-numbersÂØºÂÖ•
    const importRegex =
      /import\s*\{[^}]*\}\s*from\s*['"]@\/constants\/magic-numbers['"];\s*\n?/g;
    content = content.replace(importRegex, '');

    if (usedConstants.size > 0) {
      // ÁîüÊàêÊñ∞ÁöÑimportËØ≠Âè•
      const sortedConstants = Array.from(usedConstants).sort();
      const newImport = `import { ${sortedConstants.join(', ')} } from '@/constants/magic-numbers';\n`;

      // Âú®Á¨¨‰∏Ä‰∏™importËØ≠Âè•ÂêéÊèíÂÖ•
      const firstImportMatch = content.match(/^import .+;$/m);
      if (firstImportMatch) {
        content = content.replace(
          firstImportMatch[0],
          `${firstImportMatch[0]  }\n${  newImport}`,
        );
      } else {
        // Â¶ÇÊûúÊ≤°ÊúâÂÖ∂‰ªñimportÔºåÂú®Êñá‰ª∂ÂºÄÂ§¥ÊèíÂÖ•
        content = `${newImport  }\n${  content}`;
      }

      modified = true;
    }

    if (modified) {
      fs.writeFileSync(filePath, content, 'utf8');
      console.log(`‚úÖ ‰øÆÂ§ç: ${filePath} (${usedConstants.size}‰∏™Â∏∏Èáè)`);
      return 1;
    }

    return 0;
  } catch (error) {
    console.error(`‚ùå Â§ÑÁêÜÊñá‰ª∂Â§±Ë¥• ${filePath}:`, error.message);
    return 0;
  }
}

function main() {
  console.log('üîß ‰øÆÂ§çÊâÄÊúâimportËØ≠Âè•...');

  const availableConstants = getAllAvailableConstants();
  console.log(`üìä ÂèØÁî®Â∏∏Èáè: ${availableConstants.size} ‰∏™`);

  const affectedFiles = findFilesWithMagicConstants();
  if (affectedFiles.length === 0) {
    console.log('‚úÖ Ê≤°ÊúâÂèëÁé∞‰ΩøÁî®Â∏∏ÈáèÁöÑÊñá‰ª∂');
    return;
  }

  console.log(`üìä ÂèëÁé∞ ${affectedFiles.length} ‰∏™‰ΩøÁî®Â∏∏ÈáèÁöÑÊñá‰ª∂`);

  let totalFixes = 0;
  for (const filePath of affectedFiles) {
    const usedConstants = getUsedConstantsInFile(filePath, availableConstants);
    if (usedConstants.size > 0) {
      totalFixes += fixImportInFile(filePath, usedConstants);
    }
  }

  console.log(`\nüéâ ‰øÆÂ§çÂÆåÊàêÔºÅ`);
  console.log(`üìä ÊÄªËÆ°‰øÆÂ§ç ${totalFixes} ‰∏™Êñá‰ª∂ÁöÑimportËØ≠Âè•`);

  if (totalFixes > 0) {
    console.log('\nüí° Âª∫ËÆÆËøêË°å‰ª•‰∏ãÂëΩ‰ª§È™åËØÅ‰øÆÂ§çÊïàÊûúÔºö');
    console.log('pnpm type-check');
  }
}

if (require.main === module) {
  main();
}

