 */

const fs = require('fs');
const path = require('path');
const { execSync } = require('child_process');

// Êü•ÊâæÊâÄÊúâÂåÖÂê´ magic-numbers ÂØºÂÖ•ÁöÑÊñá‰ª∂
function findFilesWithMagicNumbersImports() {
  try {
    const result = execSync(
      'find src/ -name "*.ts" -o -name "*.tsx" | xargs grep -l "from \'@/constants/magic-numbers\'" 2>/dev/null || true',
      {
        encoding: 'utf8',
        shell: true,
      },
    );
    return result
      .trim()
      .split('\n')
      .filter((file) => file.length > 0);
  } catch (error) {
    console.log('‚ö†Ô∏è  Ê≤°ÊúâÊâæÂà∞‰ΩøÁî® magic-numbers ÂØºÂÖ•ÁöÑÊñá‰ª∂');
    return [];
  }
}

// ‰øÆÂ§çÂçï‰∏™Êñá‰ª∂ÁöÑÂØºÂÖ•ËØ≠Âè•
function fixImportsInFile(filePath) {
  try {
    let content = fs.readFileSync(filePath, 'utf8');
    let modified = false;

    // ÂåπÈÖç magic-numbers ÂØºÂÖ•ËØ≠Âè•
    const importRegex = /import\s*{\s*([^}]+)\s*}\s*from\s*['"]@\/constants\/magic-numbers['"];?/g;

    let match;
    while ((match = importRegex.exec(content)) !== null) {
      const fullImport = match[0];
      const importList = match[1];

      // ÊõøÊç¢‰∏∫Áªü‰∏ÄÂØºÂÖ•
      const newImport = `import { ${importList} } from '@/constants';`;
      content = content.replace(fullImport, newImport);
      modified = true;

      console.log(`‚úÖ ‰øÆÂ§çÂØºÂÖ•: ${filePath}`);
      console.log(`  ÂéüÂßã: ${fullImport}`);
      console.log(`  ‰øÆÂ§ç: ${newImport}`);
    }

    if (modified) {
      fs.writeFileSync(filePath, content, 'utf8');
      return 1;
    }

    return 0;
  } catch (error) {
    console.error(`‚ùå Â§ÑÁêÜÊñá‰ª∂Â§±Ë¥• ${filePath}:`, error.message);
    return 0;
  }
}

// ‰∏ªÂáΩÊï∞
function main() {
  console.log('üîß ÂºÄÂßã‰øÆÂ§çÂ∏∏ÈáèÂØºÂÖ•ËØ≠Âè•...\n');

  const files = findFilesWithMagicNumbersImports();

  if (files.length === 0) {
    console.log('‚úÖ Ê≤°ÊúâÊâæÂà∞ÈúÄË¶Å‰øÆÂ§çÁöÑÊñá‰ª∂');
    return;
  }

  console.log(`üìÅ ÊâæÂà∞ ${files.length} ‰∏™ÈúÄË¶Å‰øÆÂ§çÁöÑÊñá‰ª∂:\n`);

  let fixedCount = 0;

  for (const file of files) {
    if (fs.existsSync(file)) {
      fixedCount += fixImportsInFile(file);
    }
  }

  console.log(`\nüéâ ‰øÆÂ§çÂÆåÊàêÔºÅÂÖ±‰øÆÂ§ç ${fixedCount} ‰∏™Êñá‰ª∂`);

  // È™åËØÅ‰øÆÂ§çÁªìÊûú
  console.log('\nüîç È™åËØÅ‰øÆÂ§çÁªìÊûú...');
  try {
    execSync('pnpm type-check', { stdio: 'inherit' });
    console.log('‚úÖ TypeScript ÁºñËØëÈÄöËøáÔºÅ');
  } catch (error) {
    console.log('‚ö†Ô∏è  ‰ªçÊúâ TypeScript ÈîôËØØÔºåÈúÄË¶ÅËøõ‰∏ÄÊ≠•Ê£ÄÊü•');
  }
}

if (require.main === module) {
  main();
}

