// ÈúÄË¶Å‰øÆÂ§çÁöÑÈîôËØØÊ®°Âºè
const DECIMAL_FIXES = [
  // Ê†ºÂºè: [ÈîôËØØÊ®°Âºè, Ê≠£Á°ÆÂÄº, Â∏∏ÈáèÂêç]
  ['0\\.MAGIC_85', '0.85', 'DECIMAL_85_PERCENT'],
