#!/usr/bin/env node

/**
 * ‰øÆÂ§çÂØºÂÖ•ÂÜ≤Á™ÅËÑöÊú¨
 * 
 * ÂäüËÉΩÔºö
 * 1. Ê£ÄÊµãÂØºÂÖ•Â£∞Êòé‰∏éÊú¨Âú∞Â£∞ÊòéÁöÑÂÜ≤Á™Å
 * 2. ÁßªÈô§ÂÜ≤Á™ÅÁöÑÂØºÂÖ•ËØ≠Âè•
 * 3. ‰øùÁïôÊú¨Âú∞ÂÆö‰πâÁöÑÂ∏∏Èáè
 * 
 * ‰ΩøÁî®ÊñπÊ≥ïÔºö
 * node scripts/fix-import-conflicts.js
 */

const fs = require('fs');
const path = require('path');

class ImportConflictFixer {
  constructor() {
    this.stats = {
      filesProcessed: 0,
      conflictsFixed: 0,
      errors: 0
    };
  }

  async run() {
    console.log('üîß ÂºÄÂßã‰øÆÂ§çÂØºÂÖ•ÂÜ≤Á™Å...\n');

    try {
      const files = this.getFilesToProcess();
      console.log(`üìÅ ÊâæÂà∞ ${files.length} ‰∏™Êñá‰ª∂ÈúÄË¶ÅÂ§ÑÁêÜ\n`);

      for (const file of files) {
        await this.processFile(file);
      }

      this.printStats();

    } catch (error) {
      console.error('‚ùå ÊâßË°åÂ§±Ë¥•:', error.message);
      process.exit(1);
    }
  }

  getFilesToProcess() {
    const files = [];

    const walkDir = (dir) => {
      const items = fs.readdirSync(dir);

      for (const item of items) {
        const fullPath = path.join(dir, item);
        const stat = fs.statSync(fullPath);

        if (stat.isDirectory()) {
          if (!item.startsWith('.') && item !== 'node_modules' && !item.includes('test')) {
            walkDir(fullPath);
          }
        } else if (stat.isFile()) {
          if ((item.endsWith('.ts') || item.endsWith('.tsx')) && 
              !item.endsWith('.d.ts') && 
              !item.includes('.test.')) {
            files.push(fullPath);
          }
        }
      }
    };

    walkDir('src');
    return files;
  }

  async processFile(filePath) {
    try {
      const content = fs.readFileSync(filePath, 'utf8');

      // Êü•ÊâæÂØºÂÖ•ÂÜ≤Á™Å
      const conflicts = this.findImportConflicts(content);

      if (conflicts.length === 0) {
        return;
      }

      this.stats.filesProcessed++;

      console.log(`üîç Â§ÑÁêÜÊñá‰ª∂: ${filePath}`);
      console.log(`   ÂèëÁé∞ ${conflicts.length} ‰∏™ÂØºÂÖ•ÂÜ≤Á™Å: ${conflicts.join(', ')}`);

      // ‰øÆÂ§çÂÜ≤Á™Å
      const fixedContent = this.fixConflicts(content, conflicts);

      // ÂÜôÂÖ•‰øÆÂ§çÂêéÁöÑÂÜÖÂÆπ
      fs.writeFileSync(filePath, fixedContent, 'utf8');

      this.stats.conflictsFixed += conflicts.length;
      console.log(`   ‚úÖ Â∑≤‰øÆÂ§ç\n`);

    } catch (error) {
      this.stats.errors++;
      console.error(`‚ùå Â§ÑÁêÜÊñá‰ª∂Â§±Ë¥• ${filePath}:`, error.message);
    }
  }

  findImportConflicts(content) {
    const conflicts = [];
    const lines = content.split('\n');

    // Ëé∑ÂèñÊâÄÊúâÂØºÂÖ•ÁöÑÊ†áËØÜÁ¨¶
    const importedIdentifiers = this.getImportedIdentifiers(content);

    // Ëé∑ÂèñÊâÄÊúâÊú¨Âú∞ÂÆö‰πâÁöÑÊ†áËØÜÁ¨¶
    const localIdentifiers = this.getLocalIdentifiers(content);

    // Êü•ÊâæÂÜ≤Á™Å
    for (const identifier of importedIdentifiers) {
      if (localIdentifiers.has(identifier)) {
        conflicts.push(identifier);
      }
    }

    return conflicts;
  }

  getImportedIdentifiers(content) {
    const identifiers = new Set();
    const importRegex = /import\s*{\s*([^}]+)\s*}\s*from\s*['"][^'"]+['"];?/g;

    let match;
    while ((match = importRegex.exec(content)) !== null) {
      const imports = match[1].split(',').map(imp => imp.trim());
      imports.forEach(imp => identifiers.add(imp));
    }

    return identifiers;
  }

  getLocalIdentifiers(content) {
    const identifiers = new Set();

    // ÂåπÈÖç export const Â£∞Êòé
    const exportConstRegex = /export\s+const\s+([A-Z_][A-Z0-9_]*)\s*=/g;
    let match;
    while ((match = exportConstRegex.exec(content)) !== null) {
      identifiers.add(match[1]);
    }

    // ÂåπÈÖç const Â£∞Êòé
    const constRegex = /^const\s+([A-Z_][A-Z0-9_]*)\s*=/gm;
    while ((match = constRegex.exec(content)) !== null) {
      identifiers.add(match[1]);
    }

    return identifiers;
  }

  fixConflicts(content, conflicts) {
    const lines = content.split('\n');
    const fixedLines = [];

    for (const line of lines) {
      // Ê£ÄÊü•ÊòØÂê¶ÊòØÂØºÂÖ•Ë°å
      const importMatch = line.match(/import\s*{\s*([^}]+)\s*}\s*from\s*['"]([^'"]+)['"];?/);

      if (importMatch) {
        const [fullMatch, importList, source] = importMatch;
        const imports = importList.split(',').map(imp => imp.trim());

        // ËøáÊª§ÊéâÂÜ≤Á™ÅÁöÑÂØºÂÖ•
        const filteredImports = imports.filter(imp => !conflicts.includes(imp));

        if (filteredImports.length === 0) {
          // Â¶ÇÊûúÊâÄÊúâÂØºÂÖ•ÈÉΩÂÜ≤Á™ÅÔºåÂà†Èô§Êï¥Ë°å
          continue;
        } else if (filteredImports.length < imports.length) {
          // Â¶ÇÊûúÈÉ®ÂàÜÂØºÂÖ•ÂÜ≤Á™ÅÔºåÈáçÂÜôÂØºÂÖ•Ë°å
          const newImportLine = `import { ${filteredImports.join(', ')} } from '${source}';`;
          fixedLines.push(newImportLine);
        } else {
          // Ê≤°ÊúâÂÜ≤Á™ÅÔºå‰øùÁïôÂéüË°å
          fixedLines.push(line);
        }
      } else {
        // ÈùûÂØºÂÖ•Ë°åÔºå‰øùÁïô
        fixedLines.push(line);
      }
    }

    return fixedLines.join('\n');
  }

  printStats() {
    console.log('\nüìä Â§ÑÁêÜÁªüËÆ°:');
    console.log(`   Â§ÑÁêÜÊñá‰ª∂Êï∞: ${this.stats.filesProcessed}`);
    console.log(`   ‰øÆÂ§çÂÜ≤Á™ÅÊï∞: ${this.stats.conflictsFixed}`);
    console.log(`   ÈîôËØØÊï∞: ${this.stats.errors}`);

    if (this.stats.errors === 0) {
      console.log('\n‚úÖ ÂØºÂÖ•ÂÜ≤Á™Å‰øÆÂ§çÂÆåÊàêÔºÅ');
    } else {
      console.log('\n‚ö†Ô∏è  ÈÉ®ÂàÜÊñá‰ª∂Â§ÑÁêÜÂ§±Ë¥•ÔºåËØ∑Ê£ÄÊü•ÈîôËØØ‰ø°ÊÅØ');
    }
  }
}

// ÊâßË°åËÑöÊú¨
if (require.main === module) {
  const fixer = new ImportConflictFixer();
  fixer.run().catch(console.error);
}

module.exports = ImportConflictFixer;

