/*
 * ‰øÆÂ§çimportËØ≠Âè•‰∏≠ÁöÑÈ≠îÊ≥ïÊï∞Â≠óÈîôËØØ
 * - Ê∏ÖÁêÜimportËØ≠Âè•‰∏≠‰∏çÂÜçÈúÄË¶ÅÁöÑÂ∏∏Èáè
 * - ‰øÆÂ§çÈîôËØØÁöÑimportËØ≠Ê≥ï
 */

const fs = require('fs');
const path = require('path');
const { execSync } = require('child_process');

function findFilesWithBadImports() {
  try {
    const result = execSync(
      'grep -r "import.*MAGIC_\\|import.*[0-9]\\.[0-9]" src/ --include="*.ts" --include="*.tsx" -l',
      {
        encoding: 'utf8',
        shell: true,
      },
    );
    return result
      .trim()
      .split('\n')
      .filter((file) => file.length > 0);
  } catch (error) {
    console.log('‚ö†Ô∏è  Ê≤°ÊúâÊâæÂà∞ÊúâÈóÆÈ¢òÁöÑimportËØ≠Âè•');
    return [];
  }
}

function fixImportStatements(filePath) {
  try {
    let content = fs.readFileSync(filePath, 'utf8');
    let modified = false;

    // Êü•ÊâæÊâÄÊúâimportËØ≠Âè•
    const importRegex =
      /import\s*\{([^}]+)\}\s*from\s*['"]@\/constants\/magic-numbers['"];/g;
    const matches = [...content.matchAll(importRegex)];

    for (const match of matches) {
      const fullImport = match[0];
      const importList = match[1];

      // Ê£ÄÊü•ÊòØÂê¶ÂåÖÂê´Êó†ÊïàÁöÑÂØºÂÖ•
      if (importList.includes('MAGIC_') || /\b\d+\.\d+\b/.test(importList)) {
        console.log(`üîç ÂèëÁé∞ÈóÆÈ¢òimport: ${fullImport.substring(0, 100)}...`);

        // ÂàÜÊûêÊñá‰ª∂‰∏≠ÂÆûÈôÖ‰ΩøÁî®ÁöÑÂ∏∏Èáè
        const usedConstants = new Set();

        // Êü•ÊâæÊñá‰ª∂‰∏≠‰ΩøÁî®ÁöÑÂ∏∏Èáè
        const constantPatterns = [
          /\b(ANIMATION_DURATION_\w+)\b/g,
          /\b(SIZE_\w+)\b/g,
          /\b(OPACITY_\w+)\b/g,
          /\b(COUNT_\w+)\b/g,
          /\b(PERCENTAGE_\w+)\b/g,
          /\b(OFFSET_NEGATIVE_\w+)\b/g,
          /\b(HEX_\w+)\b/g,
          /\b(DECIMAL_\w+)\b/g,
          /\b(BYTES_PER_\w+)\b/g,
          /\b(SECONDS_PER_\w+)\b/g,
          /\b(HOURS_PER_\w+)\b/g,
          /\b(DAYS_PER_\w+)\b/g,
        ];

        for (const pattern of constantPatterns) {
          const constantMatches = [...content.matchAll(pattern)];
          constantMatches.forEach((m) => usedConstants.add(m[1]));
        }

        // ÁîüÊàêÊñ∞ÁöÑimportËØ≠Âè•
        if (usedConstants.size > 0) {
          const newImportList = Array.from(usedConstants).sort().join(', ');
          const newImport = `import { ${newImportList} } from '@/constants/magic-numbers';`;
          content = content.replace(fullImport, newImport);
          console.log(`‚úÖ ‰øÆÂ§çimport: ${newImport.substring(0, 100)}...`);
        } else {
          // Â¶ÇÊûúÊ≤°Êúâ‰ΩøÁî®‰ªª‰ΩïÂ∏∏ÈáèÔºåÂà†Èô§importËØ≠Âè•
          content = content.replace(`${fullImport  }\n`, '');
          console.log(`üóëÔ∏è  Âà†Èô§Êú™‰ΩøÁî®ÁöÑimportËØ≠Âè•`);
        }

        modified = true;
      }
    }

    if (modified) {
      fs.writeFileSync(filePath, content, 'utf8');
      console.log(`‚úÖ ‰øÆÂ§çÊñá‰ª∂: ${filePath}`);
      return 1;
    }

    return 0;
  } catch (error) {
    console.error(`‚ùå Â§ÑÁêÜÊñá‰ª∂Â§±Ë¥• ${filePath}:`, error.message);
    return 0;
  }
}

function main() {
  console.log('üîß ‰øÆÂ§çimportËØ≠Âè•‰∏≠ÁöÑÈ≠îÊ≥ïÊï∞Â≠óÈîôËØØ...');

  const affectedFiles = findFilesWithBadImports();
  if (affectedFiles.length === 0) {
    console.log('‚úÖ Ê≤°ÊúâÂèëÁé∞ÈúÄË¶Å‰øÆÂ§çÁöÑimportËØ≠Âè•');
    return;
  }

  console.log(`üìä ÂèëÁé∞ ${affectedFiles.length} ‰∏™ÂèóÂΩ±ÂìçÁöÑÊñá‰ª∂`);

  let totalFixes = 0;
  for (const filePath of affectedFiles) {
    totalFixes += fixImportStatements(filePath);
  }

  console.log(`\nüéâ ‰øÆÂ§çÂÆåÊàêÔºÅ`);
  console.log(`üìä ÊÄªËÆ°‰øÆÂ§ç ${totalFixes} ‰∏™Êñá‰ª∂ÁöÑimportËØ≠Âè•`);

  if (totalFixes > 0) {
    console.log('\nüí° Âª∫ËÆÆËøêË°å‰ª•‰∏ãÂëΩ‰ª§È™åËØÅ‰øÆÂ§çÊïàÊûúÔºö');
    console.log('pnpm type-check');
  }
}

if (require.main === module) {
  main();
}

