    // 1. ‰øÆÂ§çÈîôËØØÁöÑimport typeÔºàÂ∫îËØ•ÊòØÂÄºÂØºÂÖ•Ôºâ
    VALUE_IMPORTS.forEach((importName) => {
      // ÂåπÈÖç import type { ImportName } from 'module' Ê®°Âºè
      const typeImportRegex = new RegExp(
        `import\\s+type\\s*{([^}]*\\b${importName}\\b[^}]*)}\\s*from\\s*(['"][^'"]+['"])`,
        'g',
      );

      newContent = newContent.replace(
        typeImportRegex,
        (match, imports, module) => {
          const importList = imports.split(',').map((imp) => imp.trim());
          const valueImports = [];
          const typeImports = [];

          importList.forEach((imp) => {
            if (VALUE_IMPORTS.some((val) => imp.includes(val))) {
              valueImports.push(imp);
            } else {
              typeImports.push(imp);
            }
          });

          let result = '';

          // Ê∑ªÂä†type importÔºàÂ¶ÇÊûúÊúâÂâ©‰ΩôÁöÑÁ±ªÂûãÔºâ
          if (typeImports.length > 0) {
            result += `import type { ${typeImports.join(', ')} } from ${module};\n`;
          }

          // Ê∑ªÂä†value import
          if (valueImports.length > 0) {
            result += `import { ${valueImports.join(', ')} } from ${module};`;
          }

          if (result !== match) {
            modified = true;
            console.log(
              `  ‰øÆÂ§ç ${filePath}: ${importName} ‰ªé type import Êîπ‰∏∫ value import`,
            );
          }

          return result || match;
        },
      );
    });

    // 2. ÁßªÈô§Â§ö‰ΩôÁöÑÂàÜÂè∑
    newContent = newContent.replace(/import[^;]*;\s*;/g, (match) => {
      return match.replace(/;\s*;/, ';');
    });

    // 3. ‰øÆÂ§çÁº∫Â∞ëÁöÑimport typeÔºàÂ∫îËØ•ÊòØÁ±ªÂûãÂØºÂÖ•Ôºâ
    TYPE_ONLY_IMPORTS.forEach((importName) => {
      // ÂåπÈÖç import { ImportName } from 'module' Ê®°ÂºèÔºàÈùûtype importÔºâ
      const valueImportRegex = new RegExp(
        `import\\s*{([^}]*\\b${importName}\\b[^}]*)}\\s*from\\s*(['"][^'"]+['"])`,
        'g',
      );

      newContent = newContent.replace(
        valueImportRegex,
        (match, imports, module) => {
          // Ë∑≥ËøáÂ∑≤ÁªèÊòØtype importÁöÑÊÉÖÂÜµ
          if (match.includes('import type')) {
            return match;
          }

          const importList = imports.split(',').map((imp) => imp.trim());
          const valueImports = [];
          const typeImports = [];

          importList.forEach((imp) => {
            if (TYPE_ONLY_IMPORTS.some((type) => imp.includes(type))) {
              typeImports.push(imp);
            } else {
              valueImports.push(imp);
            }
          });

          let result = '';

          // Ê∑ªÂä†type import
          if (typeImports.length > 0) {
            result += `import type { ${typeImports.join(', ')} } from ${module};\n`;
          }

          // Ê∑ªÂä†value importÔºàÂ¶ÇÊûúÊúâÔºâ
          if (valueImports.length > 0) {
            result += `import { ${valueImports.join(', ')} } from ${module};`;
          }

          if (result !== match && typeImports.length > 0) {
            modified = true;
            console.log(
              `  ‰øÆÂ§ç ${filePath}: ${importName} ‰ªé value import Êîπ‰∏∫ type import`,
            );
          }

          return result || match;
        },
      );
    });

    if (modified) {
      fs.writeFileSync(filePath, newContent, 'utf8');
      return true;
    }

    return false;
  } catch (error) {
    console.error(`Â§ÑÁêÜÊñá‰ª∂ ${filePath} Êó∂Âá∫Èîô:`, error.message);
    return false;
  }
}

/**
 * ÈÄíÂΩíÂ§ÑÁêÜÁõÆÂΩï‰∏≠ÁöÑÊâÄÊúâTypeScriptÊñá‰ª∂
 */
function fixImportTypeInDirectory(dirPath) {
  let fixedCount = 0;

  function processDirectory(currentPath) {
    const items = fs.readdirSync(currentPath);

    for (const item of items) {
      const fullPath = path.join(currentPath, item);
      const stat = fs.statSync(fullPath);

      if (stat.isDirectory()) {
        // Ë∑≥Ëøánode_modulesÂíåÂÖ∂‰ªñ‰∏çÈúÄË¶ÅÂ§ÑÁêÜÁöÑÁõÆÂΩï
        if (
          !['node_modules', '.next', '.git', 'dist', 'build'].includes(item)
        ) {
          processDirectory(fullPath);
        }
      } else if (stat.isFile() && /\.(ts|tsx)$/.test(item)) {
        if (fixImportTypeInFile(fullPath)) {
          fixedCount++;
        }
      }
    }
  }

  if (fs.existsSync(dirPath)) {
    processDirectory(dirPath);
  }

  return fixedCount;
}

