const fs = require('fs');
const path = require('path');
const { execSync } = require('child_process');

// ÈúÄË¶ÅËΩ¨Êç¢‰∏∫import typeÁöÑÁ±ªÂûãÂØºÂÖ•Ê®°Âºè
const TYPE_IMPORTS = [
  'Metadata',
  'ImageProps',
  'TestResults',
  'NextRequest',
  'NextResponse',
  // Ê∑ªÂä†Êõ¥Â§öÈúÄË¶Åtype-onlyÂØºÂÖ•ÁöÑÁ±ªÂûã
];

/**
 * ‰øÆÂ§çÂçï‰∏™Êñá‰ª∂ÁöÑimport typeÈóÆÈ¢ò
 */
function fixImportTypeInFile(filePath) {
  try {
    const content = fs.readFileSync(filePath, 'utf8');
    let modified = false;
    let newContent = content;

    // ‰øÆÂ§çÊØè‰∏™Á±ªÂûãÂØºÂÖ•
    TYPE_IMPORTS.forEach((typeName) => {
      // ÂåπÈÖç import { TypeName } from 'module' Ê®°Âºè
      const importRegex = new RegExp(
        `import\\s*{([^}]*\\b${typeName}\\b[^}]*)}\\s*from\\s*(['"][^'"]+['"])`,
        'g',
      );

      newContent = newContent.replace(importRegex, (match, imports, module) => {
        // Ê£ÄÊü•ÊòØÂê¶Â∑≤ÁªèÊòØtype import
        if (match.includes('import type')) {
          return match;
        }

        // ÂàÜÁ¶ªÁ±ªÂûãÂØºÂÖ•ÂíåÂÄºÂØºÂÖ•
        const importList = imports.split(',').map((imp) => imp.trim());
        const typeImports = [];
        const valueImports = [];

        importList.forEach((imp) => {
          if (TYPE_IMPORTS.some((type) => imp.includes(type))) {
            typeImports.push(imp);
          } else {
            valueImports.push(imp);
          }
        });

        let result = '';

        // Ê∑ªÂä†type import
        if (typeImports.length > 0) {
          result += `import type { ${typeImports.join(', ')} } from ${module};\n`;
          modified = true;
        }

        // Ê∑ªÂä†value importÔºàÂ¶ÇÊûúÊúâÔºâ
        if (valueImports.length > 0) {
          result += `import { ${valueImports.join(', ')} } from ${module};`;
        }

        return result || match;
      });
    });

    // Â¶ÇÊûúÊñá‰ª∂Ë¢´‰øÆÊîπÔºåÂÜôÂõûÊñá‰ª∂
    if (modified) {
      fs.writeFileSync(filePath, newContent);
      console.log(`‚úÖ ‰øÆÂ§ç: ${filePath}`);
      return true;
    }

    return false;
  } catch (error) {
    console.error(`‚ùå ‰øÆÂ§çÂ§±Ë¥• ${filePath}:`, error.message);
    return false;
  }
}

/**
 * ÈÄíÂΩíÊâ´ÊèèÁõÆÂΩïÂπ∂‰øÆÂ§çÊñá‰ª∂
 */
function fixImportTypeInDirectory(dirPath) {
  const entries = fs.readdirSync(dirPath, { withFileTypes: true });
  let fixedCount = 0;

  entries.forEach((entry) => {
    const fullPath = path.join(dirPath, entry.name);

    if (entry.isDirectory()) {
      // Ë∑≥Ëøánode_modulesÂíå.nextÁ≠âÁõÆÂΩï
      if (!['node_modules', '.next', 'dist', 'build'].includes(entry.name)) {
        fixedCount += fixImportTypeInDirectory(fullPath);
      }
    } else if (entry.isFile() && /\.(ts|tsx)$/.test(entry.name)) {
      if (fixImportTypeInFile(fullPath)) {
        fixedCount++;
      }
    }
  });

  return fixedCount;
}

/**
