  const appFixedCount = fixImportTypeInDirectory('./src/app');

  // ‰øÆÂ§çÊ†πÁõÆÂΩïÁöÑÁâπÂÆöÊñá‰ª∂
  const rootFiles = ['mdx-components.tsx'];
