#!/usr/bin/env tsx

/**
 * ÁÆÄÂåñÁâàÁõ∏ÂØπË∑ØÂæÑÂØºÂÖ•‰øÆÂ§çËÑöÊú¨
 * ‰ΩøÁî®Êõ¥Áõ¥Êé•ÁöÑÂ≠óÁ¨¶‰∏≤ÊõøÊç¢ÊñπÊ≥ï
 */

import { readFileSync, writeFileSync } from 'fs';
import { glob } from 'glob';
import path from 'path';

interface FixResult {
  file: string;
  changes: number;
  errors: string[];
}

/**
 * ‰øÆÂ§çÂçï‰∏™Êñá‰ª∂‰∏≠ÁöÑÁõ∏ÂØπË∑ØÂæÑÂØºÂÖ•
 */
function fixFileImports(filePath: string): FixResult {
  const result: FixResult = {
    file: filePath,
    changes: 0,
    errors: [],
  };

  try {
    const content = readFileSync(filePath, 'utf-8');
    let newContent = content;

    // Ëé∑ÂèñÊñá‰ª∂Áõ∏ÂØπ‰∫ésrcÁõÆÂΩïÁöÑË∑ØÂæÑ‰ø°ÊÅØ
    const relativePath = path.relative('src', filePath);
    const currentDir = path.dirname(relativePath);

    // ËÆ°ÁÆóÈúÄË¶ÅÂõûÈÄÄÁöÑÂ±ÇÁ∫ßÊï∞
    const levels = currentDir === '.' ? 0 : currentDir.split('/').length;

    // ÊõøÊç¢ ../xxx Ê†ºÂºèÁöÑÂØºÂÖ•
    const parentImportRegex = /from\s+['"](\.\.\/)([^'"]+)['"]/g;
    newContent = newContent.replace(parentImportRegex, (match, prefix, importPath) => {
      result.changes++;
      // ÁÆÄÂçïÂ§ÑÁêÜÔºöÁõ¥Êé•ÊõøÊç¢‰∏∫@/Ë∑ØÂæÑ
      // ÂØπ‰∫é ../xxxÔºåÊàë‰ª¨ÈúÄË¶ÅÊ†πÊçÆÂΩìÂâçÊñá‰ª∂‰ΩçÁΩÆËÆ°ÁÆóÊ≠£Á°ÆÁöÑ@/Ë∑ØÂæÑ
      if (levels === 1) {
        // ÂΩìÂâçÂú®srcÁöÑÁõ¥Êé•Â≠êÁõÆÂΩï‰∏≠Ôºå../xxx Â∫îËØ•ÊòØ @/xxx
        return `from '@/${importPath}'`;
      } else if (levels === 2) {
        // ÂΩìÂâçÂú®srcÁöÑ‰∫åÁ∫ßÂ≠êÁõÆÂΩï‰∏≠Ôºå../xxx ÂèØËÉΩÊåáÂêë‰∏ä‰∏ÄÁ∫ß
        const parentDir = path.dirname(currentDir);
        return `from '@/${parentDir}/${importPath}'`;
      } 
        // Â§çÊùÇÊÉÖÂÜµÔºåÂ∞ùËØïÊô∫ËÉΩÊé®Êñ≠
        return `from '@/${importPath}'`;

    });

    // ÊõøÊç¢ ./xxx Ê†ºÂºèÁöÑÂØºÂÖ•
    const currentImportRegex = /from\s+['"](\.\/)([^'"]+)['"]/g;
    newContent = newContent.replace(currentImportRegex, (match, prefix, importPath) => {
      result.changes++;
      // ./xxx Â∫îËØ•ÊòØ @/currentDir/xxx
      if (currentDir === '.') {
        return `from '@/${importPath}'`;
      } 
        return `from '@/${currentDir}/${importPath}'`;

    });

    // Â§ÑÁêÜÂä®ÊÄÅÂØºÂÖ• import('./xxx') Âíå import('../xxx')
    const dynamicImportRegex = /import\s*\(\s*['"](\.\.\?\/[^'"]+)['"]\s*\)/g;
    newContent = newContent.replace(dynamicImportRegex, (match, importPath) => {
      result.changes++;
      if (importPath.startsWith('../')) {
        const cleanPath = importPath.substring(3); // ÁßªÈô§ ../
        if (levels === 1) {
          return `import('@/${cleanPath}')`;
        } else if (levels === 2) {
          const parentDir = path.dirname(currentDir);
          return `import('@/${parentDir}/${cleanPath}')`;
        } 
          return `import('@/${cleanPath}')`;

      } else if (importPath.startsWith('./')) {
        const cleanPath = importPath.substring(2); // ÁßªÈô§ ./
        if (currentDir === '.') {
          return `import('@/${cleanPath}')`;
        } 
          return `import('@/${currentDir}/${cleanPath}')`;

      }
      return match;
    });

    // Â¶ÇÊûúÊúâÂèòÊõ¥ÔºåÂÜôÂõûÊñá‰ª∂
    if (result.changes > 0) {
      writeFileSync(filePath, newContent, 'utf-8');
    }

  } catch (error) {
    result.errors.push(`Â§ÑÁêÜÊñá‰ª∂Êó∂Âá∫Èîô: ${error}`);
  }

  return result;
}

/**
 * ‰∏ªÂáΩÊï∞
 */
async function main() {
  console.log('üîß ÂºÄÂßãÁÆÄÂåñÁâàÁõ∏ÂØπË∑ØÂæÑÂØºÂÖ•‰øÆÂ§ç...');

  // Êü•ÊâæÊâÄÊúâÈúÄË¶ÅÂ§ÑÁêÜÁöÑÊñá‰ª∂ÔºàÊéíÈô§ÊµãËØïÊñá‰ª∂Ôºâ
  const files = await glob('src/**/*.{ts,tsx}', {
    ignore: [
      'src/**/*.test.{ts,tsx}',
      'src/**/__tests__/**',
      'src/test/**',
      'src/testing/**',
    ],
  });

  console.log(`üìÅ ÊâæÂà∞ ${files.length} ‰∏™Êñá‰ª∂ÈúÄË¶ÅÂ§ÑÁêÜ`);

  let totalChanges = 0;
  let processedFiles = 0;
  const errors: string[] = [];

  // Â§ÑÁêÜÊØè‰∏™Êñá‰ª∂
  for (const file of files) {
    const result = fixFileImports(file);

    if (result.changes > 0) {
      console.log(`‚úÖ ${file}: ${result.changes} ‰∏™ÂØºÂÖ•Â∑≤‰øÆÂ§ç`);
      processedFiles++;
      totalChanges += result.changes;
    }

    if (result.errors.length > 0) {
      errors.push(...result.errors);
    }
  }

  // ËæìÂá∫ÁªüËÆ°‰ø°ÊÅØ
  console.log('\nüìä ‰øÆÂ§çÁªüËÆ°:');
  console.log(`- Êâ´ÊèèÊñá‰ª∂Êï∞: ${files.length}`);
  console.log(`- ‰øÆÊîπÊñá‰ª∂Êï∞: ${processedFiles}`);
  console.log(`- ‰øÆÂ§çÂØºÂÖ•Êï∞: ${totalChanges}`);

  if (errors.length > 0) {
    console.log('\n‚ùå ÈîôËØØ‰ø°ÊÅØ:');
    errors.forEach((error) => console.log(`  ${error}`));
  }

  console.log('\n‚ú® Áõ∏ÂØπË∑ØÂæÑÂØºÂÖ•‰øÆÂ§çÂÆåÊàêÔºÅ');
  console.log('üí° Âª∫ËÆÆËøêË°å pnpm lint:check È™åËØÅ‰øÆÂ§çÊïàÊûú');
}

// ÊâßË°å‰∏ªÂáΩÊï∞
main().catch((error) => {
  console.error('‚ùå ËÑöÊú¨ÊâßË°åÂ§±Ë¥•:', error);
  process.exit(1);
});

