  'THREE_SECONDS_MS': '@/constants',
  'MINUTE_MS': '@/constants',

  // È≠îÊ≥ïÊï∞Â≠ó
  'MAGIC_0_1': '@/constants',
  'MAGIC_0_25': '@/constants',
  'MAGIC_0_8': '@/constants',
  'MAGIC_0_95': '@/constants',
  'MAGIC_0_99': '@/constants',
  'MAGIC_6': '@/constants',
  'MAGIC_8': '@/constants',
  'MAGIC_9': '@/constants',
  'MAGIC_20': '@/constants',
  'MAGIC_36': '@/constants',
  'MAGIC_72': '@/constants',
  'MAGIC_75': '@/constants',
  'MAGIC_90': '@/constants',
  'MAGIC_95': '@/constants',
  'MAGIC_99': '@/constants',
  'MAGIC_256': '@/constants',
  'MAGIC_300000': '@/constants',
  'MAGIC_429': '@/constants',
  'MAGIC_600': '@/constants',
  'MAGIC_2000': '@/constants',
  'MAGIC_2500': '@/constants',
  'MAGIC_4000': '@/constants',
  'MAGIC_4096': '@/constants',
  'MAGIC_10000': '@/constants',

  // ÊµãËØïÁõ∏ÂÖ≥
  'TEST_BASE_NUMBERS': '@/constants'
};

class MissingConstantsFixer {
  constructor() {
    this.stats = {
      filesProcessed: 0,
      constantsAdded: 0,
      errors: 0
    };
  }

  async run() {
    console.log('üîß ÂºÄÂßã‰øÆÂ§çÁº∫Â§±Â∏∏ÈáèÂØºÂÖ•...\n');

    try {
      const files = this.getFilesToProcess();
      console.log(`üìÅ ÊâæÂà∞ ${files.length} ‰∏™Êñá‰ª∂ÈúÄË¶ÅÂ§ÑÁêÜ\n`);

      for (const file of files) {
        await this.processFile(file);
      }

      this.printStats();

    } catch (error) {
      console.error('‚ùå ÊâßË°åÂ§±Ë¥•:', error.message);
      process.exit(1);
    }
  }

  getFilesToProcess() {
    const files = [];

    const walkDir = (dir) => {
      const items = fs.readdirSync(dir);

      for (const item of items) {
        const fullPath = path.join(dir, item);
        const stat = fs.statSync(fullPath);

        if (stat.isDirectory()) {
          if (!item.startsWith('.') && item !== 'node_modules' && !item.includes('test')) {
            walkDir(fullPath);
          }
        } else if (stat.isFile()) {
          if ((item.endsWith('.ts') || item.endsWith('.tsx')) && 
              !item.endsWith('.d.ts') && 
              !item.includes('.test.')) {
            files.push(fullPath);
          }
        }
      }
    };

    walkDir('src');
    return files;
  }

  async processFile(filePath) {
    try {
      const content = fs.readFileSync(filePath, 'utf8');

      // Êü•ÊâæÁº∫Â§±ÁöÑÂ∏∏Èáè
      const missingConstants = this.findMissingConstants(content);

      if (missingConstants.length === 0) {
        return;
      }

      this.stats.filesProcessed++;

      console.log(`üîç Â§ÑÁêÜÊñá‰ª∂: ${filePath}`);
      console.log(`   ÂèëÁé∞ ${missingConstants.length} ‰∏™Áº∫Â§±Â∏∏Èáè: ${missingConstants.join(', ')}`);

      // Ê∑ªÂä†Áº∫Â§±ÁöÑÂØºÂÖ•
      const fixedContent = this.addMissingImports(content, missingConstants);

      // ÂÜôÂÖ•‰øÆÂ§çÂêéÁöÑÂÜÖÂÆπ
      fs.writeFileSync(filePath, fixedContent, 'utf8');

      this.stats.constantsAdded += missingConstants.length;
      console.log(`   ‚úÖ Â∑≤‰øÆÂ§ç\n`);

    } catch (error) {
      this.stats.errors++;
      console.error(`‚ùå Â§ÑÁêÜÊñá‰ª∂Â§±Ë¥• ${filePath}:`, error.message);
    }
  }

  findMissingConstants(content) {
    const missingConstants = [];

    // Ëé∑ÂèñÂ∑≤ÂØºÂÖ•ÁöÑÂ∏∏Èáè
    const importedConstants = this.getImportedConstants(content);

    // Êü•Êâæ‰ΩøÁî®‰ΩÜÊú™ÂØºÂÖ•ÁöÑÂ∏∏Èáè
    for (const [constant, source] of Object.entries(CONSTANT_MAPPINGS)) {
      if (!importedConstants.has(constant)) {
        // Ê£ÄÊü•ÊòØÂê¶Âú®‰ª£Á†Å‰∏≠‰ΩøÁî®‰∫ÜËøô‰∏™Â∏∏Èáè
        const regex = new RegExp(`\\b${constant}\\b`);
        if (regex.test(content)) {
          missingConstants.push(constant);
        }
      }
    }

    return missingConstants;
  }

  getImportedConstants(content) {
    const importedConstants = new Set();
    const importRegex = /import\s*{\s*([^}]+)\s*}\s*from\s*['"][^'"]+['"];?/g;

    let match;
    while ((match = importRegex.exec(content)) !== null) {
      const imports = match[1].split(',').map(imp => imp.trim());
      imports.forEach(imp => importedConstants.add(imp));
    }

    return importedConstants;
  }

  addMissingImports(content, missingConstants) {
    const lines = content.split('\n');

    // Êü•ÊâæÊúÄÂêé‰∏Ä‰∏™@/constantsÂØºÂÖ•Ë°å
    let lastImportIndex = -1;
    let existingConstantsImport = null;

    for (let i = 0; i < lines.length; i++) {
      const line = lines[i];
      if (line.includes("from '@/constants'")) {
        lastImportIndex = i;
        const match = line.match(/import\s*{\s*([^}]+)\s*}\s*from\s*'@\/constants'/);
        if (match) {
          existingConstantsImport = {
            index: i,
            imports: match[1].split(',').map(imp => imp.trim())
          };
        }
      }
    }

    if (existingConstantsImport) {
      // ÂêàÂπ∂Âà∞Áé∞ÊúâÁöÑÂØºÂÖ•‰∏≠
      const allImports = [...existingConstantsImport.imports, ...missingConstants];
      const uniqueImports = [...new Set(allImports)].sort();
      const newImportLine = `import { ${uniqueImports.join(', ')} } from '@/constants';`;
      lines[existingConstantsImport.index] = newImportLine;
    } else {
      // ÂàõÂª∫Êñ∞ÁöÑÂØºÂÖ•Ë°å
      const newImportLine = `import { ${missingConstants.join(', ')} } from '@/constants';`;

      if (lastImportIndex >= 0) {
        // Âú®ÊúÄÂêé‰∏Ä‰∏™ÂØºÂÖ•ÂêéÊ∑ªÂä†
        lines.splice(lastImportIndex + 1, 0, newImportLine);
      } else {
        // Âú®Êñá‰ª∂ÂºÄÂ§¥Ê∑ªÂä†ÔºàÂú®'use client'ÊàñÂÖ∂‰ªñÊåá‰ª§ÂêéÔºâ
        let insertIndex = 0;
        for (let i = 0; i < lines.length; i++) {
          if (lines[i].startsWith("'use") || lines[i].startsWith('"use')) {
            insertIndex = i + 1;
            break;
          }
        }
        lines.splice(insertIndex, 0, '', newImportLine);
      }
    }

    return lines.join('\n');
  }

  printStats() {
    console.log('\nüìä Â§ÑÁêÜÁªüËÆ°:');
    console.log(`   Â§ÑÁêÜÊñá‰ª∂Êï∞: ${this.stats.filesProcessed}`);
    console.log(`   Ê∑ªÂä†Â∏∏ÈáèÊï∞: ${this.stats.constantsAdded}`);
    console.log(`   ÈîôËØØÊï∞: ${this.stats.errors}`);

    if (this.stats.errors === 0) {
      console.log('\n‚úÖ Áº∫Â§±Â∏∏Èáè‰øÆÂ§çÂÆåÊàêÔºÅ');
    } else {
      console.log('\n‚ö†Ô∏è  ÈÉ®ÂàÜÊñá‰ª∂Â§ÑÁêÜÂ§±Ë¥•ÔºåËØ∑Ê£ÄÊü•ÈîôËØØ‰ø°ÊÅØ');
    }
  }
}

// ÊâßË°åËÑöÊú¨
if (require.main === module) {
  const fixer = new MissingConstantsFixer();
  fixer.run().catch(console.error);
}

module.exports = MissingConstantsFixer;

