    missingConstants.sort((a, b) => {
      const numA = parseFloat(a.value);
      const numB = parseFloat(b.value);
      return numA - numB;
    });

    // ÁîüÊàêÂ∏∏ÈáèÂÆö‰πâ
    const newConstants = `
// Ëá™Âä®ÁîüÊàêÁöÑÁº∫Â§±Â∏∏Èáè
${missingConstants
  .map(({ name, value }) => {
    const numValue = parseFloat(value);
    if (isNaN(numValue)) {
