#!/usr/bin/env tsx

/**
 * ÊâπÈáè‰øÆÂ§çÁõ∏ÂØπË∑ØÂæÑÂØºÂÖ•ËÑöÊú¨
 * Â∞ÜÊâÄÊúâ ../xxx Âíå ./xxx ÂØºÂÖ•ÊõøÊç¢‰∏∫ @/xxx ÁªùÂØπË∑ØÂæÑÂØºÂÖ•
 */

import { readFileSync, writeFileSync } from 'fs';
import { glob } from 'glob';
import path from 'path';

interface ImportReplacement {
  file: string;
  line: number;
  original: string;
  replacement: string;
}

/**
 * Â∞ÜÁõ∏ÂØπË∑ØÂæÑËΩ¨Êç¢‰∏∫@/ÁªùÂØπË∑ØÂæÑ
 */
function convertRelativeToAbsolute(relativePath: string, currentFilePath: string): string {
  // Ëé∑ÂèñÂΩìÂâçÊñá‰ª∂Áõ∏ÂØπ‰∫éÈ°πÁõÆÊ†πÁõÆÂΩïÁöÑÁõÆÂΩïË∑ØÂæÑ
  const currentDir = path.dirname(currentFilePath);

  // Ëß£ÊûêÁõ∏ÂØπË∑ØÂæÑÔºåÂæóÂà∞ÁªùÂØπË∑ØÂæÑ
  const absolutePath = path.resolve(currentDir, relativePath);

  // Â∞ÜÁªùÂØπË∑ØÂæÑËΩ¨Êç¢‰∏∫Áõ∏ÂØπ‰∫ésrcÁõÆÂΩïÁöÑË∑ØÂæÑ
  const srcRelativePath = path.relative('src', absolutePath);

  // ËøîÂõû@/Ê†ºÂºèÁöÑË∑ØÂæÑÔºåÁ°Æ‰øù‰ΩøÁî®Ê≠£ÊñúÊù†
  return `@/${srcRelativePath.replace(/\\/g, '/')}`;
}

/**
 * Â§ÑÁêÜÂçï‰∏™Êñá‰ª∂ÁöÑÂØºÂÖ•ÊõøÊç¢
 */
function processFile(filePath: string): ImportReplacement[] {
  const content = readFileSync(filePath, 'utf-8');
  const lines = content.split('\n');
  const replacements: ImportReplacement[] = [];

  let hasChanges = false;
  const newLines = lines.map((line, index) => {
    // ÂåπÈÖçÂêÑÁßçÂØºÂÖ•ËØ≠Âè•ÁöÑÊ≠£ÂàôË°®ËææÂºè
    const importPatterns = [
      // import ... from './xxx' Êàñ '../xxx'
      /^(\s*import\s+.*?\s+from\s+['"])(\.\.?\/[^'"]+)(['"])/,
      // import('./xxx') Êàñ import('../xxx') Âä®ÊÄÅÂØºÂÖ•
      /^(\s*.*?import\s*\(\s*['"])(\.\.?\/[^'"]+)(['"])/,
      // require('./xxx') Êàñ require('../xxx')
      /^(\s*.*?require\s*\(\s*['"])(\.\.?\/[^'"]+)(['"])/,
    ];

    for (const pattern of importPatterns) {
      const match = line.match(pattern);
      if (match) {
        const [, prefix, relativePath, suffix] = match;

        // Ë∑≥ËøáÂ∑≤ÁªèÊòØ@/Ê†ºÂºèÁöÑÂØºÂÖ•
        if (relativePath.startsWith('@/')) {
          continue;
        }

        // ËΩ¨Êç¢‰∏∫ÁªùÂØπË∑ØÂæÑ
        const absolutePath = convertRelativeToAbsolute(relativePath, filePath);
        const newLine = `${prefix}${absolutePath}${suffix}`;

        replacements.push({
          file: filePath,
          line: index + 1,
          original: line.trim(),
          replacement: newLine.trim(),
        });

        hasChanges = true;
        return newLine;
      }
    }

    return line;
  });

  // Â¶ÇÊûúÊúâÂèòÊõ¥ÔºåÂÜôÂõûÊñá‰ª∂
  if (hasChanges) {
    writeFileSync(filePath, newLines.join('\n'), 'utf-8');
  }

  return replacements;
}

/**
 * ‰∏ªÂáΩÊï∞
 */
async function main() {
  console.log('üîß ÂºÄÂßãÊâπÈáè‰øÆÂ§çÁõ∏ÂØπË∑ØÂæÑÂØºÂÖ•...');

  // Êü•ÊâæÊâÄÊúâÈúÄË¶ÅÂ§ÑÁêÜÁöÑÊñá‰ª∂
  const files = await glob('src/**/*.{ts,tsx,js,jsx}', {
    ignore: [
      'src/**/*.test.{ts,tsx,js,jsx}',
      'src/**/__tests__/**',
      'src/test/**',
      'src/testing/**',
    ],
  });

  console.log(`üìÅ ÊâæÂà∞ ${files.length} ‰∏™Êñá‰ª∂ÈúÄË¶ÅÂ§ÑÁêÜ`);

  let totalReplacements = 0;
  const allReplacements: ImportReplacement[] = [];

  // Â§ÑÁêÜÊØè‰∏™Êñá‰ª∂
  for (const file of files) {
    try {
      const replacements = processFile(file);
      if (replacements.length > 0) {
        console.log(`‚úÖ ${file}: ${replacements.length} ‰∏™ÂØºÂÖ•Â∑≤‰øÆÂ§ç`);
        allReplacements.push(...replacements);
        totalReplacements += replacements.length;
      }
    } catch (error) {
      console.error(`‚ùå Â§ÑÁêÜÊñá‰ª∂ ${file} Êó∂Âá∫Èîô:`, error);
    }
  }

  // ËæìÂá∫ÁªüËÆ°‰ø°ÊÅØ
  console.log('\nüìä ‰øÆÂ§çÁªüËÆ°:');
  console.log(`- Â§ÑÁêÜÊñá‰ª∂Êï∞: ${files.length}`);
  console.log(`- ‰øÆÂ§çÂØºÂÖ•Êï∞: ${totalReplacements}`);

  if (allReplacements.length > 0) {
    console.log('\nüìù ‰øÆÂ§çËØ¶ÊÉÖ:');
    allReplacements.slice(0, 10).forEach((replacement) => {
      console.log(`  ${replacement.file}:${replacement.line}`);
      console.log(`    - ${replacement.original}`);
      console.log(`    + ${replacement.replacement}`);
    });

    if (allReplacements.length > 10) {
      console.log(`  ... ËøòÊúâ ${allReplacements.length - 10} ‰∏™‰øÆÂ§çÈ°π`);
    }
  }

  console.log('\n‚ú® Áõ∏ÂØπË∑ØÂæÑÂØºÂÖ•‰øÆÂ§çÂÆåÊàêÔºÅ');
  console.log('üí° Âª∫ËÆÆËøêË°å pnpm lint:check È™åËØÅ‰øÆÂ§çÊïàÊûú');
}

// ÊâßË°å‰∏ªÂáΩÊï∞
main().catch((error) => {
  console.error('‚ùå ËÑöÊú¨ÊâßË°åÂ§±Ë¥•:', error);
  process.exit(1);
});

#!/usr/bin/env node

const fs = require('fs');
const path = require('path');
const { execSync } = require('child_process');

console.log('üöÄ ÂºÄÂßã‰øÆÂ§çÂâ©‰ΩôÁöÑimport typeÈóÆÈ¢ò...');

// Ëé∑ÂèñÊâÄÊúâÊúâimport typeÈîôËØØÁöÑÊñá‰ª∂
function getFilesWithImportTypeErrors() {
  try {
    const output = execSync(
      'pnpm type-check 2>&1 | grep -E "(TS1484|TS1361)" | grep -o "src/[^(]*" | sort | uniq',
      { encoding: 'utf8' },
    );
    return output
      .trim()
      .split('\n')
      .filter((file) => file.trim());
  } catch (error) {
    console.log('Ê≤°ÊúâÊâæÂà∞import typeÈîôËØØ');
    return [];
  }
}

// ‰øÆÂ§çÂçï‰∏™Êñá‰ª∂ÁöÑimport typeÈóÆÈ¢ò
function fixImportTypeInFile(filePath) {
  if (!fs.existsSync(filePath)) {
    console.log(`‚ö†Ô∏è Êñá‰ª∂‰∏çÂ≠òÂú®: ${filePath}`);
    return false;
  }

  let content = fs.readFileSync(filePath, 'utf8');
  let modified = false;

  // Â∏∏ËßÅÁöÑÈúÄË¶Å‰øÆÂ§çÁöÑÊ®°Âºè
  const fixes = [
    // TS1484: ÈúÄË¶ÅÊ∑ªÂä†typeÂÖ≥ÈîÆÂ≠óÁöÑÁ±ªÂûãÂØºÂÖ•
    {
      pattern: /import \{([^}]*)\} from '([^']+)';/g,
      replacement: (match, imports, from) => {
        // Ê£ÄÊü•ÊòØÂê¶ÂåÖÂê´ÈúÄË¶Åtype-only importÁöÑÁ±ªÂûã
        const typeOnlyTypes = [
          'QualityBenchmark',
          'QualityComparison',
          'QualityReport',
          'TranslationManagerConfig',
          'TranslationQualityCheck',
          'ValidationReport',
          'LocaleQualityReport',
          'QualityIssue',
          'QualityScore',
          'TranslationSecurityConfig',
          'SecurityReport',
          'SecurityIssue',
          'QualityCheckResult',
          'QualityMetrics',
          'TranslationQuality',
          'QualityAnalysis',
          'TranslationValidationConfig',
          'ValidationError',
          'ValidationResult',
        ];

        const importList = imports.split(',').map((imp) => imp.trim());
        const typeImports = [];
        const valueImports = [];

        importList.forEach((imp) => {
          if (typeOnlyTypes.some((type) => imp.includes(type))) {
            typeImports.push(imp);
          } else {
            valueImports.push(imp);
          }
        });

        let result = '';
        if (typeImports.length > 0) {
          result += `import type { ${typeImports.join(', ')} } from '${from}';`;
        }
        if (valueImports.length > 0) {
          if (result) result += '\n';
          result += `import { ${valueImports.join(', ')} } from '${from}';`;
        }

        return result || match;
      },
    },
    // STORAGE_KEYS Â∫îËØ•ÊòØÂÄºÂØºÂÖ•Ôºå‰∏çÊòØÁ±ªÂûãÂØºÂÖ•
    {
      pattern:
        /import type \{([^}]*),\s*STORAGE_KEYS,([^}]*)\} from '([^']+)';/g,
      replacement: (match, before, after, from) => {
        const beforeClean = before.trim() ? `${before.trim()  },` : '';
        const afterClean = after.trim() ? `,${  after.trim()}` : '';
        return `import type {${beforeClean}${afterClean}} from '${from}';\nimport { STORAGE_KEYS } from '${from}';`;
      },
    },
    {
      pattern: /import type \{\s*STORAGE_KEYS,([^}]*)\} from '([^']+)';/g,
      replacement: (match, after, from) => {
        const afterClean = after.trim() ? after.trim() : '';
        if (afterClean) {
          return `import type {${afterClean}} from '${from}';\nimport { STORAGE_KEYS } from '${from}';`;
        } 
          return `import { STORAGE_KEYS } from '${from}';`;

      },
    },
    {
      pattern: /import type \{([^}]*),\s*STORAGE_KEYS\s*\} from '([^']+)';/g,
      replacement: (match, before, from) => {
        const beforeClean = before.trim() ? before.trim() : '';
        if (beforeClean) {
          return `import type {${beforeClean}} from '${from}';\nimport { STORAGE_KEYS } from '${from}';`;
        } 
          return `import { STORAGE_KEYS } from '${from}';`;

      },
    },
    // ÂÖ∂‰ªñÂ∏∏ËßÅÁöÑÂÄºÂØºÂÖ•ÈîôËØØ
    {
      pattern:
        /import type \{([^}]*),\s*(QUALITY_BENCHMARKS|TRANSLATION_LIMITS|VALIDATION_RULES|STORAGE_CONSTANTS),([^}]*)\} from '([^']+)';/g,
      replacement: (match, before, constant, after, from) => {
        const beforeClean = before.trim() ? `${before.trim()  },` : '';
        const afterClean = after.trim() ? `,${  after.trim()}` : '';
        return `import type {${beforeClean}${afterClean}} from '${from}';\nimport { ${constant} } from '${from}';`;
      },
    },
    // ÂçïÁã¨ÁöÑÂ∏∏ÈáèÂØºÂÖ•
    {
      pattern:
        /import type \{\s*(STORAGE_CONSTANTS|QUALITY_BENCHMARKS|TRANSLATION_LIMITS|VALIDATION_RULES)\s*\} from '([^']+)';/g,
      replacement: (match, constant, from) => {
        return `import { ${constant} } from '${from}';`;
      },
    },
    {
      pattern:
        /import type \{([^}]*),\s*(STORAGE_CONSTANTS|QUALITY_BENCHMARKS|TRANSLATION_LIMITS|VALIDATION_RULES)\s*\} from '([^']+)';/g,
      replacement: (match, before, constant, from) => {
        const beforeClean = before.trim() ? before.trim() : '';
        if (beforeClean) {
          return `import type {${beforeClean}} from '${from}';\nimport { ${constant} } from '${from}';`;
        } 
          return `import { ${constant} } from '${from}';`;

      },
    },
  ];

  // Â∫îÁî®‰øÆÂ§ç
  for (const fix of fixes) {
    if (typeof fix.replacement === 'function') {
      content = content.replace(fix.pattern, fix.replacement);
    } else {
      const newContent = content.replace(fix.pattern, fix.replacement);
      if (newContent !== content) {
        content = newContent;
        modified = true;
      }
    }
  }

  // Ê£ÄÊü•ÊòØÂê¶Êúâ‰øÆÊîπ
  const originalContent = fs.readFileSync(filePath, 'utf8');
  if (content !== originalContent) {
    fs.writeFileSync(filePath, content);
    modified = true;
  }

  return modified;
}

// ‰∏ªÊâßË°åÂáΩÊï∞
function main() {
  const files = getFilesWithImportTypeErrors();

  if (files.length === 0) {
    console.log('‚úÖ Ê≤°ÊúâÊâæÂà∞ÈúÄË¶Å‰øÆÂ§çÁöÑimport typeÈîôËØØ');
    return;
  }

  console.log(`üìÅ ÊâæÂà∞ ${files.length} ‰∏™Êñá‰ª∂ÈúÄË¶Å‰øÆÂ§ç:`);
