      console.log(examples);
    }
  } catch (error) {
    console.log('È™åËØÅÊó∂Âá∫Èîô:', error.message);
  }

  console.log('\nüéØ ÊâπÈáè‰øÆÂ§ç‰ªªÂä°ÂÆåÊàêÔºÅ');
}

