#!/usr/bin/env node

/**
 * ‰øÆÂ§çTS1205ÈáçÊñ∞ÂØºÂá∫ÈîôËØØ
 * Â∞Ü export * from ËΩ¨Êç¢‰∏∫ÂÖ∑‰ΩìÁöÑÂëΩÂêçÂØºÂá∫
 */

const fs = require('fs');
const path = require('path');
const { execSync } = require('child_process');

/**
 * ÂàÜÊûêÊ®°ÂùóÁöÑÂØºÂá∫ÂÜÖÂÆπ
 */
function analyzeModuleExports(modulePath) {
  try {
    const content = fs.readFileSync(modulePath, 'utf8');
    const exports = new Set();

    // ÂåπÈÖç export { ... } from ËØ≠Âè•
    const namedExportRegex =
      /export\s*{\s*([^}]+)\s*}\s*from\s*['"][^'"]+['"]/g;
    let match;
    while ((match = namedExportRegex.exec(content)) !== null) {
      const exportList = match[1].split(',').map((exp) => exp.trim());
      exportList.forEach((exp) => {
        // Â§ÑÁêÜ as ÈáçÂëΩÂêç
        const cleanExp = exp.split(' as ')[0].trim();
        exports.add(cleanExp);
      });
    }

    // ÂåπÈÖç export const/function/class/interface/type ËØ≠Âè•
    const directExportRegex =
      /export\s+(?:const|function|class|interface|type)\s+(\w+)/g;
    while ((match = directExportRegex.exec(content)) !== null) {
      exports.add(match[1]);
    }

    // ÂåπÈÖç export type { ... } ËØ≠Âè•
    const typeExportRegex = /export\s+type\s*{\s*([^}]+)\s*}/g;
    while ((match = typeExportRegex.exec(content)) !== null) {
      const typeList = match[1].split(',').map((exp) => exp.trim());
      typeList.forEach((exp) => {
        const cleanExp = exp.split(' as ')[0].trim();
        exports.add(cleanExp);
      });
    }

    return Array.from(exports);
  } catch (error) {
    console.warn(`Êó†Ê≥ïÂàÜÊûêÊ®°Âùó ${modulePath}: ${error.message}`);
    return [];
  }
}

/**
 * ‰øÆÂ§çÂçï‰∏™Êñá‰ª∂ÁöÑexport *ÈóÆÈ¢ò
 */
function fixExportStarInFile(filePath) {
  try {
    const content = fs.readFileSync(filePath, 'utf8');
    let newContent = content;
    let modified = false;

    // ÂåπÈÖç export * from 'module' ËØ≠Âè•
    const exportStarRegex = /export\s*\*\s*from\s*['"]([^'"]+)['"]/g;
    const matches = [];
    let match;

    while ((match = exportStarRegex.exec(content)) !== null) {
      matches.push({
        fullMatch: match[0],
        modulePath: match[1],
      });
    }

    // Â§ÑÁêÜÊØè‰∏™ export * ËØ≠Âè•
    for (const matchInfo of matches) {
      const { fullMatch, modulePath } = matchInfo;

      // Ëß£ÊûêÁõ∏ÂØπË∑ØÂæÑ
      let resolvedPath;
      if (modulePath.startsWith('./') || modulePath.startsWith('../')) {
        resolvedPath = path.resolve(path.dirname(filePath), modulePath);
        // Â∞ùËØïÊ∑ªÂä†.tsÊâ©Â±ïÂêç
        if (!fs.existsSync(resolvedPath) && !resolvedPath.endsWith('.ts')) {
          resolvedPath += '.ts';
        }
      } else {
        // Ë∑≥ËøáÂ§ñÈÉ®Ê®°Âùó
        continue;
      }

      if (fs.existsSync(resolvedPath)) {
        const exports = analyzeModuleExports(resolvedPath);

        if (exports.length > 0) {
          // Â∞ÜÂØºÂá∫ÂàÜ‰∏∫Á±ªÂûãÂíåÂÄº
          const typeExports = [];
          const valueExports = [];

          // ÁÆÄÂçïÁöÑÂêØÂèëÂºèÂàÜÁ±ªÔºàÂèØ‰ª•Ê†πÊçÆÈúÄË¶ÅÊîπËøõÔºâ
          exports.forEach((exp) => {
            if (
              exp.includes('Type') ||
              exp.includes('Interface') ||
              exp.includes('Config') ||
              exp.includes('Options') ||
              (exp.startsWith('I') && exp[1] === exp[1].toUpperCase())
            ) {
              typeExports.push(exp);
            } else {
              valueExports.push(exp);
            }
          });

          let replacement = '';

          // Ê∑ªÂä†Á±ªÂûãÂØºÂá∫
          if (typeExports.length > 0) {
            replacement += `export type { ${typeExports.join(', ')} } from '${modulePath}';\n`;
          }

          // Ê∑ªÂä†ÂÄºÂØºÂá∫
          if (valueExports.length > 0) {
            replacement += `export { ${valueExports.join(', ')} } from '${modulePath}';`;
          }

          // Â¶ÇÊûúÊ≤°ÊúâÂàÜÁ±ªÔºåÂÖ®ÈÉ®‰Ωú‰∏∫ÂÄºÂØºÂá∫
          if (
            typeExports.length === 0 &&
            valueExports.length === 0 &&
            exports.length > 0
          ) {
            replacement = `export { ${exports.join(', ')} } from '${modulePath}';`;
          }

          if (replacement) {
            newContent = newContent.replace(fullMatch, replacement);
            modified = true;
            console.log(
              `  ‰øÆÂ§ç ${filePath}: ÊõøÊç¢ export * from '${modulePath}' -> ${exports.length}‰∏™ÂÖ∑‰ΩìÂØºÂá∫`,
            );
          }
        }
      }
    }

    if (modified) {
      fs.writeFileSync(filePath, newContent, 'utf8');
      return true;
    }

    return false;
  } catch (error) {
    console.error(`Â§ÑÁêÜÊñá‰ª∂ ${filePath} Êó∂Âá∫Èîô:`, error.message);
    return false;
  }
}

/**
 * ÈÄíÂΩíÂ§ÑÁêÜÁõÆÂΩï‰∏≠ÁöÑÊâÄÊúâTypeScriptÊñá‰ª∂
 */
function fixExportStarInDirectory(dirPath) {
  let fixedCount = 0;

  function processDirectory(currentPath) {
    const items = fs.readdirSync(currentPath);

    for (const item of items) {
      const fullPath = path.join(currentPath, item);
      const stat = fs.statSync(fullPath);

      if (stat.isDirectory()) {
        // Ë∑≥Ëøá‰∏çÈúÄË¶ÅÂ§ÑÁêÜÁöÑÁõÆÂΩï
        if (
          ![
            'node_modules',
            '.next',
            '.git',
            'dist',
            'build',
            'backups',
          ].includes(item)
        ) {
          processDirectory(fullPath);
        }
      } else if (stat.isFile() && /\.(ts|tsx)$/.test(item)) {
        if (fixExportStarInFile(fullPath)) {
          fixedCount++;
        }
      }
    }
  }

  if (fs.existsSync(dirPath)) {
    processDirectory(dirPath);
  }

  return fixedCount;
}

/**
 * ‰∏ªÂáΩÊï∞
 */
function main() {
  console.log('üöÄ ÂºÄÂßã‰øÆÂ§çTS1205ÈáçÊñ∞ÂØºÂá∫ÈîôËØØ...\n');

  const startTime = Date.now();

  // ‰øÆÂ§çsrcÁõÆÂΩï
  console.log('üìÅ Â§ÑÁêÜ src ÁõÆÂΩï...');
  const srcFixedCount = fixExportStarInDirectory('./src');

  const totalFixed = srcFixedCount;
  const duration = Date.now() - startTime;

  console.log(`\nüìä ‰øÆÂ§çÂÆåÊàêÁªüËÆ°:`);
  console.log(`   ‰øÆÂ§çÊñá‰ª∂Êï∞: ${totalFixed}`);
  console.log(`   ËÄóÊó∂: ${duration}ms`);

  // ËøêË°åTypeScriptÊ£ÄÊü•È™åËØÅ‰øÆÂ§çÊïàÊûú
  console.log('\nüîç È™åËØÅ‰øÆÂ§çÊïàÊûú...');
  try {
    const output = execSync('pnpm type-check 2>&1', { encoding: 'utf8' });
    const errorCount = (output.match(/error TS/g) || []).length;
    console.log(`‚úÖ TypeScriptÊ£ÄÊü•ÂÆåÊàêÔºåÂâ©‰ΩôÈîôËØØ: ${errorCount}‰∏™`);
  } catch (error) {
    const errorOutput = error.stdout || error.stderr || '';
    const errorCount = (errorOutput.match(/error TS/g) || []).length;
    console.log(`‚ö†Ô∏è ‰ªçÊúâTypeScriptÈîôËØØ: ${errorCount}‰∏™`);

    // ÊòæÁ§∫TS1205Áõ∏ÂÖ≥ÈîôËØØ
    const errorLines = errorOutput.split('\n');
    const ts1205Errors = errorLines.filter((line) => line.includes('TS1205'));
    if (ts1205Errors.length > 0) {
      console.log('\nÂâ©‰ΩôTS1205ÈîôËØØ:');
      ts1205Errors.slice(0, 5).forEach((line) => {
        console.log(`  ${line}`);
      });
    }
  }

  console.log('\nüéØ TS1205‰øÆÂ§ç‰ªªÂä°ÂÆåÊàêÔºÅ');
}

// ËøêË°åËÑöÊú¨
if (require.main === module) {
  main();
}

module.exports = { fixExportStarInFile, fixExportStarInDirectory };

