// Ëé∑ÂèñÊâÄÊúâTS6196ÈîôËØØÔºàÊú™‰ΩøÁî®ÁöÑÂØºÂÖ•Ôºâ
function getUnusedImportErrors() {
  try {
    const output = execSync('pnpm type-check 2>&1 | grep "TS6196"', {
      encoding: 'utf8',
    });
    return output
      .trim()
      .split('\n')
      .filter((line) => line.trim())
