          /^([^(]+)\((\d+),(\d+)\): error TS6196: '([^']+)' is declared but never used\./,
        );
        if (match) {
          return {
            file: match[1],
            line: parseInt(match[2]),
            column: parseInt(match[3]),
            variable: match[4],
            fullLine: line,
          };
        }
        return null;
      })
