  const content = fs.readFileSync(filePath, 'utf8');
  let modified = false;

  // ÊåâÂèòÈáèÂêçÂàÜÁªÑÂ§ÑÁêÜ
  const variablesByLine = {};
  errors.forEach((error) => {
    if (!variablesByLine[error.line]) {
      variablesByLine[error.line] = [];
    }
    variablesByLine[error.line].push(error.variable);
  });

  // Â§ÑÁêÜÊØè‰∏ÄË°åÁöÑÂØºÂÖ•
  for (const [lineNum, variables] of Object.entries(variablesByLine)) {
    const lineIndex = parseInt(lineNum) - 1;
    const lines = content.split('\n');

    if (lineIndex >= 0 && lineIndex < lines.length) {
      const line = lines[lineIndex];

      // Ê£ÄÊü•ÊòØÂê¶ÊòØimportËØ≠Âè•
      if (line.trim().startsWith('import')) {
        let newLine = line;

        // Â§ÑÁêÜÊØè‰∏™Êú™‰ΩøÁî®ÁöÑÂèòÈáè
        for (const variable of variables) {
          // Ê£ÄÊü•ÊòØÂê¶ÊòØËä±Êã¨Âè∑ÂØºÂÖ• import { ... } from '...'
          const braceMatch = newLine.match(/import\s*\{\s*([^}]+)\s*\}\s*from/);
          if (braceMatch) {
            const imports = braceMatch[1]
              .split(',')
              .map((imp) => imp.trim())
              .filter((imp) => imp);
            const filteredImports = imports.filter((imp) => imp !== variable);

            if (filteredImports.length === 0) {
              // Â¶ÇÊûúÊ≤°ÊúâÂâ©‰ΩôÂØºÂÖ•ÔºåÂà†Èô§Êï¥Ë°å
              newLine = '';
              console.log(`  ‚úÖ Âà†Èô§Á©∫ÂØºÂÖ•Ë°å: ${variable}`);
            } else if (filteredImports.length < imports.length) {
              // ÁßªÈô§ÁâπÂÆöÁöÑÂØºÂÖ•
              newLine = newLine.replace(
                /\{[^}]+\}/,
                `{ ${filteredImports.join(', ')} }`,
              );
              console.log(`  ‚úÖ ‰ªéÂØºÂÖ•‰∏≠ÁßªÈô§: ${variable}`);
            }
          }
          // Ê£ÄÊü•ÊòØÂê¶ÊòØÈªòËÆ§ÂØºÂÖ• import Variable from '...'
          else if (newLine.includes(`import ${variable} from`)) {
            newLine = '';
            console.log(`  ‚úÖ Âà†Èô§Êú™‰ΩøÁî®ÁöÑÈªòËÆ§ÂØºÂÖ•: ${variable}`);
          }
          // Ê£ÄÊü•ÊòØÂê¶ÊòØÂëΩÂêçÁ©∫Èó¥ÂØºÂÖ• import * as Variable from '...'
          else if (newLine.includes(`import * as ${variable} from`)) {
            newLine = '';
            console.log(`  ‚úÖ Âà†Èô§Êú™‰ΩøÁî®ÁöÑÂëΩÂêçÁ©∫Èó¥ÂØºÂÖ•: ${variable}`);
          }
        }

        if (newLine !== line) {
          if (newLine.trim() === '') {
            // Âà†Èô§Êï¥Ë°å
            lines.splice(lineIndex, 1);
          } else {
            // Êõ¥Êñ∞Ë°åÂÜÖÂÆπ
            lines[lineIndex] = newLine;
          }
          modified = true;
        }
      }
    }
  }

  if (modified) {
    const newContent = lines.join('\n');
    fs.writeFileSync(filePath, newContent);
    return true;
  }

  return false;
}

// ‰∏ªÊâßË°åÂáΩÊï∞
function main() {
