#!/usr/bin/env node

const fs = require('fs');
const path = require('path');
const { execSync } = require('child_process');

console.log('üöÄ ÂºÄÂßã‰øÆÂ§çÊú™‰ΩøÁî®ÂèòÈáèÂíåÂ£∞ÊòéÈîôËØØ...');

// Ëé∑ÂèñÊâÄÊúâÊú™‰ΩøÁî®ÂèòÈáèÂíåÂ£∞ÊòéÈîôËØØ
function getUnusedVariableErrors() {
  try {
    const output = execSync(
      'pnpm type-check 2>&1 | grep -E "(TS6133|TS6196)"',
      { encoding: 'utf8' },
    );
    return output
      .trim()
      .split('\n')
      .filter((line) => line.trim())
      .map((line) => {
        const match = line.match(
          /^([^(]+)\((\d+),(\d+)\): error (TS\d+): '([^']+)' is declared but its value is never read\./,
        );
        if (match) {
          return {
            file: match[1],
            line: parseInt(match[2]),
            column: parseInt(match[3]),
            errorCode: match[4],
            variable: match[5],
            fullLine: line,
          };
        }
        return null;
      })
      .filter(Boolean);
  } catch (error) {
    console.log('Ê≤°ÊúâÊâæÂà∞Êú™‰ΩøÁî®ÂèòÈáèÈîôËØØ');
    return [];
  }
}

// ÂÆâÂÖ®Âú∞‰øÆÂ§çÊú™‰ΩøÁî®ÂèòÈáè
function fixUnusedVariableInFile(filePath, errors) {
  if (!fs.existsSync(filePath)) {
    console.log(`‚ö†Ô∏è Êñá‰ª∂‰∏çÂ≠òÂú®: ${filePath}`);
    return false;
  }

  const content = fs.readFileSync(filePath, 'utf8');
  const lines = content.split('\n');
  let modified = false;

  // ÊåâË°åÂè∑ÂÄíÂ∫èÂ§ÑÁêÜÔºåÈÅøÂÖçË°åÂè∑ÂÅèÁßªÈóÆÈ¢ò
  const sortedErrors = errors.sort((a, b) => b.line - a.line);

  for (const error of sortedErrors) {
    const lineIndex = error.line - 1;
    if (lineIndex >= 0 && lineIndex < lines.length) {
      const line = lines[lineIndex];
      const {variable} = error;

      // Â§ÑÁêÜ‰∏çÂêåÁ±ªÂûãÁöÑÊú™‰ΩøÁî®ÂèòÈáè
      if (error.errorCode === 'TS6133') {
        // Êú™‰ΩøÁî®ÁöÑÂèòÈáè
        if (variable.startsWith('_')) {
          // Â∑≤ÁªèÊòØ‰∏ãÂàíÁ∫øÂâçÁºÄÔºåË∑≥Ëøá
          continue;
        }

        // ÁâπÊÆäÂ§ÑÁêÜÔºöÊµãËØïÊñá‰ª∂‰∏≠ÁöÑÂèòÈáèÂèØ‰ª•Ê∑ªÂä†‰∏ãÂàíÁ∫øÂâçÁºÄ
        if (filePath.includes('.test.') || filePath.includes('__tests__')) {
          const newLine = line.replace(
            new RegExp(`\\b${variable}\\b`),
            `_${variable}`,
          );
          if (newLine !== line) {
            lines[lineIndex] = newLine;
            modified = true;
            console.log(
              `  ‚úÖ ÊµãËØïÊñá‰ª∂ÂèòÈáèÊ∑ªÂä†‰∏ãÂàíÁ∫øÂâçÁºÄ: ${variable} -> _${variable}`,
            );
          }
        }
        // ÂØπ‰∫éÈùûÊµãËØïÊñá‰ª∂ÔºåÂ¶ÇÊûúÊòØÂáΩÊï∞ÂèÇÊï∞Ôºå‰πüÂèØ‰ª•Ê∑ªÂä†‰∏ãÂàíÁ∫ø
        else if (line.includes('(') && line.includes(')')) {
          const newLine = line.replace(
            new RegExp(`\\b${variable}\\b`),
            `_${variable}`,
          );
          if (newLine !== line) {
            lines[lineIndex] = newLine;
            modified = true;
            console.log(
              `  ‚úÖ ÂáΩÊï∞ÂèÇÊï∞Ê∑ªÂä†‰∏ãÂàíÁ∫øÂâçÁºÄ: ${variable} -> _${variable}`,
            );
          }
        }
      } else if (error.errorCode === 'TS6196') {
        // Êú™‰ΩøÁî®ÁöÑÂØºÂÖ•
        // Ê£ÄÊü•ÊòØÂê¶ÊòØÂçïÁã¨ÁöÑÂØºÂÖ•Ë°å
        if (line.trim().startsWith('import') && line.includes(variable)) {
          // Â¶ÇÊûúÊòØÂçïÁã¨ÂØºÂÖ•‰∏îÂè™ÊúâËøô‰∏Ä‰∏™ÂèòÈáèÔºåÂà†Èô§Êï¥Ë°å
          const importMatch = line.match(/import\s*\{\s*([^}]+)\s*\}/);
          if (importMatch) {
            const imports = importMatch[1].split(',').map((imp) => imp.trim());
            if (imports.length === 1 && imports[0] === variable) {
              // Âà†Èô§Êï¥Ë°åÂØºÂÖ•
              lines.splice(lineIndex, 1);
              modified = true;
              console.log(`  ‚úÖ Âà†Èô§Êú™‰ΩøÁî®ÁöÑÂØºÂÖ•Ë°å: ${variable}`);
              continue;
            } else if (imports.length > 1) {
              // ‰ªéÂ§ö‰∏™ÂØºÂÖ•‰∏≠ÁßªÈô§Ëøô‰∏™ÂèòÈáè
              const newImports = imports.filter((imp) => imp !== variable);
              const newLine = line.replace(
                /\{[^}]+\}/,
                `{ ${newImports.join(', ')} }`,
              );
              lines[lineIndex] = newLine;
              modified = true;
              console.log(`  ‚úÖ ‰ªéÂØºÂÖ•‰∏≠ÁßªÈô§: ${variable}`);
            }
          }
        }
      }
    }
  }

  if (modified) {
    const newContent = lines.join('\n');
    fs.writeFileSync(filePath, newContent);
    return true;
  }

  return false;
}

// ‰∏ªÊâßË°åÂáΩÊï∞
function main() {
  const errors = getUnusedVariableErrors();

  if (errors.length === 0) {
    console.log('‚úÖ Ê≤°ÊúâÊâæÂà∞ÈúÄË¶Å‰øÆÂ§çÁöÑÊú™‰ΩøÁî®ÂèòÈáèÈîôËØØ');
