#!/usr/bin/env node

const fs = require('fs');
const path = require('path');

/**
 * ‰øÆÂ§çWhatsAppÁ±ªÂûãÊñá‰ª∂‰∏≠ÁöÑÁõ∏ÂØπË∑ØÂæÑÂØºÂÖ•ÈóÆÈ¢ò
 * Â∞Ü './whatsapp-xxx' ÊõøÊç¢‰∏∫ '@/types/whatsapp-xxx'
 */

const filesToFix = [
  'src/types/whatsapp-service-types.ts',
  'src/types/whatsapp-webhook-events.ts', 
  'src/types/whatsapp-webhook-types.ts',
  'src/types/whatsapp-api-requests/api-types.ts',
  'src/types/whatsapp-api-config.ts',
  'src/types/whatsapp-webhook-utils.ts',
  'src/types/__tests__/index.test.ts',
  'src/types/whatsapp.ts',
  'src/types/index.ts',
  'src/types/whatsapp-service-interface.ts',
  'src/types/whatsapp-webhook-utils/webhook-utils.ts'
];

function fixRelativeImports(filePath) {
  if (!fs.existsSync(filePath)) {
    console.log(`Êñá‰ª∂‰∏çÂ≠òÂú®: ${filePath}`);
    return;
  }

  const content = fs.readFileSync(filePath, 'utf8');
  let modifiedContent = content;
  let changeCount = 0;

  // ‰øÆÂ§çÁõ∏ÂØπË∑ØÂæÑÂØºÂÖ•
  // Â∞Ü './whatsapp-xxx' ÊõøÊç¢‰∏∫ '@/types/whatsapp-xxx'
  modifiedContent = modifiedContent.replace(
    /from\s+['"]\.\/whatsapp-([^'"]+)['"]/g,
    (match, moduleName) => {
      changeCount++;
      return `from '@/types/whatsapp-${moduleName}'`;
    }
  );

  // ‰øÆÂ§çÁõ∏ÂØπË∑ØÂæÑÂØºÂÖ• - Â§ÑÁêÜÂ≠êÁõÆÂΩïÊÉÖÂÜµ
  // Â∞Ü './whatsapp-xxx/yyy' ÊõøÊç¢‰∏∫ '@/types/whatsapp-xxx/yyy'
  modifiedContent = modifiedContent.replace(
    /from\s+['"]\.\/whatsapp-([^'"]+\/[^'"]+)['"]/g,
    (match, modulePath) => {
      changeCount++;
      return `from '@/types/whatsapp-${modulePath}'`;
    }
  );

  // ‰øÆÂ§çÂÖ∂‰ªñÁõ∏ÂØπË∑ØÂæÑÂØºÂÖ•
  // Â∞Ü './interfaces' Á≠âÊõøÊç¢‰∏∫ '@/types/whatsapp-webhook-utils/interfaces'
  if (filePath.includes('whatsapp-webhook-utils')) {
    modifiedContent = modifiedContent.replace(
      /from\s+['"]\.\/interfaces['"]/g,
      () => {
        changeCount++;
        return `from '@/types/whatsapp-webhook-utils/interfaces'`;
      }
    );

    modifiedContent = modifiedContent.replace(
      /from\s+['"]\.\/functions['"]/g,
      () => {
        changeCount++;
        return `from '@/types/whatsapp-webhook-utils/functions'`;
      }
    );
  }

  // ‰øÆÂ§ç‰∏äÁ∫ßÁõÆÂΩïÁöÑÁõ∏ÂØπË∑ØÂæÑÂØºÂÖ•
  // Â∞Ü '../whatsapp-xxx' ÊõøÊç¢‰∏∫ '@/types/whatsapp-xxx'
  modifiedContent = modifiedContent.replace(
    /from\s+['"]\.\.\/whatsapp-([^'"]+)['"]/g,
    (match, moduleName) => {
      changeCount++;
      return `from '@/types/whatsapp-${moduleName}'`;
    }
  );

  if (changeCount > 0) {
    fs.writeFileSync(filePath, modifiedContent, 'utf8');
    console.log(`‚úÖ ‰øÆÂ§ç ${filePath}: ${changeCount} ‰∏™ÂØºÂÖ•Ë∑ØÂæÑ`);
  } else {
    console.log(`‚è≠Ô∏è  Ë∑≥Ëøá ${filePath}: Êó†ÈúÄ‰øÆÂ§ç`);
  }
}

function main() {
  console.log('üîß ÂºÄÂßã‰øÆÂ§çWhatsAppÁ±ªÂûãÊñá‰ª∂ÁöÑÁõ∏ÂØπË∑ØÂæÑÂØºÂÖ•...\n');

  let totalFixed = 0;

  filesToFix.forEach(filePath => {
    try {
      fixRelativeImports(filePath);
      totalFixed++;
    } catch (error) {
      console.error(`‚ùå ‰øÆÂ§çÂ§±Ë¥• ${filePath}:`, error.message);
    }
  });

  console.log(`\nüéâ ‰øÆÂ§çÂÆåÊàê! Â§ÑÁêÜ‰∫Ü ${totalFixed} ‰∏™Êñá‰ª∂`);
}

if (require.main === module) {
  main();
}

module.exports = { fixRelativeImports };

#!/usr/bin/env node

/**
 * Â≠ó‰ΩìÂ≠êÈõÜÂåñÂÆûÊñΩËÑöÊú¨
 *
 * Âü∫‰∫é‰∏≠ÊñáÂ≠óÁ¨¶ÂàÜÊûêÁªìÊûúÔºåÁîüÊàêÂ≠ó‰ΩìÂ≠êÈõÜÂåñÈÖçÁΩÆÂíåÂÆûÊñΩÊåáÂçó
 * ÊîØÊåÅPingFang SCÂ≠ó‰ΩìÁöÑÂ≠êÈõÜÂåñÂ§ÑÁêÜ
 */

const fs = require('fs');
const path = require('path');

class FontSubsetImplementation {
  constructor() {
    this.config = {
      sourceFont: 'PingFang SC',
      targetFormats: ['woff2', 'woff'],
      outputDir: 'public/fonts/subsets',
      characterFile: 'reports/chinese-characters.txt',
      analysisFile: 'reports/chinese-character-analysis.json',
    };

    this.results = {
      subsetGenerated: false,
      configUpdated: false,
      fallbackOptimized: false,
      cacheConfigured: false,
    };
  }

  /**
   * ËØªÂèñÂ≠óÁ¨¶ÂàÜÊûêÁªìÊûú
   */
  loadAnalysisResults() {
    try {
      const analysisPath = path.join(process.cwd(), this.config.analysisFile);
      const analysisData = JSON.parse(fs.readFileSync(analysisPath, 'utf-8'));

      const charactersPath = path.join(
        process.cwd(),
        this.config.characterFile,
      );
      const characters = fs.readFileSync(charactersPath, 'utf-8').trim();

      return {
        analysis: analysisData,
        characters,
        charCount: analysisData.summary.uniqueChars,
        estimatedSavings: analysisData.recommendations.estimatedSavings,
      };
    } catch (error) {
      console.error('‚ùå Êó†Ê≥ïËØªÂèñÂ≠óÁ¨¶ÂàÜÊûêÁªìÊûú:', error.message);
      return null;
    }
  }

  /**
   * ÁîüÊàêÂ≠ó‰ΩìÂ≠êÈõÜÂåñÈÖçÁΩÆ
   */
  generateSubsetConfig(data) {
    const config = {
      // Â≠ó‰ΩìÂ≠êÈõÜÂåñÈÖçÁΩÆ
      fontSubset: {
        source: {
          font: this.config.sourceFont,
          fallbacks: ['system-ui', 'sans-serif'],
        },
        subset: {
          characters: data.characters,
          unicodeRange: this.generateUnicodeRanges(data.characters),
          formats: this.config.targetFormats,
          outputDir: this.config.outputDir,
        },
        optimization: {
          hinting: true,
          compression: 'maximum',
          removeUnusedFeatures: true,
        },
        performance: {
          preload: true,
          display: 'swap',
          fallbackDelay: '100ms',
        },
      },

      // È¢ÑÊúüÊÄßËÉΩÊîπËøõ
      expectedImprovements: {
        fileSizeReduction: data.estimatedSavings.savingsPercentage,
        loadTimeImprovement: '30-50ms',
        lcpImprovement: '30-50ms',
        cacheEfficiency: '95%',
      },

      // ÂÆûÊñΩÊ≠•È™§
      implementationSteps: [
        '1. ÂÆâË£ÖÂ≠ó‰ΩìÂ§ÑÁêÜÂ∑•ÂÖ∑ (fonttools, pyftsubset)',
        '2. ÁîüÊàêÂ≠ó‰ΩìÂ≠êÈõÜÊñá‰ª∂',
        '3. Êõ¥Êñ∞CSSÂ≠ó‰ΩìÈÖçÁΩÆ',
        '4. ÈÖçÁΩÆÂ≠ó‰ΩìÈ¢ÑÂä†ËΩΩ',
        '5. ÂÆûÊñΩÂ≠ó‰ΩìÂõûÈÄÄÁ≠ñÁï•',
        '6. ÈÖçÁΩÆÁºìÂ≠òÁ≠ñÁï•',
        '7. È™åËØÅÂ≠ó‰ΩìÊòæÁ§∫ÊïàÊûú',
      ],
    };

    return config;
  }

  /**
   * ÁîüÊàêUnicodeËåÉÂõ¥
   */
  generateUnicodeRanges(characters) {
    const ranges = [];
    const codes = characters
      .split('')
      .map((char) => char.charCodeAt(0))
      .sort((a, b) => a - b);

    let start = codes[0];
    let end = codes[0];

    for (let i = 1; i < codes.length; i++) {
      if (codes[i] === end + 1) {
        end = codes[i];
      } else {
        ranges.push(
          start === end
            ? `U+${start.toString(16).toUpperCase()}`
            : `U+${start.toString(16).toUpperCase()}-${end.toString(16).toUpperCase()}`,
        );
        start = end = codes[i];
      }
    }

    // Ê∑ªÂä†ÊúÄÂêé‰∏Ä‰∏™ËåÉÂõ¥
    ranges.push(
      start === end
        ? `U+${start.toString(16).toUpperCase()}`
        : `U+${start.toString(16).toUpperCase()}-${end.toString(16).toUpperCase()}`,
    );

    return ranges;
  }

