#!/usr/bin/env node
/*
 * Â¢ûÈáèÊ†ºÂºèÊ£ÄÊü•Ôºà‰ªÖÈíàÂØπÊöÇÂ≠òÊñá‰ª∂Ôºâ
 * - ËØªÂèñ Git ÊöÇÂ≠òÂå∫ÁöÑÊñá‰ª∂ÂàóË°®
 * - ‰ªÖÂØπËøô‰∫õÊñá‰ª∂ËøêË°å Prettier Ê†ºÂºèÊ£ÄÊü•
 * - ‰ºòÂåñpre-commitÊÄßËÉΩÔºåÈÅøÂÖçÂÖ®ÈáèÊ†ºÂºèÊ£ÄÊü•
 */

const { execSync, spawnSync } = require('child_process');

function getStagedFiles() {
  try {
    const output = execSync(
      'git diff --name-only --cached --diff-filter=ACMR',
      { stdio: ['ignore', 'pipe', 'ignore'] },
    )
      .toString()
      .trim();
    if (!output) return [];

    // ËøáÊª§Âá∫ Prettier ÊîØÊåÅÁöÑÊñá‰ª∂Á±ªÂûã
    return output
      .split('\n')
      .filter((f) =>
        /\.(js|jsx|ts|tsx|json|md|css|scss|html|yml|yaml)$/i.test(f),
      );
  } catch {
    return [];
  }
}

function runPrettierCheck(files) {
  if (files.length === 0) {
    console.log('[format:check:staged] Êó†ÈúÄÊ£ÄÊü•ÔºöÊ≤°ÊúâÊöÇÂ≠òÁöÑÊ†ºÂºèÂåñÊñá‰ª∂');
    return 0;
  }

  console.log(`[format:check:staged] Ê£ÄÊü• ${files.length} ‰∏™Êñá‰ª∂ÁöÑÊ†ºÂºè...`);

  // ‰ΩøÁî® prettier --check ÂØπÊåáÂÆöÊñá‰ª∂ËøõË°åÊ†ºÂºèÊ£ÄÊü•
  const result = spawnSync('npx', ['prettier', '--check', ...files], {
    stdio: 'inherit',
    cwd: process.cwd(),
  });

  if (result.status !== 0) {
    console.error('[format:check:staged] ‰ª£Á†ÅÊ†ºÂºèÊ£ÄÊü•Â§±Ë¥•');
    console.error('ËØ∑ËøêË°å pnpm format:write ‰øÆÂ§çÊ†ºÂºèÈóÆÈ¢ò');
    return result.status;
  }

  console.log('[format:check:staged] ‚úÖ ‰ª£Á†ÅÊ†ºÂºèÊ£ÄÊü•ÈÄöËøá');
  return 0;
}

function main() {
  const stagedFiles = getStagedFiles();
  const exitCode = runPrettierCheck(stagedFiles);
  process.exit(exitCode);
}

if (require.main === module) {
  main();
}

module.exports = { getStagedFiles, runPrettierCheck };

