  console.log('üöÄ ASTÈ≠îÊ≥ïÊï∞Â≠óÊõøÊç¢Âô®ÂêØÂä®...');

  // ÂàùÂßãÂåñÈ°πÁõÆ - ‰øÆÂ§çË∑ØÂæÑÈóÆÈ¢ò
  const rootDir = process.cwd().endsWith('scripts/magic-numbers')
    ? resolve(process.cwd(), '../..')
    : process.cwd();

  const project = new Project({
    tsConfigFilePath: resolve(rootDir, 'tsconfig.json'),
    manipulationSettings: {
      indentationText: '  ',
    },
  });

  // Ëé∑ÂèñÊñá‰ª∂ÂàóË°®
  let filePaths: string[];
  if (opts.files) {
    // Ê£ÄÊü•ÊòØÂê¶ÊòØÂçï‰∏™Êñá‰ª∂
    if (opts.files.endsWith('.ts') || opts.files.endsWith('.tsx')) {
      // Âçï‰∏™Êñá‰ª∂Ê®°Âºè
      const filePath = opts.files.startsWith('/') ? opts.files : resolve(rootDir, opts.files);
      filePaths = [filePath];
      console.log(`üéØ Âçï‰∏™Êñá‰ª∂Ê®°Âºè: ${filePath}`);
    } else {
      // ÁõÆÂΩïÊ®°Âºè - Â§ÑÁêÜÁõ∏ÂØπË∑ØÂæÑ
      let searchDir: string;
      if (opts.files.startsWith('/')) {
        searchDir = opts.files;
      } else {
        // Â§ÑÁêÜÁõ∏ÂØπË∑ØÂæÑÔºåÁ°Æ‰øùÊ≠£Á°ÆËß£Êûê
        searchDir = resolve(rootDir, opts.files.replace('/**/*.{ts,tsx}', ''));
      }

      console.log(`üîç Êñá‰ª∂Ê®°Âºè: ${opts.files}`);
      console.log(`üìÅ Ê†πÁõÆÂΩï: ${rootDir}`);
      console.log(`üéØ ÊêúÁ¥¢ÁõÆÂΩï: ${searchDir}`);

      // ‰ΩøÁî®Ëá™ÂÆö‰πâÊñá‰ª∂Êü•Êâæ
      filePaths = findTsFiles(searchDir);
      console.log(`üìÑ ÊâæÂà∞Êñá‰ª∂Êï∞Èáè: ${filePaths.length}`);

      if (filePaths.length > 0) {
        console.log(`üìã Ââç5‰∏™Êñá‰ª∂Á§∫‰æã:`);
        filePaths.slice(0, 5).forEach(file => {
          console.log(`  ${file.replace(rootDir, '.')}`);
        });
      }
    }
  } else {
    // Ê∑ªÂä†ÊâÄÊúâÊ∫êÊñá‰ª∂
    const srcDir = resolve(rootDir, 'src');
    console.log(`üìÅ ÈªòËÆ§ÊêúÁ¥¢ÁõÆÂΩï: ${srcDir}`);
    filePaths = findTsFiles(srcDir);
  }

  // Ê∑ªÂä†Êñá‰ª∂Âà∞È°πÁõÆ
  const files = project.addSourceFilesAtPaths(filePaths);

  const targetFiles = files.slice(0, opts.limit ?? files.length);

  // Âä†ËΩΩÂ¢ûÂº∫ÁâàÊò†Â∞Ñ
  const mapping = loadEnhancedMapping();
  if (Object.keys(mapping).length === 0) {
    console.error('‚ùå Êò†Â∞ÑÊñá‰ª∂‰∏∫Á©∫ÊàñÂä†ËΩΩÂ§±Ë¥•');
    return [];
  }

  console.log(`üìä Â§ÑÁêÜ ${targetFiles.length} ‰∏™Êñá‰ª∂...`);
  console.log(`üìã ÂèØÁî®Â∏∏ÈáèÊò†Â∞Ñ: ${Object.keys(mapping).length} ‰∏™`);

  const logs: ReplaceLogEntry[] = [];
  let totalReplacements = 0;

  for (const sourceFile of targetFiles) {
    const filePath = sourceFile.getFilePath();
    const relativePath = filePath.replace(`${process.cwd()  }/`, '');

    const fileLog: ReplaceLogEntry = {
      file: relativePath,
      changes: [],
      imports: { added: [], aliased: {} },
    };

    const newConstants = new Set<string>();
    const constantsWithModules: Array<{ constant: string; module: string }> = [];

    // Áõ¥Êé•ËøõË°åÊõøÊç¢Ôºå‰ΩÜÂÖàÂ§ÑÁêÜÂØºÂÖ•
    sourceFile.forEachDescendant((node) => {
      if (ts.isNumericLiteral(node.compilerNode)) {
        const text = node.getText();

        // Ê£ÄÊü•ÊòØÂê¶Â∫îËØ•Ë∑≥Ëøá
        if (shouldSkipNode(node)) {
          return;
        }

        const { constantName, isSupported, module } = ensureConstDefined(mapping, text);

        if (!isSupported) {
          return;
        }

        const parent = node.getParent();

        // Â§ÑÁêÜË¥üÂè∑ÂâçÁºÄ
        if (parent && ts.isPrefixUnaryExpression(parent.compilerNode) &&
            parent.compilerNode.operator === ts.SyntaxKind.MinusToken) {

          fileLog.changes.push({
            start: node.getStart(),
            end: node.getEnd(),
            raw: `-${text}`,
            constant: constantName,
            negated: true,
          });

          // ÊõøÊç¢Êìç‰ΩúÊï∞Ôºå‰øùÁïôË¥üÂè∑
          node.replaceWithText(constantName);
          newConstants.add(constantName);
          constantsWithModules.push({ constant: constantName, module });
          totalReplacements++;

        } else {
          fileLog.changes.push({
            start: node.getStart(),
            end: node.getEnd(),
            raw: text,
            constant: constantName,
            negated: false,
          });

          // Áõ¥Êé•ÊõøÊç¢
          node.replaceWithText(constantName);
          newConstants.add(constantName);
          constantsWithModules.push({ constant: constantName, module });
          totalReplacements++;
        }
      }
    });

    // Â§ÑÁêÜÂØºÂÖ•ÔºàÂú®ÊõøÊç¢‰πãÂêéÔºå‰ΩÜÂú®‰øùÂ≠ò‰πãÂâçÔºâ
    if (newConstants.size > 0) {
      mergeAndAliasImports(sourceFile, constantsWithModules, fileLog);
    }

    if (newConstants.size > 0) {
      console.log(`üîß ${relativePath}: ${newConstants.size} ‰∏™ÊõøÊç¢`);
      newConstants.forEach(constName => {
        console.log(`    ${mapping[Object.keys(mapping).find(k => mapping[k] === constName) || '']} ‚Üí ${constName}`);
      });
    }

    // ‰øùÂ≠òÊñá‰ª∂
    if (!opts.dryRun && opts.write && newConstants.size > 0) {
      await sourceFile.save();
    }

    if (fileLog.changes.length > 0) {
      logs.push(fileLog);
    }
  }

  // ÁîüÊàêÊó•Âøó
  const timestamp = new Date().toISOString().replace(/[:.]/g, '-');
  const logPath = resolve(__dirname, '../logs', `ast-${timestamp}.json`);

  const logData = {
    timestamp: new Date().toISOString(),
    options: opts,
    summary: {
      filesProcessed: targetFiles.length,
      filesModified: logs.length,
      totalReplacements,
    },
    logs,
  };

  writeFileSync(logPath, JSON.stringify(logData, null, 2));

  console.log(`üìä Â§ÑÁêÜÂÆåÊàê:`);
  console.log(`  Êñá‰ª∂Â§ÑÁêÜ: ${targetFiles.length}`);
  console.log(`  Êñá‰ª∂‰øÆÊîπ: ${logs.length}`);
  console.log(`  ÊÄªÊõøÊç¢Êï∞: ${totalReplacements}`);
  console.log(`üìÑ Êó•Âøó‰øùÂ≠òÂà∞: ${logPath}`);

  if (opts.dryRun) {
    console.log('üîç ËøôÊòØÂπ≤Ë∑ëÊ®°ÂºèÔºåÊú™ÂÆûÈôÖ‰øÆÊîπÊñá‰ª∂');
  }

  return logs;
}

// CLI ÂÖ•Âè£
if (require.main === module) {
  const args = process.argv.slice(2);

  const options: Options = {
    write: args.includes('--write'),
    dryRun: args.includes('--dry-run'),
    files: undefined,
    limit: undefined,
  };

  // Ëß£Êûê --files ÂèÇÊï∞
  const filesIndex = args.findIndex(arg => arg === '--files');
  if (filesIndex !== -1 && filesIndex + 1 < args.length) {
    options.files = args[filesIndex + 1];
  } else {
    const filesArg = args.find(arg => arg.startsWith('--files='));
    if (filesArg) {
      options.files = filesArg.split('=')[1];
    }
  }

  // Ëß£Êûê --limit ÂèÇÊï∞
  const limitIndex = args.findIndex(arg => arg === '--limit');
  if (limitIndex !== -1 && limitIndex + 1 < args.length) {
    options.limit = parseInt(args[limitIndex + 1]);
  } else {
    const limitArg = args.find(arg => arg.startsWith('--limit='));
    if (limitArg) {
      options.limit = parseInt(limitArg.split('=')[1]);
    }
  }

  // ÈªòËÆ§‰∏∫Âπ≤Ë∑ëÊ®°Âºè
  if (!options.write) {
    options.dryRun = true;
  }

  run(options).catch(console.error);
}

#!/usr/bin/env tsx

/**
 * CODEXÂàÜÂ±ÇÊ≤ªÁêÜÔºöÂàÜÊûêËøáÊª§ÂêéÁöÑÊï∞Â≠óÔºåËØÜÂà´ÁúüÊ≠£Êúâ‰∏öÂä°ËØ≠‰πâÁöÑÂ∏∏Èáè
 * 
 * ÁõÆÊ†áÔºö‰ªé162‰∏™Êï∞Â≠ó‰∏≠Á≠õÈÄâÂá∫40-60‰∏™ÊúâÊÑè‰πâÁöÑ‰∏öÂä°Â∏∏Èáè
 */

import { readFileSync } from 'node:fs';
import { resolve } from 'node:path';

interface NumberAnalysis {
  value: string;
  constantName: string;
  fileCount: number;
  category: 'business' | 'config' | 'ui' | 'time' | 'http' | 'test' | 'noise';
  priority: 'high' | 'medium' | 'low';
  reason: string;
}

/**
 * ÂàÜÊûêËøáÊª§ÂêéÁöÑÊï∞Â≠óÔºåÊåâ‰∏öÂä°‰ª∑ÂÄºÂàÜÁ±ª
 */
function analyzeFilteredNumbers(): NumberAnalysis[] {
  // ‰ªéÈ¢ÑÊ£ÄËæìÂá∫‰∏≠ÊèêÂèñÁöÑÊï∞Â≠óÁªüËÆ°ÔºàÁÆÄÂåñÁâàÔºåÂÆûÈôÖÂ∫îËØ•‰ªémapping.jsonËØªÂèñÔºâ
  const numbersFromPreflight = [
    { value: '0', name: 'ZERO', count: 171 },
    { value: '1', name: 'ONE', count: 118 },
    { value: '2', name: 'COUNT_PAIR', count: 57 },
    { value: '3', name: 'COUNT_TRIPLE', count: 38 },
    { value: '4', name: 'COUNT_QUAD', count: 17 },
    { value: '5', name: 'COUNT_FIVE', count: 47 },
    { value: '10', name: 'COUNT_TEN', count: 54 },
    { value: '24', name: 'HOURS_PER_DAY', count: 34 },
    { value: '25', name: 'PERCENTAGE_QUARTER', count: 8 },
    { value: '30', name: 'DAYS_PER_MONTH', count: 17 },
    { value: '50', name: 'PERCENTAGE_HALF', count: 36 },
    { value: '60', name: 'SECONDS_PER_MINUTE', count: 50 },
    { value: '90', name: 'ANGLE_90_DEG', count: 11 },
    { value: '100', name: 'PERCENTAGE_FULL', count: 101 },
    { value: '200', name: 'HTTP_OK', count: 25 },
    { value: '300', name: 'ANIMATION_DURATION_NORMAL', count: 20 },
    { value: '360', name: 'ANGLE_360_DEG', count: 4 },
    { value: '400', name: 'HTTP_BAD_REQUEST', count: 20 },
    { value: '401', name: 'HTTP_UNAUTHORIZED', count: 2 },
    { value: '500', name: 'ANIMATION_DURATION_SLOW', count: 39 },
    { value: '640', name: 'BREAKPOINT_SM', count: 2 },
    { value: '768', name: 'BREAKPOINT_MD', count: 4 },
    { value: '1000', name: 'ANIMATION_DURATION_VERY_SLOW', count: 93 },
    { value: '1024', name: 'BYTES_PER_KB', count: 22 },
    { value: '1280', name: 'BREAKPOINT_XL', count: 3 },
    { value: '1920', name: 'BREAKPOINT_FULL_HD', count: 2 },
    { value: '3000', name: 'THREE_SECONDS_MS', count: 13 },
    { value: '5000', name: 'FIVE_SECONDS_MS', count: 15 },
    { value: '10000', name: 'TEN_SECONDS_MS', count: 16 },
    { value: '30000', name: 'THIRTY_SECONDS_MS', count: 11 },
    { value: '60000', name: 'MINUTE_MS', count: 12 },
  ];

  const analyses: NumberAnalysis[] = [];

  for (const item of numbersFromPreflight) {
    const analysis = categorizeNumber(item.value, item.name, item.count);
    analyses.push(analysis);
  }

  return analyses.sort((a, b) => {
    // Êåâ‰ºòÂÖàÁ∫ßÂíå‰ΩøÁî®È¢ëÁéáÊéíÂ∫è
    const priorityOrder = { high: 3, medium: 2, low: 1 };
    const priorityDiff = priorityOrder[b.priority] - priorityOrder[a.priority];
    if (priorityDiff !== 0) return priorityDiff;
    return b.fileCount - a.fileCount;
  });
}

/**
 * ÂØπÂçï‰∏™Êï∞Â≠óËøõË°åÂàÜÁ±ªÂíå‰ºòÂÖàÁ∫ßËØÑ‰º∞
 */
function categorizeNumber(value: string, constantName: string, fileCount: number): NumberAnalysis {
  const num = parseFloat(value);

  // HTTPÁä∂ÊÄÅÁ†Å - È´ò‰ºòÂÖàÁ∫ß‰∏öÂä°Â∏∏Èáè
  if ([200, 201, 400, 401, 403, 404, 429, 500, 502, 503].includes(num)) {
    return {
      value,
      constantName,
      fileCount,
      category: 'http',
      priority: 'high',
      reason: 'HTTPÁä∂ÊÄÅÁ†ÅÔºåAPI‰∫§‰∫íÊ†∏ÂøÉÂ∏∏Èáè'
    };
  }

  // Êó∂Èó¥Áõ∏ÂÖ≥ - È´ò‰ºòÂÖàÁ∫ß‰∏öÂä°Â∏∏Èáè
  if ([60, 1000, 3000, 5000, 10000, 30000, 60000].includes(num)) {
    return {
      value,
      constantName,
      fileCount,
      category: 'time',
      priority: 'high',
      reason: 'Êó∂Èó¥Â∏∏ÈáèÔºåÁî®Êà∑‰ΩìÈ™åÂíåÊÄßËÉΩÁõ∏ÂÖ≥'
    };
  }

  // ÁôæÂàÜÊØî - È´ò‰ºòÂÖàÁ∫ßUIÂ∏∏Èáè
  if ([25, 50, 75, 100].includes(num)) {
    return {
      value,
      constantName,
      fileCount,
      category: 'ui',
      priority: 'high',
      reason: 'ÁôæÂàÜÊØîÂ∏∏ÈáèÔºåUIÂ∏ÉÂ±ÄÂíåÂä®ÁîªÊ†∏ÂøÉ'
    };
  }

  // ÂìçÂ∫îÂºèÊñ≠ÁÇπ - È´ò‰ºòÂÖàÁ∫ßUIÂ∏∏Èáè
  if ([640, 768, 1024, 1280, 1536, 1920].includes(num)) {
    return {
      value,
      constantName,
      fileCount,
      category: 'ui',
      priority: 'high',
      reason: 'ÂìçÂ∫îÂºèÊñ≠ÁÇπÔºåÁßªÂä®Á´ØÈÄÇÈÖçÊ†∏ÂøÉ'
    };
  }

  // Âä®ÁîªÊåÅÁª≠Êó∂Èó¥ - ‰∏≠‰ºòÂÖàÁ∫ßUIÂ∏∏Èáè
  if ([150, 250, 300, 500, 750, 1000, 1500].includes(num)) {
    return {
      value,
      constantName,
      fileCount,
      category: 'ui',
      priority: 'medium',
      reason: 'Âä®ÁîªÊåÅÁª≠Êó∂Èó¥ÔºåÁî®Êà∑‰ΩìÈ™åÁõ∏ÂÖ≥'
    };
  }

  // Âü∫Á°ÄËÆ°Êï∞ - ‰∏≠‰ºòÂÖàÁ∫ß
  if ([0, 1, 2, 3, 4, 5, 10].includes(num) && fileCount > 20) {
    return {
      value,
      constantName,
      fileCount,
      category: 'business',
      priority: 'medium',
      reason: 'È´òÈ¢ë‰ΩøÁî®ÁöÑÂü∫Á°ÄËÆ°Êï∞Ôºå‰ª£Á†ÅÂèØËØªÊÄßÈáçË¶Å'
    };
  }

  // ËßíÂ∫¶ - ‰∏≠‰ºòÂÖàÁ∫ß
  if ([45, 90, 180, 270, 360].includes(num)) {
    return {
      value,
      constantName,
      fileCount,
      category: 'ui',
      priority: 'medium',
      reason: 'ËßíÂ∫¶Â∏∏ÈáèÔºåÂõæÂΩ¢ÂíåÂä®ÁîªÁõ∏ÂÖ≥'
    };
  }

  // Êï∞ÊçÆÂ§ßÂ∞è - ‰∏≠‰ºòÂÖàÁ∫ßÈÖçÁΩÆ
  if ([256, 512, 1024, 2048, 4096, 8192].includes(num)) {
    return {
      value,
      constantName,
      fileCount,
      category: 'config',
      priority: 'medium',
      reason: 'Êï∞ÊçÆÂ§ßÂ∞èÂ∏∏ÈáèÔºåÊÄßËÉΩÂíåÂ≠òÂÇ®Áõ∏ÂÖ≥'
    };
  }

  // ÊµãËØïÊï∞ÊçÆ - ‰Ωé‰ºòÂÖàÁ∫ß
  if (constantName.includes('TEST_') || [1234, 12345, 999].includes(num)) {
    return {
      value,
      constantName,
      fileCount,
      category: 'test',
      priority: 'low',
      reason: 'ÊµãËØïÊï∞ÊçÆÔºåÂèØËÄÉËôëË±ÅÂÖç'
    };
  }

  // ÂÖ∂‰ªñ‰ΩéÈ¢ëÊï∞Â≠ó - Âô™Èü≥
  if (fileCount < 5) {
    return {
      value,
      constantName,
      fileCount,
      category: 'noise',
      priority: 'low',
      reason: '‰ΩéÈ¢ë‰ΩøÁî®ÔºåÂèØËÄÉËôëË±ÅÂÖçÊàñÂ±ÄÈÉ®Â∏∏Èáè'
    };
  }

  // ÈªòËÆ§ÂàÜÁ±ª
  return {
    value,
    constantName,
    fileCount,
    category: 'business',
    priority: 'medium',
    reason: '‰∏≠È¢ë‰∏öÂä°Êï∞Â≠óÔºåÈúÄË¶ÅËØÑ‰º∞'
  };
}

/**
 * ÁîüÊàêCODEXÊé®ËçêÁöÑÁ≤æÁÆÄÂ∏∏ÈáèÂ∫ì
 */
function generateCodexRecommendations(analyses: NumberAnalysis[]): void {
  console.log('üéØ CODEXÂàÜÂ±ÇÊ≤ªÁêÜÔºöÁ≤æÁÆÄÂ∏∏ÈáèÂ∫ìÊé®Ëçê');
