  // ‰∏≠‰ºòÂÖàÁ∫ßÂ∏∏ÈáèÔºàÈÄâÊã©ÊÄß‰øùÁïôÔºâ
  const mediumPriority = analyses.filter(a => a.priority === 'medium');
  console.log(`üü° ‰∏≠‰ºòÂÖàÁ∫ßÂ∏∏Èáè (${mediumPriority.length}‰∏™) - ÈÄâÊã©ÊÄß‰øùÁïô:`);
  mediumPriority.forEach(item => {
