  });
  console.log('');

  // ‰Ωé‰ºòÂÖàÁ∫ßÂ∏∏ÈáèÔºàÂª∫ËÆÆË±ÅÂÖçÔºâ
  const lowPriority = analyses.filter(a => a.priority === 'low');
  console.log(`üü¢ ‰Ωé‰ºòÂÖàÁ∫ßÂ∏∏Èáè (${lowPriority.length}‰∏™) - Âª∫ËÆÆË±ÅÂÖçÊàñÂ±ÄÈÉ®Â§ÑÁêÜ:`);
  lowPriority.forEach(item => {
    console.log(`  ${item.value} ‚Üí ${item.constantName} (${item.fileCount}‰∏™Êñá‰ª∂) - ${item.reason}`);
  });
  console.log('');

  // ÁªüËÆ°Âª∫ËÆÆ
  const recommended = highPriority.length + Math.ceil(mediumPriority.length * 0.6);
  console.log(`üìä CODEXÂª∫ËÆÆÔºö‰øùÁïô ${recommended} ‰∏™Â∏∏Èáè (È´ò‰ºòÂÖàÁ∫ß + 60%‰∏≠‰ºòÂÖàÁ∫ß)`);
  console.log(`üìà ‰ºòÂåñÊïàÊûúÔºö‰ªé ${analyses.length} ‰∏™ÂáèÂ∞ëÂà∞ ${recommended} ‰∏™ÔºåÂáèÂ∞ë ${Math.round((1 - recommended / analyses.length) * 100)}%`);
  console.log('');

  // ÊåâÁ±ªÂà´ÁªüËÆ°
  console.log('üìã ÊåâÁ±ªÂà´ÁªüËÆ°:');
  const categories = ['http', 'time', 'ui', 'business', 'config', 'test', 'noise'];
  categories.forEach(category => {
    const items = analyses.filter(a => a.category === category);
    if (items.length > 0) {
      console.log(`  ${category}: ${items.length}‰∏™`);
    }
  });
}

// ÊâßË°åÂàÜÊûê
if (require.main === module) {
  const analyses = analyzeFilteredNumbers();
  generateCodexRecommendations(analyses);
}

#!/usr/bin/env tsx

/**
 * CODEXÂàÜÂ±ÇÊ≤ªÁêÜÔºöÂâ©‰ΩôÊï∞Â≠óÂàÜÁ±ªÂ§ÑÁêÜÂàÜÊûê
 * 
