    const modulePath = filePath.replace(/^src\//, '@/').replace(/\.ts$/, '');

    console.log(`üîç ÂàÜÊûêÊñá‰ª∂: ${filePath}`);

    // Êü•ÊâæÂØºÂá∫ÁöÑÂèòÈáèÂ£∞Êòé
    const exportedDeclarations = sourceFile.getExportedDeclarations();

    for (const [exportName, declarations] of exportedDeclarations) {
      for (const declaration of declarations) {
        if (declaration.getKind() === ts.SyntaxKind.VariableDeclaration) {
          const varDecl = declaration as VariableDeclaration;
          const initializer = varDecl.getInitializer();

          if (initializer && ts.isNumericLiteral(initializer.compilerNode)) {
            const value = parseFloat(initializer.getText());
            const line = varDecl.getStartLineNumber();

            const constantInfo: ConstantInfo = {
              export: exportName,
              module: modulePath,
              value,
              filePath,
              line,
            };

            constants.push(constantInfo);

            if (!valueToConstants.has(value)) {
              valueToConstants.set(value, []);
            }
            valueToConstants.get(value)!.push(constantInfo);

            console.log(`  ‚úÖ ÂèëÁé∞Â∏∏Èáè: ${exportName} = ${value} (Ë°å ${line})`);
          }
        }
      }
    }

    // Êü•ÊâæÂØπË±°‰∏≠ÁöÑÊï∞Â≠óÂ∏∏Èáè
    sourceFile.forEachDescendant((node) => {
      if (ts.isPropertyAssignment(node.compilerNode)) {
        const propAssignment = node;
        const initializer = propAssignment.getInitializer();

        if (initializer && ts.isNumericLiteral(initializer.compilerNode)) {
          const value = parseFloat(initializer.getText());
          const propertyName = propAssignment.getName();

          // Â∞ùËØïÊâæÂà∞ÂåÖÂê´Ëøô‰∏™Â±ûÊÄßÁöÑÂØºÂá∫ÂØπË±°
          let parent = propAssignment.getParent();
          while (parent && !ts.isVariableDeclaration(parent.compilerNode)) {
            parent = parent.getParent();
          }

          if (parent && ts.isVariableDeclaration(parent.compilerNode)) {
            const varDecl = parent as VariableDeclaration;
            const varName = varDecl.getName();

            // Ê£ÄÊü•ÊòØÂê¶Ë¢´ÂØºÂá∫
            const exportedDeclarations = sourceFile.getExportedDeclarations();
            if (exportedDeclarations.has(varName)) {
              const line = propAssignment.getStartLineNumber();
              const exportName = `${varName}.${propertyName}`;

              const constantInfo: ConstantInfo = {
                export: exportName,
                module: modulePath,
                value,
                filePath,
                line,
              };

              constants.push(constantInfo);

              if (!valueToConstants.has(value)) {
                valueToConstants.set(value, []);
              }
              valueToConstants.get(value)!.push(constantInfo);

              console.log(`  ‚úÖ ÂèëÁé∞ÂØπË±°Â∏∏Èáè: ${exportName} = ${value} (Ë°å ${line})`);
            }
          }
        }
      }
    });
  }

  console.log(`\nüìä Êî∂ÈõÜÂÆåÊàê: ÂèëÁé∞ ${constants.length} ‰∏™Â∏∏Èáè`);

  // ÁîüÊàêÂÄôÈÄâÊò†Â∞Ñ
  const candidateMapping: Record<string, any> = {};
  const conflicts: Record<string, ConstantInfo[]> = {};

  for (const [value, constantInfos] of valueToConstants) {
    const valueStr = value.toString();

    if (constantInfos.length === 1) {
      // ÂîØ‰∏ÄÂ∏∏ÈáèÔºåÁõ¥Êé•Êò†Â∞Ñ
      const info = constantInfos[0];
      candidateMapping[valueStr] = {
        export: info.export,
        module: info.module,
        source: `${info.filePath}:${info.line}`,
      };
    } else {
      // Â§ö‰∏™Â∏∏ÈáèÔºåËÆ∞ÂΩïÂÜ≤Á™Å
      conflicts[valueStr] = constantInfos;

      // ÈÄâÊã©‰ºòÂÖàÁ∫ßÊúÄÈ´òÁöÑÔºàÊåâÊ®°Âùó‰ºòÂÖàÁ∫ßÔºâ
      const prioritized = prioritizeConstant(constantInfos);
      candidateMapping[valueStr] = {
        export: prioritized.export,
        module: prioritized.module,
        source: `${prioritized.filePath}:${prioritized.line}`,
        alternatives: constantInfos.filter(c => c !== prioritized).map(c => ({
          export: c.export,
          module: c.module,
          source: `${c.filePath}:${c.line}`,
        })),
      };
    }
  }

  // ‰øùÂ≠òÁªìÊûú
  const outputPath = 'scripts/magic-numbers/existing-constants-analysis.json';
  const result = {
    _comment: "Áé∞ÊúâÂ∏∏ÈáèÂàÜÊûêÁªìÊûú",
    _generated: new Date().toISOString(),
    _stats: {
      totalConstants: constants.length,
      uniqueValues: valueToConstants.size,
      conflicts: Object.keys(conflicts).length,
    },
    candidateMapping,
    conflicts,
    allConstants: constants,
  };

  writeFileSync(outputPath, JSON.stringify(result, null, 2));
  console.log(`\nüíæ ÁªìÊûú‰øùÂ≠òÂà∞: ${outputPath}`);

  // ÊòæÁ§∫ÂÜ≤Á™ÅÊëòË¶Å
  if (Object.keys(conflicts).length > 0) {
    console.log('\n‚ö†Ô∏è  ÂèëÁé∞ÂÜ≤Á™ÅÁöÑÊï∞ÂÄº:');
    for (const [value, infos] of Object.entries(conflicts)) {
      console.log(`  ${value}: ${infos.length} ‰∏™Â∏∏Èáè`);
      infos.forEach(info => {
        console.log(`    - ${info.export} (${info.module})`);
      });
    }
  }

  return result;
}

/**
 * Â∏∏Èáè‰ºòÂÖàÁ∫ßÁ≠ñÁï•
 * È¢ÜÂüüÂ∏∏Èáè > ÈÄöÁî®Â∏∏Èáè > magic-numbers.ts ÂÖúÂ∫ï
 */
function prioritizeConstant(constants: ConstantInfo[]): ConstantInfo {
  // ‰ºòÂÖàÁ∫ßËßÑÂàô
  const priorityOrder = [
    // È¢ÜÂüüÂ∏∏ÈáèÔºàÂÖ∑‰Ωì‰∏öÂä°Ôºâ
    /constants\/app-constants/,
    /config\/security/,
    /constants\/performance/,

    // ÈÄöÁî®Â∏∏Èáè
    /constants\//,
    /config\//,

    // ÂÖúÂ∫ï
    /magic-numbers/,
  ];

  for (const pattern of priorityOrder) {
    const match = constants.find(c => pattern.test(c.filePath));
    if (match) return match;
  }

  // Â¶ÇÊûúÊ≤°ÊúâÂåπÈÖçÔºåËøîÂõûÁ¨¨‰∏Ä‰∏™
  return constants[0];
}

// ËøêË°åÊî∂ÈõÜ
if (require.main === module) {
  collectExistingConstants().catch((error) => {
    console.error('‚ùå Êî∂ÈõÜÂ§±Ë¥•:', error);
    process.exit(1);
  });
}

export { collectExistingConstants, type ConstantInfo };

#!/usr/bin/env tsx

/**
 * ÂéªÈáçÊò†Â∞ÑÊù°ÁõÆ
 * 
 * Â§ÑÁêÜÈáçÂ§çÁöÑÂØºÂá∫Êò†Â∞ÑÔºå‰øùÁïôÊúÄÂêàÈÄÇÁöÑÊù°ÁõÆ
 */

import fs from 'fs';
import path from 'path';

interface MappingEntry {
  export: string;
  module: string;
  source?: string;
  type?: string;
  alternatives?: any[];
}

class MappingDeduplicator {
  private mappingPath: string;
  private mappingData: Record<string, MappingEntry>;

  constructor(mappingPath: string) {
    this.mappingPath = mappingPath;
    this.mappingData = this.loadMappingFile();
  }

  private loadMappingFile(): Record<string, MappingEntry> {
    const content = fs.readFileSync(this.mappingPath, 'utf-8');
    return JSON.parse(content);
  }

  private saveMappingFile(): void {
    const content = JSON.stringify(this.mappingData, null, 2);
    fs.writeFileSync(this.mappingPath, content);
  }

  /**
   * ÂéªÈáçÊò†Â∞ÑÊù°ÁõÆ
   */
  deduplicateMappings(): void {
    console.log('üîß ÂºÄÂßãÂéªÈáçÊò†Â∞ÑÊù°ÁõÆ...');

    // ÊåâÂØºÂá∫ÂêçÁß∞ÂàÜÁªÑ
    const exportGroups = new Map<string, Array<{key: string, entry: MappingEntry}>>();

    for (const [key, entry] of Object.entries(this.mappingData)) {
      if (!entry || !entry.export) continue;

      if (!exportGroups.has(entry.export)) {
        exportGroups.set(entry.export, []);
      }
      exportGroups.get(entry.export)!.push({key, entry});
    }

    let removedCount = 0;

    // Â§ÑÁêÜÈáçÂ§çÁöÑÂØºÂá∫
    for (const [exportName, entries] of exportGroups) {
      if (entries.length > 1) {
        console.log(`\nüîç Â§ÑÁêÜÈáçÂ§çÂØºÂá∫: ${exportName} (${entries.length} ‰∏™Êù°ÁõÆ)`);

        // ÈÄâÊã©ÊúÄ‰Ω≥Êù°ÁõÆ
        const bestEntry = this.selectBestEntry(entries);

        // Âà†Èô§ÂÖ∂‰ªñÊù°ÁõÆ
        for (const {key, entry} of entries) {
          if (key !== bestEntry.key) {
            console.log(`  ‚ùå Âà†Èô§ÈáçÂ§çÊù°ÁõÆ: ${key} (${entry.module})`);
            delete this.mappingData[key];
            removedCount++;
          } else {
            console.log(`  ‚úÖ ‰øùÁïôÊù°ÁõÆ: ${key} (${entry.module})`);
          }
        }
      }
    }

    this.saveMappingFile();
    console.log(`\nüìä ÂéªÈáçÂÆåÊàê: Âà†Èô§‰∫Ü ${removedCount} ‰∏™ÈáçÂ§çÊù°ÁõÆ`);
  }

  /**
   * ÈÄâÊã©ÊúÄ‰Ω≥Êò†Â∞ÑÊù°ÁõÆ
   */
  private selectBestEntry(entries: Array<{key: string, entry: MappingEntry}>): {key: string, entry: MappingEntry} {
    // ‰ºòÂÖàÁ∫ßËßÑÂàôÔºö
    // 1. ‰ºòÂÖàÈÄâÊã©ÂéüÂßãÂÆö‰πâÊ®°ÂùóÔºàÈùûmagic-numbersÔºâ
    // 2. ‰ºòÂÖàÈÄâÊã©Êõ¥ÂÖ∑‰ΩìÁöÑÊ®°ÂùóË∑ØÂæÑ
    // 3. ‰ºòÂÖàÈÄâÊã©ËæÉÂ∞èÁöÑÊï∞ÂÄºÈîÆÔºàÈÄöÂ∏∏ÊòØÂéüÂßãÊò†Â∞ÑÔºâ

    const nonMagicNumbers = entries.filter(({entry}) => 
      !entry.module.includes('magic-numbers')
    );

    if (nonMagicNumbers.length > 0) {
      // Âú®Èùûmagic-numbersÊ®°Âùó‰∏≠ÈÄâÊã©ÊúÄÂÖ∑‰ΩìÁöÑ
      return nonMagicNumbers.sort((a, b) => {
        // ‰ºòÂÖàÈÄâÊã©Êõ¥ÂÖ∑‰ΩìÁöÑÊ®°ÂùóÂêç
        const aSpecificity = a.entry.module.split('/').length;
        const bSpecificity = b.entry.module.split('/').length;
        if (aSpecificity !== bSpecificity) {
          return bSpecificity - aSpecificity;
        }

        // ÂÖ∂Ê¨°ÊåâÊï∞ÂÄºÈîÆÊéíÂ∫è
        const aNum = parseFloat(a.key);
        const bNum = parseFloat(b.key);
        if (!isNaN(aNum) && !isNaN(bNum)) {
          return aNum - bNum;
        }

        return a.key.localeCompare(b.key);
      })[0];
    }

    // Â¶ÇÊûúÈÉΩÊòØmagic-numbersÔºåÈÄâÊã©Êï∞ÂÄºÊúÄÂ∞èÁöÑ
    return entries.sort((a, b) => {
      const aNum = parseFloat(a.key);
      const bNum = parseFloat(b.key);
      if (!isNaN(aNum) && !isNaN(bNum)) {
        return aNum - bNum;
      }
      return a.key.localeCompare(b.key);
    })[0];
  }

  /**
   * ÁîüÊàêÂéªÈáçÊä•Âëä
   */
  generateReport(): string {
    const lines: string[] = [];

    lines.push('# Êò†Â∞ÑÂéªÈáçÊä•Âëä');
    lines.push('');
    lines.push(`ÁîüÊàêÊó∂Èó¥: ${new Date().toISOString()}`);
    lines.push('');

    // ÁªüËÆ°‰ø°ÊÅØ
    const totalEntries = Object.keys(this.mappingData).length;

    lines.push('## ÂéªÈáçÂêéÁªüËÆ°');
    lines.push(`- ÊÄªÊò†Â∞ÑÊù°ÁõÆ: ${totalEntries}`);
    lines.push('');

    // ÊåâÊ®°ÂùóÂàÜÁªÑÁªüËÆ°
    const moduleStats = new Map<string, number>();
    for (const entry of Object.values(this.mappingData)) {
      if (entry && entry.module) {
        moduleStats.set(entry.module, (moduleStats.get(entry.module) || 0) + 1);
      }
    }

    lines.push('## ÊåâÊ®°ÂùóÂàÜÂ∏É');
    for (const [module, count] of Array.from(moduleStats.entries()).sort()) {
      lines.push(`- ${module}: ${count} ‰∏™Êò†Â∞Ñ`);
    }
    lines.push('');

    return lines.join('\n');
  }
}

async function main() {
  const mappingPath = path.resolve(__dirname, 'enhanced-codex-mapping.json');
  const deduplicator = new MappingDeduplicator(mappingPath);

  try {
    // ÂéªÈáçÊò†Â∞Ñ
    deduplicator.deduplicateMappings();

    // ÁîüÊàêÊä•Âëä
    const report = deduplicator.generateReport();
    console.log(`\n${  report}`);

    // ‰øùÂ≠òÊä•Âëä
    const reportPath = path.resolve(__dirname, 'mapping-dedup-report.md');
    fs.writeFileSync(reportPath, report);
    console.log(`üìÑ ÂéªÈáçÊä•ÂëäÂ∑≤‰øùÂ≠òÂà∞: ${reportPath}`);

  } catch (error) {
    console.error('‚ùå ÂéªÈáçÂ§±Ë¥•:', error);
    process.exit(1);
  }
}

if (require.main === module) {
  main();
}

