#!/usr/bin/env tsx

/**
 * ‰øÆÂ§çÊò†Â∞Ñ‰∏ç‰∏ÄËá¥ÈóÆÈ¢ò
 * 
 * Ê†πÊçÆÊ†°È™åÊä•Âëä‰øÆÊ≠£Êò†Â∞ÑÊñá‰ª∂‰∏≠ÁöÑÈóÆÈ¢òÔºö
 * 1. ‰øÆÊ≠£ÂµåÂ•óÂØπË±°Â±ûÊÄßÁöÑÊò†Â∞ÑÊ†ºÂºè
 * 2. Ê∏ÖÁêÜÊó†ÊïàÁöÑÊò†Â∞ÑÊù°ÁõÆ
 * 3. Êõ¥Êñ∞Ê®°ÂùóË∑ØÂæÑ
 */

import fs from 'fs';
import path from 'path';

interface MappingEntry {
  export: string;
  module: string;
  source?: string;
  type?: string;
  alternatives?: any[];
}

class MappingFixer {
  private mappingPath: string;
  private mappingData: Record<string, MappingEntry>;

  constructor(mappingPath: string) {
    this.mappingPath = mappingPath;
    this.mappingData = this.loadMappingFile();
  }

  /**
   * Âä†ËΩΩÊò†Â∞ÑÊñá‰ª∂
   */
  private loadMappingFile(): Record<string, MappingEntry> {
    try {
      const content = fs.readFileSync(this.mappingPath, 'utf-8');
      return JSON.parse(content);
    } catch (error) {
      throw new Error(`Failed to load mapping file: ${error}`);
    }
  }

  /**
   * ‰øùÂ≠òÊò†Â∞ÑÊñá‰ª∂
   */
  private saveMappingFile(): void {
    const content = JSON.stringify(this.mappingData, null, 2);
    fs.writeFileSync(this.mappingPath, content);
  }

  /**
   * ‰øÆÂ§çÊâÄÊúâÊò†Â∞ÑÈóÆÈ¢ò
   */
  fixAllMappings(): void {
    console.log('üîß ÂºÄÂßã‰øÆÂ§çÊò†Â∞Ñ‰∏ç‰∏ÄËá¥ÈóÆÈ¢ò...');

    let fixedCount = 0;
    let removedCount = 0;

    for (const [value, entry] of Object.entries(this.mappingData)) {
      if (!entry || !entry.export || !entry.module) {
        console.log(`‚ùå Âà†Èô§Êó†ÊïàÊù°ÁõÆ: ${value}`);
        delete this.mappingData[value];
        removedCount++;
        continue;
      }

      const originalExport = entry.export;
      const fixed = this.fixSingleMapping(entry);

      if (fixed && entry.export !== originalExport) {
        console.log(`‚úÖ ‰øÆÂ§çÊò†Â∞Ñ: ${originalExport} ‚Üí ${entry.export}`);
        fixedCount++;
      }
    }

    this.saveMappingFile();
    console.log(`\nüìä ‰øÆÂ§çÂÆåÊàê:`);
    console.log(`- ‰øÆÂ§çÁöÑÊò†Â∞Ñ: ${fixedCount}`);
    console.log(`- Âà†Èô§ÁöÑÊó†ÊïàÊù°ÁõÆ: ${removedCount}`);
  }

  /**
   * ‰øÆÂ§çÂçï‰∏™Êò†Â∞ÑÊù°ÁõÆ
   */
  private fixSingleMapping(entry: MappingEntry): boolean {
    let fixed = false;

    // ‰øÆÂ§çÂµåÂ•óÂØπË±°Â±ûÊÄßÊò†Â∞Ñ
    if (entry.export.includes('.')) {
      const parts = entry.export.split('.');
      if (parts.length === 2) {
        // ÂØπ‰∫é OBJECT.PROPERTY Ê†ºÂºèÔºåÊîπ‰∏∫Âè™Êò†Â∞ÑÂà∞ OBJECT
        entry.export = parts[0];
        fixed = true;
      }
    }

    // ‰øÆÂ§çÊ®°ÂùóË∑ØÂæÑ
    const originalModule = entry.module;
    entry.module = this.fixModulePath(entry.module);
    if (entry.module !== originalModule) {
      fixed = true;
    }

    return fixed;
  }

  /**
   * ‰øÆÂ§çÊ®°ÂùóË∑ØÂæÑ
   */
  private fixModulePath(modulePath: string): string {
    // Â∞ÜÊåáÂêëmagic-numbersÁöÑË∑ØÂæÑÈáçÂÆöÂêëÂà∞Ê≠£Á°ÆÁöÑÊ®°Âùó
    if (modulePath === '@/constants/magic-numbers') {
      // Ëøô‰∫õÂ∫îËØ•Ê†πÊçÆÂÆûÈôÖÊÉÖÂÜµÈáçÂÆöÂêëÂà∞Ê≠£Á°ÆÁöÑÊ®°Âùó
      // ÊöÇÊó∂‰øùÊåÅ‰∏çÂèòÔºåËÆ©ÂêéÁª≠ÁöÑÊ†°È™åÊù•Â§ÑÁêÜ
      return modulePath;
    }

    // Ê†áÂáÜÂåñÊ®°ÂùóË∑ØÂæÑÊ†ºÂºè
    if (modulePath.startsWith('@/constants/') && !modulePath.endsWith('-constants')) {
      // Á°Æ‰øùË∑ØÂæÑÊ†ºÂºèÊ≠£Á°Æ
      return modulePath;
    }

    return modulePath;
  }

  /**
   * ÁîüÊàê‰øÆÂ§çÊä•Âëä
   */
  generateReport(): string {
    const lines: string[] = [];

    lines.push('# Êò†Â∞Ñ‰øÆÂ§çÊä•Âëä');
    lines.push('');
    lines.push(`ÁîüÊàêÊó∂Èó¥: ${new Date().toISOString()}`);
    lines.push('');

    // ÁªüËÆ°‰ø°ÊÅØ
    const totalEntries = Object.keys(this.mappingData).length;
    const validEntries = Object.values(this.mappingData).filter(entry => 
      entry && entry.export && entry.module
    ).length;

    lines.push('## ‰øÆÂ§çÂêéÁªüËÆ°');
    lines.push(`- ÊÄªÊò†Â∞ÑÊù°ÁõÆ: ${totalEntries}`);
    lines.push(`- ÊúâÊïàÊù°ÁõÆ: ${validEntries}`);
    lines.push(`- Êó†ÊïàÊù°ÁõÆ: ${totalEntries - validEntries}`);
    lines.push('');

    // ÊåâÊ®°ÂùóÂàÜÁªÑÁªüËÆ°
    const moduleStats = new Map<string, number>();
    for (const entry of Object.values(this.mappingData)) {
      if (entry && entry.module) {
        moduleStats.set(entry.module, (moduleStats.get(entry.module) || 0) + 1);
      }
    }

    lines.push('## ÊåâÊ®°ÂùóÂàÜÂ∏É');
    for (const [module, count] of Array.from(moduleStats.entries()).sort()) {
      lines.push(`- ${module}: ${count} ‰∏™Êò†Â∞Ñ`);
    }
    lines.push('');

    return lines.join('\n');
  }
}

