  }

  // Ê∑ªÂä†Êñ∞Â∏∏Èáè
  if (newConstants.length > 0) {
    constantsContent += '\n// Ëá™Âä®ÁîüÊàêÁöÑÂ∏∏Èáè\n';

    for (const { name, value } of newConstants) {
