import { Project, ts } from 'ts-morph';
import { loadMapping, shouldSkipNode, normalize } from './utils';
import { resolve } from 'node:path';

/**
 * È¢ÑÊ£ÄÈ™åËØÅËÑöÊú¨
 * Êâ´ÊèèÊâÄÊúâÂ∞ÜË¢´ÊõøÊç¢ÁöÑÊï∞Â≠óÔºåÁ°Æ‰øùÂØπÂ∫îÁöÑÂ∏∏ÈáèÈÉΩÂ∑≤ÂÆö‰πâ
 */
async function preflight() {
  console.log('üîç ASTÈ≠îÊ≥ïÊï∞Â≠óÈ¢ÑÊ£ÄÈ™åËØÅÂêØÂä®...');

  // ÂàùÂßãÂåñÈ°πÁõÆ - ‰øÆÂ§çË∑ØÂæÑÈóÆÈ¢ò
  const rootDir = process.cwd().endsWith('scripts/magic-numbers')
    ? resolve(process.cwd(), '../..')
    : process.cwd();

  const project = new Project({
    tsConfigFilePath: resolve(rootDir, 'tsconfig.json')
  });

  // Ê∑ªÂä†Ê∫êÊñá‰ª∂
  project.addSourceFilesAtPaths(resolve(rootDir, 'src/**/*.{ts,tsx}'));
  const sourceFiles = project.getSourceFiles();

  console.log(`üìä Êâ´Êèè ${sourceFiles.length} ‰∏™Êñá‰ª∂...`);

  // Âä†ËΩΩÊò†Â∞Ñ
  const mapping = loadMapping();
  if (Object.keys(mapping).length === 0) {
    console.error('‚ùå Êò†Â∞ÑÊñá‰ª∂‰∏∫Á©∫ÊàñÂä†ËΩΩÂ§±Ë¥•');
    process.exit(1);
  }

  console.log(`üìã ÂèØÁî®Â∏∏ÈáèÊò†Â∞Ñ: ${Object.keys(mapping).length} ‰∏™`);

  // Êî∂ÈõÜÊâÄÊúâÂ∞ÜË¢´‰ΩøÁî®ÁöÑÊï∞Â≠ó
  const willUse = new Set<string>();
  const fileUsage = new Map<string, Set<string>>();

  for (const sourceFile of sourceFiles) {
    const filePath = sourceFile.getFilePath().replace(`${process.cwd()  }/`, '');
    const numbersInFile = new Set<string>();

    sourceFile.forEachDescendant((node) => {
      if (ts.isNumericLiteral(node.compilerNode)) {
        // Â∫îÁî®Áõ∏ÂêåÁöÑËøáÊª§ÈÄªËæë
        if (shouldSkipNode(node)) {
          return;
        }

        const text = node.getText();
        const normalized = normalize(text);

        willUse.add(normalized);
        numbersInFile.add(normalized);
      }
    });

    if (numbersInFile.size > 0) {
      fileUsage.set(filePath, numbersInFile);
    }
  }

  console.log(`üî¢ ÂèëÁé∞ ${willUse.size} ‰∏™‰∏çÂêåÁöÑÊï∞Â≠óÂ∞ÜË¢´ÊõøÊç¢`);

  // Ê£ÄÊü•Áº∫Â§±ÁöÑÂ∏∏Èáè
  const missing = [...willUse].filter(num => !mapping[num]);

  if (missing.length > 0) {
    console.error('‚ùå Áº∫Â§±Â∏∏ÈáèÂÆö‰πâÔºåËØ∑ÂÖàË°•ÂÖÖ‰ª•‰∏ãÂ∏∏ÈáèÂà∞ mapping.json Âíå src/constants/magic-numbers.ts:');
    console.error('');

    missing.sort((a, b) => parseFloat(a) - parseFloat(b)).forEach(num => {
      console.error(`  "${num}": "MAGIC_${num.replace('.', '_').replace('-', 'NEG_')}",`);
    });

    console.error('');
    console.error('üìÅ Ê∂âÂèäÁöÑÊñá‰ª∂:');
    fileUsage.forEach((numbers, file) => {
      const missingInFile = [...numbers].filter(num => missing.includes(num));
      if (missingInFile.length > 0) {
        console.error(`  ${file}: ${missingInFile.join(', ')}`);
      }
    });

    process.exit(1);
  }

  // ÊòæÁ§∫Â∞ÜË¢´ÊõøÊç¢ÁöÑÊï∞Â≠óÁªüËÆ°
  console.log('');
  console.log('üìä Â∞ÜË¢´ÊõøÊç¢ÁöÑÊï∞Â≠óÁªüËÆ°:');
  const sortedNumbers = [...willUse].sort((a, b) => parseFloat(a) - parseFloat(b));
  sortedNumbers.forEach(num => {
    const count = [...fileUsage.values()].reduce((acc, set) => acc + (set.has(num) ? 1 : 0), 0);
    console.log(`  ${num} ‚Üí ${mapping[num]} (${count} ‰∏™Êñá‰ª∂)`);
  });

  console.log('');
  console.log('‚úÖ È¢ÑÊ£ÄÈÄöËøáÔºÅÊâÄÊúâÂ∞ÜÊõøÊç¢ÁöÑÊï∞ÂÄºÂùáÊúâÂ∏∏ÈáèÂÆö‰πâ„ÄÇ');
  console.log(`üìà ÊÄªËÆ°: ${willUse.size} ÁßçÊï∞Â≠óÔºåÂàÜÂ∏ÉÂú® ${fileUsage.size} ‰∏™Êñá‰ª∂‰∏≠`);
}

// ËøêË°åÈ¢ÑÊ£Ä
if (require.main === module) {
  preflight().catch((error) => {
    console.error('‚ùå È¢ÑÊ£ÄÂ§±Ë¥•:', error);
    process.exit(1);
  });
}

