import { run, Options } from './ast-replace-magic-numbers';
import { execSync } from 'node:child_process';

interface BatchOptions extends Options {
  batchSize?: number;
  validateAfterEach?: boolean;
}

/**
 * ÊâπÂ§ÑÁêÜÊâßË°åÂô®
 * ÊîØÊåÅÂàÜÊâπÂ§ÑÁêÜ„ÄÅËá™Âä®È™åËØÅÂíåÂ§±Ë¥•ÂõûÊªö
 */
async function runBatch() {
  const args = process.argv.slice(2);

  const options: BatchOptions = {
    write: args.includes('--write'),
    dryRun: args.includes('--dry-run'),
    files: args.find(arg => arg.startsWith('--files='))?.split('=')[1] || 'src/**/*.{ts,tsx}',
    limit: parseInt(args.find(arg => arg.startsWith('--limit='))?.split('=')[1] || '0') || undefined,
    batchSize: parseInt(args.find(arg => arg.startsWith('--batch-size='))?.split('=')[1] || '5'),
    validateAfterEach: !args.includes('--no-validate'),
  };

  // ÈªòËÆ§‰∏∫Âπ≤Ë∑ëÊ®°Âºè
  if (!options.write) {
    options.dryRun = true;
  }

  console.log('üöÄ ASTÈ≠îÊ≥ïÊï∞Â≠óÊâπÂ§ÑÁêÜÂô®ÂêØÂä®...');
  console.log(`üìä ÈÖçÁΩÆ: ${JSON.stringify(options, null, 2)}`);

  try {
    // ËøêË°åÈ¢ÑÊ£Ä
    console.log('üîç ËøêË°åÈ¢ÑÊ£ÄÈ™åËØÅ...');
    execSync('tsx scripts/magic-numbers/preflight.ts', { 
      stdio: 'inherit',
      cwd: process.cwd(),
    });
    console.log('‚úÖ È¢ÑÊ£ÄÈÄöËøá');

    // ÊâßË°åÊõøÊç¢
    console.log('üîß ÂºÄÂßãÊâßË°åÊõøÊç¢...');
    const logs = await run(options);

    if (logs.length === 0) {
      console.log('‚ÑπÔ∏è  Ê≤°ÊúâÊñá‰ª∂ÈúÄË¶Å‰øÆÊîπ');
      return;
    }

    // È™åËØÅÁªìÊûú
    if (options.validateAfterEach && options.write && !options.dryRun) {
      console.log('üîç È™åËØÅ‰øÆÊîπÁªìÊûú...');

      try {
        // TypeScript ÁºñËØëÊ£ÄÊü•
        console.log('  Ê£ÄÊü• TypeScript ÁºñËØë...');
        execSync('pnpm run type-check', { 
          stdio: 'pipe',
          cwd: process.cwd(),
        });
        console.log('  ‚úÖ TypeScript ÁºñËØëÊ≠£Â∏∏');

        // ESLint Ê£ÄÊü•È≠îÊ≥ïÊï∞Â≠ó
        console.log('  Ê£ÄÊü•È≠îÊ≥ïÊï∞Â≠óÈîôËØØ...');
        const lintOutput = execSync('pnpm run lint:check 2>&1 | grep "no-magic-numbers" | wc -l', {
          encoding: 'utf8',
          cwd: process.cwd(),
        });

        const magicNumberErrors = parseInt(lintOutput.trim());
        console.log(`  È≠îÊ≥ïÊï∞Â≠óÈîôËØØ: ${magicNumberErrors} ‰∏™`);

        if (magicNumberErrors > 0) {
          console.warn('‚ö†Ô∏è  ‰ªçÊúâÈ≠îÊ≥ïÊï∞Â≠óÈîôËØØÔºåÂèØËÉΩÈúÄË¶ÅËøõ‰∏ÄÊ≠•Â§ÑÁêÜ');
        }

      } catch (error) {
        console.error('‚ùå È™åËØÅÂ§±Ë¥•:', error);
        console.error('üí° Âª∫ËÆÆÊ£ÄÊü•ÊúÄËøëÁöÑ‰øÆÊîπÂπ∂ËÄÉËôëÂõûÊªö');
        process.exit(1);
      }
    }

    // ËæìÂá∫ÊëòË¶Å
    console.log('');
    console.log('üìä ÊâπÂ§ÑÁêÜÂÆåÊàêÊëòË¶Å:');
    console.log(`  Â§ÑÁêÜÊñá‰ª∂: ${logs.length}`);

    const totalReplacements = logs.reduce((sum, log) => sum + log.changes.length, 0);
    console.log(`  ÊÄªÊõøÊç¢Êï∞: ${totalReplacements}`);

    if (totalReplacements > 0) {
      console.log('');
      console.log('üìã ‰øÆÊîπÁöÑÊñá‰ª∂:');
      logs.forEach(log => {
        console.log(`  ${log.file}: ${log.changes.length} ‰∏™ÊõøÊç¢`);
        if (log.imports.added.length > 0) {
          console.log(`    Êñ∞Â¢ûÂØºÂÖ•: ${log.imports.added.join(', ')}`);
        }
        if (Object.keys(log.imports.aliased).length > 0) {
          console.log(`    Âà´ÂêçÂØºÂÖ•: ${Object.entries(log.imports.aliased).map(([k, v]) => `${k} as ${v}`).join(', ')}`);
        }
      });
    }

    if (options.dryRun) {
      console.log('');
      console.log('üîç ËøôÊòØÂπ≤Ë∑ëÊ®°ÂºèÔºåÊú™ÂÆûÈôÖ‰øÆÊîπÊñá‰ª∂');
      console.log('üí° ‰ΩøÁî® --write ÂèÇÊï∞ÊâßË°åÂÆûÈôÖ‰øÆÊîπ');
    }

  } catch (error) {
    console.error('‚ùå ÊâπÂ§ÑÁêÜÂ§±Ë¥•:', error);
    process.exit(1);
  }
}

