  console.log('');
  console.log(`üìÑ ËØ¶ÁªÜÊä•ÂëäÂ∑≤‰øùÂ≠òÂà∞: ${reportPath}`);

  return report;
}

/**
 * ÁîüÊàê‰ºòÂåñÂª∫ËÆÆ
 */
function generateRecommendations(stats: Record<string, number>): string[] {
  const recommendations: string[] = [];

  if (stats.TEST_PRECISION > 10) {
    recommendations.push('Âª∫ËÆÆÂ∞ÜÊµãËØïÁî®Á≤æÁ°ÆÂ∞èÊï∞ÂΩíÁ±ªÂà∞‰∏ìÈó®ÁöÑÊµãËØïÂ∏∏ÈáèÊñá‰ª∂‰∏≠');
  }

  if (stats.NUMERIC > 50) {
    recommendations.push('Â≠òÂú®Â§ßÈáèÈÄöÁî®Êï∞ÂÄºÂ∏∏ÈáèÔºåÂª∫ËÆÆËøõ‰∏ÄÊ≠•ÁªÜÂåñÂàÜÁ±ªËßÑÂàô');
  }

  if (stats.BREAKPOINT > 5) {
    recommendations.push('ÂìçÂ∫îÂºèÊñ≠ÁÇπËæÉÂ§öÔºåÂª∫ËÆÆÁªü‰∏ÄÂà∞‰∏ìÈó®ÁöÑÊñ≠ÁÇπÂ∏∏ÈáèÊñá‰ª∂‰∏≠');
  }

  return recommendations;
}

// ËøêË°åÂàÜÊûê
if (require.main === module) {
  main().catch((error) => {
    console.error('‚ùå ËØ≠‰πâÂàÜÊûêÂ§±Ë¥•:', error);
    process.exit(1);
