import { Project, ts } from 'ts-morph';
import { resolve } from 'node:path';
import { writeFileSync } from 'node:fs';
import { loadEnhancedMapping, ensureConstDefined, shouldSkipNode } from './utils';

// ÊµãËØïASTÂ∑•ÂÖ∑ÁöÑÊ†∏ÂøÉÂäüËÉΩ
async function testASTCore() {
  const log: string[] = [];
  log.push('üß™ ÊµãËØïASTÂ∑•ÂÖ∑Ê†∏ÂøÉÂäüËÉΩ...');

  // ÂàùÂßãÂåñÈ°πÁõÆ
  const rootDir = resolve(__dirname, '../..');
  const project = new Project({
    tsConfigFilePath: resolve(rootDir, 'tsconfig.json'),
  });

  // Ê∑ªÂä†ÊµãËØïÊñá‰ª∂
  const testFilePath = resolve(__dirname, 'test-sample.ts');
  log.push(`üìÑ ÊµãËØïÊñá‰ª∂: ${testFilePath}`);

  try {
    const sourceFile = project.addSourceFileAtPath(testFilePath);
    log.push(`‚úÖ ÊàêÂäüÂä†ËΩΩÊµãËØïÊñá‰ª∂`);

    // Âä†ËΩΩÊò†Â∞Ñ
    const mapping = loadEnhancedMapping();
    log.push(`üìã Âä†ËΩΩÊò†Â∞Ñ: ${Object.keys(mapping).length} ‰∏™Â∏∏Èáè`);

    // Êü•ÊâæÊï∞Â≠óÂ≠óÈù¢Èáè
    let foundNumbers = 0;
    let replacements = 0;

    sourceFile.forEachDescendant((node) => {
      if (ts.isNumericLiteral(node.compilerNode)) {
        const text = node.getText();
        foundNumbers++;

        log.push(`üîç ÂèëÁé∞Êï∞Â≠ó: ${text}`);

        // Ê£ÄÊü•ÊòØÂê¶Â∫îËØ•Ë∑≥Ëøá
        if (shouldSkipNode(node)) {
          log.push(`  ‚è≠Ô∏è Ë∑≥Ëøá: ${text}`);
          return;
        }

        const { constantName, isSupported } = ensureConstDefined(mapping, text);

        if (isSupported) {
          log.push(`  ‚úÖ ÂèØÊõøÊç¢: ${text} ‚Üí ${constantName}`);
          replacements++;
        } else {
          log.push(`  ‚ùå Êó†Êò†Â∞Ñ: ${text}`);
        }
      }
    });

    log.push(`\nüìä ÊµãËØïÁªìÊûú:`);
    log.push(`  ÂèëÁé∞Êï∞Â≠ó: ${foundNumbers}`);
    log.push(`  ÂèØÊõøÊç¢: ${replacements}`);

    // ÂÜôÂÖ•ÁªìÊûúÂà∞Êñá‰ª∂
    const resultPath = resolve(__dirname, 'test-result.txt');
    writeFileSync(resultPath, log.join('\n'));

    if (foundNumbers > 0 && replacements > 0) {
      log.push(`\nüéâ ASTÂ∑•ÂÖ∑Ê†∏ÂøÉÂäüËÉΩÊ≠£Â∏∏ÔºÅ`);
      writeFileSync(resultPath, log.join('\n'));
      return true;
    } 
      log.push(`\n‚ùå ASTÂ∑•ÂÖ∑ÂèØËÉΩÂ≠òÂú®ÈóÆÈ¢ò`);
      writeFileSync(resultPath, log.join('\n'));
      return false;


  } catch (error) {
    log.push(`‚ùå ÊµãËØïÂ§±Ë¥•: ${error}`);
    const resultPath = resolve(__dirname, 'test-result.txt');
    writeFileSync(resultPath, log.join('\n'));
    return false;
  }
}

// ËøêË°åÊµãËØï
if (require.main === module) {
  testASTCore().then(success => {
    process.exit(success ? 0 : 1);
  });
}

export { testASTCore };

#!/usr/bin/env tsx

import { resolve, join } from 'node:path';
import { readdirSync, statSync } from 'node:fs';

/**
 * ÈÄíÂΩíÊü•ÊâæÂåπÈÖçÁöÑTypeScriptÊñá‰ª∂
 */
function findTsFiles(dir: string, pattern: RegExp = /\.(ts|tsx)$/): string[] {
  const results: string[] = [];

  try {
    const items = readdirSync(dir);

    for (const item of items) {
      const fullPath = join(dir, item);
      const stat = statSync(fullPath);

      if (stat.isDirectory()) {
        // Ë∑≥Ëøánode_modulesÂíå.gitÁ≠âÁõÆÂΩï
        if (!item.startsWith('.') && item !== 'node_modules') {
          results.push(...findTsFiles(fullPath, pattern));
        }
      } else if (stat.isFile() && pattern.test(item)) {
        results.push(fullPath);
      }
    }
  } catch (error) {
    console.warn(`Êó†Ê≥ïËØªÂèñÁõÆÂΩï ${dir}:`, error);
  }

  return results;
}

// ÊµãËØïÊñá‰ª∂Êü•Êâæ
const rootDir = process.cwd().endsWith('scripts/magic-numbers')
  ? resolve(process.cwd(), '../..')
  : process.cwd();

const srcDir = resolve(rootDir, 'src');

console.log('Ê†πÁõÆÂΩï:', rootDir);
console.log('Ê∫êÁ†ÅÁõÆÂΩï:', srcDir);

const files = findTsFiles(srcDir);
console.log(`ÊâæÂà∞ ${files.length} ‰∏™TypeScriptÊñá‰ª∂`);

if (files.length > 0) {
  console.log('Ââç10‰∏™Êñá‰ª∂:');
  files.slice(0, 10).forEach((file, index) => {
    console.log(`${index + 1}. ${file.replace(rootDir, '.')}`);
  });
}

import { SourceFile, Node, ts, SyntaxKind } from 'ts-morph';
import { readFileSync } from 'node:fs';
import { resolve } from 'node:path';

/**
 * Êò†Â∞ÑÊù°ÁõÆÊé•Âè£
 */
export interface MappingEntry {
  export: string;
  module: string;
  source: string;
  type?: string;
  alternatives?: Array<{
    export: string;
    module: string;
    source: string;
  }>;
}

/**
 * Âä†ËΩΩÂ¢ûÂº∫ÁâàÊò†Â∞ÑÊñá‰ª∂
 */
export function loadEnhancedMapping(): Record<string, MappingEntry> {
  // ‰ºòÂÖà‰ΩøÁî®Â¢ûÂº∫ÁâàÊò†Â∞Ñ - ‰øÆÂ§çË∑ØÂæÑËß£Êûê
  const baseDir = __dirname.includes('scripts/magic-numbers') ? resolve(__dirname, '../..') : process.cwd();
  const enhancedMappingPath = resolve(baseDir, 'scripts/magic-numbers/enhanced-codex-mapping.json');
  const fallbackMappingPath = resolve(baseDir, 'scripts/magic-numbers/codex-mapping.json');

  try {
    let content: string;

    try {
      content = readFileSync(enhancedMappingPath, 'utf-8');
      console.log('üìã ‰ΩøÁî®Â¢ûÂº∫ÁâàCODEXÊò†Â∞Ñ');
    } catch {
      content = readFileSync(fallbackMappingPath, 'utf-8');
      console.log('üìã ‰ΩøÁî®ÂéüÂßãCODEXÊò†Â∞Ñ');
    }

    const data = JSON.parse(content);

    // ËøáÊª§ÊéâÂÖÉÊï∞ÊçÆÂ≠óÊÆµ
    const mapping: Record<string, MappingEntry> = {};
    for (const [key, value] of Object.entries(data)) {
      if (!key.startsWith('_')) {
        if (typeof value === 'string') {
          // ÂÖºÂÆπÂéüÂßãÊ†ºÂºè
          mapping[key] = {
            export: value,
            module: "@/constants/magic-numbers",
            source: "ÂéüÂßãCODEXÊò†Â∞Ñ",
            type: "codex-legacy",
          };
        } else if (typeof value === 'object' && value !== null) {
          // Â¢ûÂº∫Ê†ºÂºè
          mapping[key] = value as MappingEntry;
        }
      }
    }

    return mapping;
  } catch (error) {
    console.error('‚ùå Êó†Ê≥ïÂä†ËΩΩÊò†Â∞ÑÊñá‰ª∂:', error);
    return {};
  }
}

/**
 * ÂÖºÂÆπÊÄßÂáΩÊï∞ÔºöËé∑ÂèñÁÆÄÂçïÁöÑÂ≠óÁ¨¶‰∏≤Êò†Â∞Ñ
 */
export function loadMapping(): Record<string, string> {
  const enhancedMapping = loadEnhancedMapping();
  const simpleMapping: Record<string, string> = {};

  for (const [key, entry] of Object.entries(enhancedMapping)) {
    simpleMapping[key] = entry.export;
  }

  return simpleMapping;
}

/**
 * ËßÑËåÉÂåñÊï∞Â≠óÂ≠óÁ¨¶‰∏≤
 * Â§ÑÁêÜÁßëÂ≠¶ËÆ°Êï∞Ê≥ï„ÄÅÊï∞Â≠óÂàÜÈöîÁ¨¶Á≠â
 */
export function normalize(text: string): string {
  // ÁßªÈô§Êï∞Â≠óÂàÜÈöîÁ¨¶ (1_000 -> 1000)
  const withoutSeparators = text.replace(/_/g, '');

  // ËΩ¨Êç¢‰∏∫Êï∞Â≠óÂÜçËΩ¨ÂõûÂ≠óÁ¨¶‰∏≤‰ª•ËßÑËåÉÂåñÊ†ºÂºè
  const num = parseFloat(withoutSeparators);

  // Â§ÑÁêÜÊï¥Êï∞ÂíåÂ∞èÊï∞
  if (Number.isInteger(num)) {
    return num.toString();
  } 
    return num.toString();

}

/**
 * Á°Æ‰øùÂ∏∏ÈáèÂ∑≤ÂÆö‰πâ - ÊîØÊåÅÂ¢ûÂº∫ÁâàÊò†Â∞ÑÊ†ºÂºè
 */
export function ensureConstDefined(map: Record<string, MappingEntry | string>, text: string) {
  const normalized = normalize(text);
  const entry = map[normalized];

  if (!entry) {
    return { constantName: '', isSupported: false, module: '' };
  }

  // ÂÖºÂÆπÊóßÊ†ºÂºèÔºàÂ≠óÁ¨¶‰∏≤Ôºâ
  if (typeof entry === 'string') {
    return {
      constantName: entry,
      isSupported: true,
      module: '@/constants/magic-numbers'
    };
  }

  // Êñ∞Ê†ºÂºèÔºàMappingEntryÂØπË±°Ôºâ
  return {
    constantName: entry.export,
    isSupported: true,
    module: entry.module
  };
}

/**
 * CODEXÂàÜÂ±ÇÊ≤ªÁêÜÔºöÂà§Êñ≠ÊòØÂê¶Â∫îËØ•Ë∑≥ËøáËØ•ËäÇÁÇπ
 * Ë∑≥ËøáÁ±ªÂûãÂüü„ÄÅBigInt„ÄÅÂ≠óÁ¨¶‰∏≤/Ê®°Êùø/JSX/Ê≠£Âàô/Ê≥®Èáä„ÄÅÊµãËØïÊñá‰ª∂„ÄÅÊï∞ÊçÆÂûãÊï∞Â≠óÁ≠â
 */
export function shouldSkipNode(node: Node): boolean {
  const {compilerNode} = node;
  const sourceFile = node.getSourceFile();
  const filePath = sourceFile.getFilePath();

  // 1. Ë∑ØÂæÑËøáÊª§ÔºöË∑≥ËøáÊµãËØïÊñá‰ª∂„ÄÅÂ§πÂÖ∑„ÄÅMockÁ≠â
  if (shouldSkipByPath(filePath)) {
    return true;
  }

  // 2. Ë∑≥Ëøá BigInt Â≠óÈù¢Èáè
  if (ts.isBigIntLiteral(compilerNode)) {
    return true;
  }

  // 3. Ë∑≥ËøáÂ∏∏ÈáèÂÆö‰πâÊñá‰ª∂ÂíåÂçï‰ΩçÂ∑•ÂÖ∑Â∫ìÔºåÈÅøÂÖçÂæ™ÁéØÂºïÁî®
  if (filePath.includes('constants/magic-numbers.ts') ||
      filePath.includes('constants/count.ts') ||
      filePath.includes('constants/decimal.ts') ||
      filePath.includes('constants/hex.ts') ||
      filePath.includes('constants/time.ts') ||
      filePath.includes('lib/units.ts') ||
      filePath.includes('constants/app-constants.ts') ||
      filePath.includes('constants/performance-constants.ts') ||
      filePath.includes('constants/security-constants.ts') ||
      filePath.includes('constants/i18n-constants.ts') ||
      filePath.includes('constants/performance.ts') ||
      (filePath.includes('constants/') && filePath.includes('-constants.ts'))) {
    return true;
  }

  // 4. ËØ≠Â¢ÉËøáÊª§ÔºöË∑≥ËøáÁ±ªÂûãÂüü„ÄÅÂ≠óÁ¨¶‰∏≤Á≠â
  if (shouldSkipByContext(node)) {
    return true;
  }

  // 5. Êï∞ÂÄºÂΩ¢ÊÄÅËøáÊª§ÔºöË∑≥ËøáÊï∞ÊçÆÂûãÊï∞Â≠ó
  const text = node.getText();
  const num = parseFloat(text);
  if (shouldSkipByNumericPattern(num, text)) {
    return true;
  }

  // 6. ËØ≠‰πâÈîÆÂêçËøáÊª§ÔºöÊ†πÊçÆÂ±ûÊÄßÂêçÂà§Êñ≠ÊòØÂê¶‰∏∫ÂùêÊ†áÁ≠âÊï∞ÊçÆ
  if (shouldSkipBySemanticContext(node)) {
    return true;
  }

  // 7. ÊñπÊ≥ïË∞ÉÁî®ËØ≠Â¢ÉËøáÊª§ÔºöË∑≥ËøáÁâπÂÆöÊñπÊ≥ïË∞ÉÁî®‰∏≠ÁöÑÂèÇÊï∞
  if (shouldSkipByMethodContext(node)) {
    return true;
  }

  // 8. ËØ≠‰πâ‰∏çÂåπÈÖçËøáÊª§ÔºöÊ†πÊçÆÂèòÈáèÂêçÂíå‰∏ä‰∏ãÊñáÂà§Êñ≠ËØ≠‰πâ
  if (shouldSkipBySemanticMismatch(node)) {
    return true;
  }

  return false;
}

/**
 * Ë∑ØÂæÑËøáÊª§ÔºöË∑≥ËøáÊµãËØïÊñá‰ª∂„ÄÅÂ§πÂÖ∑„ÄÅMockÁ≠â
 */
function shouldSkipByPath(filePath: string): boolean {
  const skipPatterns = [
    // ÊµãËØïÊñá‰ª∂
    /\.test\.(js|jsx|ts|tsx)$/,
    /__tests__\//,
    /\/tests?\//,
    /\/e2e\//,

    // Â§πÂÖ∑ÂíåMock
    /__fixtures__\//,
    /\/mocks?\//,
    /\/fixtures?\//,

    // ÈÖçÁΩÆÂíåËÑöÊú¨ÔºàÈÉ®ÂàÜË±ÅÂÖçÔºâ
    /\/scripts\//,
    /\.config\.(js|ts|mjs)$/,

    // Á±ªÂûãÂÆö‰πâÊñá‰ª∂
    /\.d\.ts$/,
    /@types\//,

    // CODEXÂàÜÂ±ÇÊ≤ªÁêÜÔºöÊâ©Â±ïÊµãËØïÁõ∏ÂÖ≥Êñá‰ª∂
    /test-.*\.ts$/,
    /test-.*\.tsx$/,
    /\/test-/,
    /-test\./,
    /constants\/test-/,
    /constants.*test/,

    // ÂºÄÂèëÂ∑•ÂÖ∑ÂíåËØäÊñ≠Êñá‰ª∂
    /dev-tools/,
    /diagnostics/,
    /react-scan/,

    // ÈÖçÁΩÆÊñá‰ª∂ÔºàÂ∫îËØ•‰ΩøÁî®ÈÖçÁΩÆÈõÜ‰∏≠ÂåñÔºâ
    /config\/app\.ts$/,

    // CODEXÂàÜÂ±ÇÊ≤ªÁêÜÔºöÂÜÖÂÆπÂûãÊï∞ÊçÆÊñá‰ª∂Ë±ÅÂÖç
    /lib\/site-config\.ts$/,
    /lib\/colors\/.*\.ts$/,
    /lib\/translation-benchmarks\.ts$/,
    /lib\/.*-benchmarks\.ts$/,

    // APIË∑ØÁî±‰∏≠ÁöÑ‰∏öÂä°Êï∞ÊçÆ
    /app\/api\/.*\/route\.ts$/,

    // Á±ªÂûãÂÆö‰πâ‰∏≠ÁöÑÈÖçÁΩÆÊï∞ÊçÆ
    /types\/.*-config\/.*\.ts$/,
  ];

  return skipPatterns.some(pattern => pattern.test(filePath));
}

/**
 * ËØ≠Â¢ÉËøáÊª§ÔºöË∑≥ËøáÁ±ªÂûãÂüü„ÄÅÂ≠óÁ¨¶‰∏≤„ÄÅÊ®°Êùø„ÄÅJSXÁ≠â
 */
function shouldSkipByContext(node: Node): boolean {
  const parent = node.getParent();
  if (!parent) return true;

  const parentNode = parent.compilerNode;

  // Ë∑≥ËøáÁ±ªÂûãÂüü‰∏≠ÁöÑÂ≠óÈù¢Èáè
  if (ts.isLiteralTypeNode(parentNode)) {
    return true;
  }

  // Ë∑≥ËøáÂ≠óÁ¨¶‰∏≤Â≠óÈù¢Èáè‰∏≠ÁöÑÂÜÖÂÆπ
  if (ts.isStringLiteral(parentNode) || ts.isNoSubstitutionTemplateLiteral(parentNode)) {
    return true;
  }

  // Ë∑≥ËøáÊ®°ÊùøÂ≠óÈù¢ÈáèÁöÑÈùûË°®ËææÂºèÈÉ®ÂàÜ
  if (ts.isTemplateHead(parentNode) || ts.isTemplateMiddle(parentNode) || ts.isTemplateTail(parentNode)) {
    return true;
  }

  // Ë∑≥ËøáÊ≠£ÂàôË°®ËææÂºè
  if (ts.isRegularExpressionLiteral(parentNode)) {
    return true;
  }

  // Ë∑≥Ëøá JSX ÊñáÊú¨ÂíåÂ±ûÊÄß
  if (ts.isJsxText(parentNode) || ts.isJsxAttribute(parentNode)) {
    return true;
  }

  // Ë∑≥ËøáÊ≥®ÈáäÔºàÈÄöËøáÊ£ÄÊü•Áà∂ËäÇÁÇπÁöÑ triviaÔºâ
  const sourceFile = node.getSourceFile();
  const start = node.getStart();
  const fullStart = node.getFullStart();

  if (start !== fullStart) {
    const triviaText = sourceFile.getFullText().slice(fullStart, start);
    if (triviaText.includes('//') || triviaText.includes('/*')) {
      return true;
    }
  }

  return false;
}

/**
 * Êï∞ÂÄºÂΩ¢ÊÄÅËøáÊª§ÔºöË∑≥ËøáÊï∞ÊçÆÂûãÊï∞Â≠ó
 */
function shouldSkipByNumericPattern(num: number, text: string): boolean {
  // 1. Êó∂Èó¥Êà≥ËøáÊª§Ôºö13‰ΩçÊØ´ÁßíÊó∂Èó¥Êà≥
  if (isTimestampMs(num)) {
    return true;
  }

  // 2. ÈïøÂ∞èÊï∞ËøáÊª§ÔºöÁ≤æÂ∫¶ÊµãËØïÁî®ÁöÑÈïøÂ∞èÊï∞
  if (isLongDecimal(text)) {
    return true;
  }

  // 3. Â§ßÊï¥Êï∞ËøáÊª§ÔºöID„ÄÅËÆ°Êï∞Á≠â
  if (isHugeInteger(num)) {
    return true;
  }

  // 4. Âú∞ÁêÜÂùêÊ†áËåÉÂõ¥ËøáÊª§ (‰∏¥Êó∂Á¶ÅÁî®Áî®‰∫éÊµãËØï)
  // if (isCoordinateRange(num)) {
  //   return true;
  // }

  // 5. CODEXÂàÜÂ±ÇÊ≤ªÁêÜÔºöÂ∏∏ËßÅÊï∞Â≠óË±ÅÂÖç (‰∏¥Êó∂Á¶ÅÁî®Áî®‰∫éÊµãËØï)
  // if (isCommonNumber(num)) {
  //   return true;
  // }

  // 6. ÊµãËØïÊï∞ÊçÆËøáÊª§
  if (isTestData(num)) {
    return true;
  }

  // 7. ÈÖçÁΩÆÊï∞Â≠óËøáÊª§ÔºàÂ∫îËØ•Áî®ÈÖçÁΩÆÈõÜ‰∏≠ÂåñÔºâ
  if (isConfigNumber(num)) {
    return true;
  }

  return false;
}

/**
 * ÊñπÊ≥ïË∞ÉÁî®ËØ≠Â¢ÉËøáÊª§ÔºöË∑≥ËøáÁâπÂÆöÊñπÊ≥ïË∞ÉÁî®‰∏≠ÁöÑÂèÇÊï∞
 */
function shouldSkipByMethodContext(node: Node): boolean {
  const parent = node.getParent();
  if (!parent) return false;

  // Ê£ÄÊü•ÊòØÂê¶Âú®ÊñπÊ≥ïË∞ÉÁî®‰∏≠
  let current = parent;
  while (current && !ts.isCallExpression(current.compilerNode)) {
    current = current.getParent();
    if (!current) return false;
  }

  if (!current || !ts.isCallExpression(current.compilerNode)) {
    return false;
  }

  const callExpression = current.compilerNode as ts.CallExpression;
  const callText = current.getText();

  // Ë∑≥ËøáËøõÂà∂ËΩ¨Êç¢ÊñπÊ≥ï
  if (callText.includes('.toString(') && node.getText() === '36') {
    return true;
  }

  // Ë∑≥ËøáÊï∞ÁªÑÂàáÁâáÊñπÊ≥ï
  if (callText.includes('.slice(') && (node.getText() === '500' || node.getText() === '-500')) {
    return true;
  }

  // Ë∑≥ËøáÊï∞Â≠¶ËøêÁÆóÊñπÊ≥ï
  if (callText.includes('Math.') && ['36', '16', '10', '2'].includes(node.getText())) {
    return true;
  }

  // Ë∑≥ËøáÊó∂Èó¥Êà≥ÂíåÈöèÊú∫Êï∞ÁîüÊàê
  if (callText.includes('Date.now()') || callText.includes('Math.random()')) {
    return true;
  }

  return false;
}

/**
 * ËØ≠‰πâ‰∏çÂåπÈÖçËøáÊª§ÔºöÊ†πÊçÆÂèòÈáèÂêçÂíå‰∏ä‰∏ãÊñáÂà§Êñ≠ËØ≠‰πâÊòØÂê¶ÂåπÈÖç
 */
function shouldSkipBySemanticMismatch(node: Node): boolean {
  const value = node.getText();
  const parent = node.getParent();
  if (!parent) return false;

  // Ëé∑Âèñ‰∏ä‰∏ãÊñá‰ø°ÊÅØ
  const contextText = parent.getText().toLowerCase();

  // HTTPÁä∂ÊÄÅÁ†ÅËØ≠‰πâÊ£ÄÊü•
  if (['200', '400', '401', '404', '500'].includes(value)) {
    // Â¶ÇÊûú‰∏ä‰∏ãÊñáÂåÖÂê´Âä®Áîª„ÄÅÊåÅÁª≠Êó∂Èó¥„ÄÅÂª∂ËøüÁ≠âÂÖ≥ÈîÆËØçÔºåË∑≥ËøáHTTPÁä∂ÊÄÅÁ†ÅÊò†Â∞Ñ
    const animationKeywords = [
      'animation', 'duration', 'delay', 'timeout', 'interval',
      'transition', 'easing', 'timing', 'debounce', 'throttle'
    ];

    if (animationKeywords.some(keyword => contextText.includes(keyword))) {
      return true;
    }
  }

  // Âä®ÁîªÊåÅÁª≠Êó∂Èó¥ËØ≠‰πâÊ£ÄÊü•
  if (['300', '500', '1000'].includes(value)) {
    // Â¶ÇÊûú‰∏ä‰∏ãÊñáÂåÖÂê´HTTP„ÄÅÁä∂ÊÄÅ„ÄÅÂìçÂ∫îÁ≠âÂÖ≥ÈîÆËØçÔºåË∑≥ËøáÂä®ÁîªÊåÅÁª≠Êó∂Èó¥Êò†Â∞Ñ
    const httpKeywords = [
      'http', 'status', 'response', 'request', 'api', 'error',
      'success', 'fail', 'code', 'result'
    ];

    if (httpKeywords.some(keyword => contextText.includes(keyword))) {
      return true;
    }
  }

  // Â∞∫ÂØ∏ÂíåÂÉèÁ¥†ÂÄºËØ≠‰πâÊ£ÄÊü•
  if (['640', '768', '1024', '1280', '1920'].includes(value)) {
    // Â¶ÇÊûú‰∏ä‰∏ãÊñá‰∏çÂåÖÂê´Â±èÂπï„ÄÅÂÆΩÂ∫¶„ÄÅÊñ≠ÁÇπÁ≠âÂÖ≥ÈîÆËØçÔºåÂèØËÉΩÊòØÂÖ∂‰ªñÁî®ÈÄî
    const sizeKeywords = [
      'width', 'height', 'screen', 'breakpoint', 'viewport',
      'resolution', 'size', 'dimension'
    ];

    if (!sizeKeywords.some(keyword => contextText.includes(keyword))) {
      // Ëøõ‰∏ÄÊ≠•Ê£ÄÊü•ÊòØÂê¶‰∏∫Êó∂Èó¥Êà≥ÊàñÂÖ∂‰ªñÊï∞ÊçÆ
      if (contextText.includes('timestamp') || contextText.includes('time') ||
          contextText.includes('date') || contextText.includes('id')) {
        return true;
      }
    }
  }

  return false;
}

/**
 * ËØ≠‰πâÈîÆÂêçËøáÊª§ÔºöÊ†πÊçÆÂ±ûÊÄßÂêçÂà§Êñ≠ÊòØÂê¶‰∏∫ÂùêÊ†áÁ≠âÊï∞ÊçÆ
 */
function shouldSkipBySemanticContext(node: Node): boolean {
  // Êü•ÊâæÁà∂Á∫ßÂ±ûÊÄßÂêçÊàñÂèòÈáèÂêç
  let current = node.getParent();
  let depth = 0;
  const maxDepth = 3; // ÊúÄÂ§öÂêë‰∏äÊü•Êâæ3Â±Ç

  while (current && depth < maxDepth) {
    const currentNode = current.compilerNode;

    // Ê£ÄÊü•Â±ûÊÄßËµãÂÄºÔºö{ lat: 39.9042 }
    if (ts.isPropertyAssignment(currentNode)) {
      const propertyName = currentNode.name;
      if (ts.isIdentifier(propertyName) || ts.isStringLiteral(propertyName)) {
        const name = propertyName.getText().toLowerCase();
        if (isCoordinatePropertyName(name)) {
          return true;
        }
      }
    }

    // Ê£ÄÊü•ÂèòÈáèÂ£∞ÊòéÔºöconst lat = 39.9042
    if (ts.isVariableDeclaration(currentNode)) {
      const {name} = currentNode;
      if (ts.isIdentifier(name)) {
        const varName = name.getText().toLowerCase();
        if (isCoordinatePropertyName(varName)) {
          return true;
        }
      }
    }

    current = current.getParent();
    depth++;
  }

  return false;
}

// ============================================================================
// ËæÖÂä©ÂáΩÊï∞
// ============================================================================

/**
 * Âà§Êñ≠ÊòØÂê¶‰∏∫Êó∂Èó¥Êà≥Ôºà13‰ΩçÊØ´ÁßíÔºâ
 */
function isTimestampMs(num: number): boolean {
  return num >= 1_000_000_000_000 && num < 2_000_000_000_000;
}

/**
 * Âà§Êñ≠ÊòØÂê¶‰∏∫ÈïøÂ∞èÊï∞ÔºàÁ≤æÂ∫¶ÊµãËØïÁî®Ôºâ
 */
function isLongDecimal(text: string): boolean {
  return /\.\d{4,}$/.test(text);
}

/**
 * Âà§Êñ≠ÊòØÂê¶‰∏∫Â§ßÊï¥Êï∞ÔºàID„ÄÅËÆ°Êï∞Á≠âÔºâ
 */
function isHugeInteger(num: number): boolean {
  return Number.isInteger(num) && Math.abs(num) >= 1_000_000;
}

/**
 * Âà§Êñ≠ÊòØÂê¶Âú®Âú∞ÁêÜÂùêÊ†áËåÉÂõ¥ÂÜÖ
 */
function isCoordinateRange(num: number): boolean {
  // Á∫¨Â∫¶ËåÉÂõ¥ -90 Âà∞ 90ÔºåÁªèÂ∫¶ËåÉÂõ¥ -180 Âà∞ 180
  return (num >= -180 && num <= 180) && (num % 1 !== 0); // ÊúâÂ∞èÊï∞ÈÉ®ÂàÜ
}

/**
 * Âà§Êñ≠Â±ûÊÄßÂêçÊòØÂê¶‰∏∫ÂùêÊ†áÁõ∏ÂÖ≥
 */
function isCoordinatePropertyName(name: string): boolean {
  const coordinatePatterns = [
    /^(lat|latitude)$/i,
    /^(lng|lon|longitude)$/i,
    /^(x|y)$/i,
    /coord/i,
    /position/i,
    /location/i,
  ];

  return coordinatePatterns.some(pattern => pattern.test(name));
}

/**
 * CODEXÂàÜÂ±ÇÊ≤ªÁêÜÔºöÂà§Êñ≠ÊòØÂê¶‰∏∫Â∏∏ËßÅÊï∞Â≠óÔºàÂ∫îËØ•Ë±ÅÂÖçÔºâ
 */
function isCommonNumber(num: number): boolean {
  // Âü∫Á°ÄÊï∞Â≠ó
  if (num >= 0 && num <= 10) return true;

  // Â∏∏ËßÅÂ∞èÊï∞Â≠ó
  if ([12, 15, 16, 20, 24, 25, 30, 32, 40, 42, 45, 49, 50].includes(num)) return true;

  // ÁôæÂàÜÊØîÁõ∏ÂÖ≥
  if ([60, 65, 70, 75, 80, 85, 90, 95, 99, 100].includes(num)) return true;

  // Â∞∫ÂØ∏ÂíåÂÉèÁ¥†
  if ([120, 128, 150, 160, 190, 250, 256, 300, 360, 365].includes(num)) return true;

  // Êï∞ÊçÆÂ§ßÂ∞è
  if ([512, 640, 700, 750, 768, 800, 900].includes(num)) return true;

  // Â§ßÊï∞Â≠óÂíåÊó∂Èó¥
  if ([1000, 1024, 1200, 1280, 1500, 1536, 1600, 1800, 1920].includes(num)) return true;
  if ([2000, 2048, 2500, 3000, 4000, 4096, 5000, 6000, 7000, 8000, 8192, 9000].includes(num)) return true;
  if ([10000, 12000, 15000, 30000, 45000, 50000, 60000, 65536].includes(num)) return true;
  if ([100000, 120000, 125000, 170000, 200000, 300000, 500000].includes(num)) return true;

  return false;
}

/**
 * Âà§Êñ≠ÊòØÂê¶‰∏∫ÊµãËØïÊï∞ÊçÆ
 */
function isTestData(num: number): boolean {
  return [42, 999, 1234, 12345, 996, 997, 998].includes(num) ||
         (num >= 131000 && num <= 131100); // WhatsAppÈîôËØØÁ†ÅËåÉÂõ¥
}

/**
 * Âà§Êñ≠ÊòØÂê¶‰∏∫ÈÖçÁΩÆÊï∞Â≠óÔºàÁ´ØÂè£„ÄÅË∂ÖÊó∂Á≠âÔºâ
 */
function isConfigNumber(num: number): boolean {
  // Á´ØÂè£Âè∑
  if ([8888, 8900].includes(num)) return true;

  // HTTPÁä∂ÊÄÅÁ†ÅÔºà‰ΩéÈ¢ëÔºâ
  if ([403, 429, 503].includes(num)) return true;

  return false;
}

/**
 * Ê®°ÂùóÂåñÂØºÂÖ•Â§ÑÁêÜ - ÊîØÊåÅÂ§öÊ®°ÂùóÂØºÂÖ•
 * Ê†πÊçÆÊò†Â∞Ñ‰ø°ÊÅØÂ∞ÜÂ∏∏ÈáèÂØºÂÖ•Âà∞ÂØπÂ∫îÁöÑÊ®°Âùó
 */
export function mergeAndAliasImports(
  sourceFile: SourceFile,
  constantsWithModules: Array<{ constant: string; module: string }>,
  log: { imports: { added: string[]; aliased: Record<string, string> } }
): void {
  if (constantsWithModules.length === 0) return;

  // ÊåâÊ®°ÂùóÂàÜÁªÑÂ∏∏Èáè
  const moduleGroups = new Map<string, string[]>();
  constantsWithModules.forEach(({ constant, module }) => {
    if (!moduleGroups.has(module)) {
      moduleGroups.set(module, []);
    }
    moduleGroups.get(module)!.push(constant);
  });

  // Â§ÑÁêÜÊØè‰∏™Ê®°ÂùóÁöÑÂØºÂÖ•
  moduleGroups.forEach((constants, module) => {
    processModuleImports(sourceFile, module, constants, log);
  });
}

/**
 * Â§ÑÁêÜÂçï‰∏™Ê®°ÂùóÁöÑÂØºÂÖ•
 */
function processModuleImports(
  sourceFile: SourceFile,
  module: string,
  newConstants: string[],
  log: { imports: { added: string[]; aliased: Record<string, string> } }
): void {

  // Ëé∑ÂèñÁé∞ÊúâÁöÑÂØºÂÖ•Â£∞Êòé
  const existingImports = sourceFile.getImportDeclarations()
    .filter(imp => imp.getModuleSpecifierValue() === module);

  // Êî∂ÈõÜÁé∞ÊúâÁöÑÂØºÂÖ•ÂêçÁß∞
  const existingNames = new Set<string>();
  existingImports.forEach(imp => {
    const namedImports = imp.getNamedImports();
    namedImports.forEach(namedImport => {
      existingNames.add(namedImport.getName());
    });
  });

  // Ê£ÄÊü•Êú¨Âú∞‰ΩúÁî®Âüü‰∏≠ÁöÑÊ†áËØÜÁ¨¶ÂÜ≤Á™ÅÔºàÊéíÈô§Êàë‰ª¨ÂàöÂàöÊ∑ªÂä†ÁöÑÂ∏∏ÈáèÔºâ
  const localIdentifiers = new Set<string>();
  const newConstantNames = new Set(newConstants);

  sourceFile.getDescendantsOfKind(SyntaxKind.Identifier).forEach(identifier => {
    const identifierText = identifier.getText();
    // ÊéíÈô§Êàë‰ª¨ÂàöÂàöÊ∑ªÂä†ÁöÑÂ∏∏ÈáèÂêçÔºåÈÅøÂÖçËØØÂà§‰∏∫ÂÜ≤Á™Å
    if (!newConstantNames.has(identifierText)) {
      localIdentifiers.add(identifierText);
    }
  });



  // Â§ÑÁêÜÊñ∞Â∏∏ÈáèÔºåÁîüÊàêÂà´ÂêçÂ¶ÇÊûúÈúÄË¶Å
  const finalImports: string[] = [];
  const aliasMap: Record<string, string> = {};

  [...existingNames, ...newConstants].forEach(constName => {
    if (localIdentifiers.has(constName) && !existingNames.has(constName)) {
      // ÈúÄË¶ÅÂà´Âêç
      const aliasName = `${constName}_CONST`;
      finalImports.push(`${constName} as ${aliasName}`);
      aliasMap[constName] = aliasName;
      log.imports.aliased[constName] = aliasName;
    } else {
      finalImports.push(constName);
      if (!existingNames.has(constName)) {
        log.imports.added.push(constName);
      }
    }
  });

  // ÁßªÈô§ÊâÄÊúâÁé∞ÊúâÁöÑÂØºÂÖ•
  existingImports.forEach(imp => imp.remove());

  // Ê∑ªÂä†Êñ∞ÁöÑÂêàÂπ∂ÂØºÂÖ•
  if (finalImports.length > 0) {
    const sortedImports = [...new Set(finalImports)].sort();
    sourceFile.addImportDeclaration({
      moduleSpecifier: module,
      namedImports: sortedImports,
    });
  }

  // Â¶ÇÊûúÊúâÂà´ÂêçÔºåÈúÄË¶ÅÊõøÊç¢ÂØπÂ∫îÁöÑÂºïÁî®
  Object.entries(aliasMap).forEach(([original, alias]) => {
    sourceFile.getDescendantsOfKind(SyntaxKind.Identifier)
      .filter(identifier => identifier.getText() === original)
      .forEach(identifier => {
        identifier.replaceWithText(alias);
      });
  });

  // ÁªÑÁªáÂØºÂÖ•
  sourceFile.organizeImports();
}

#!/usr/bin/env tsx

/**
 * Êò†Â∞Ñ‰∏ÄËá¥ÊÄßÊ†°È™åËÑöÊú¨
 *
 * ÊåâÁÖßCODEXÂª∫ËÆÆÔºåÈ™åËØÅenhanced-codex-mapping.json‰∏≠ÊØè‰∏™Êò†Â∞ÑÊù°ÁõÆ
 * ‰∏éÂÆûÈôÖÊ®°ÂùóÂØºÂá∫ÁöÑ‰∏ÄËá¥ÊÄßÔºåÁîüÊàêÁº∫Â§±/ÂÜ≤Á™ÅÊä•Âëä
 */

import { Project, SourceFile } from 'ts-morph';
import fs from 'fs';
import path from 'path';

interface MappingEntry {
  export: string;
  module: string;
  source?: string;
  type?: string;
}

interface ValidationResult {
  valid: MappingEntry[];
  missing: Array<{ entry: MappingEntry; reason: string }>;
  conflicts: Array<{ entry: MappingEntry; reason: string }>;
  duplicates: Array<{ export: string; modules: string[] }>;
}

class MappingValidator {
  private project: Project;
  private srcPath: string;

  constructor() {
    // Á°Æ‰øù‰ªéÈ°πÁõÆÊ†πÁõÆÂΩïËøêË°å
    const projectRoot = path.resolve(__dirname, '../..');
    this.project = new Project({
      tsConfigFilePath: path.resolve(projectRoot, 'tsconfig.json'),
    });
    this.srcPath = path.resolve(projectRoot, 'src');
  }

  /**
   * È™åËØÅÊò†Â∞ÑÊñá‰ª∂ÁöÑ‰∏ÄËá¥ÊÄß
   */
  async validateMapping(mappingPath: string): Promise<ValidationResult> {
    const mappingData = this.loadMappingFile(mappingPath);
    const result: ValidationResult = {
      valid: [],
      missing: [],
      conflicts: [],
      duplicates: []
    };

    // Ê£ÄÊü•ÈáçÂ§çÂØºÂá∫
    this.checkDuplicateExports(mappingData, result);

    // È™åËØÅÊØè‰∏™Êò†Â∞ÑÊù°ÁõÆ
    for (const [value, entry] of Object.entries(mappingData)) {
      await this.validateEntry(value, entry, result);
    }

    return result;
  }

  /**
   * Âä†ËΩΩÊò†Â∞ÑÊñá‰ª∂
   */
  private loadMappingFile(mappingPath: string): Record<string, MappingEntry> {
    try {
      const content = fs.readFileSync(mappingPath, 'utf-8');
      return JSON.parse(content);
    } catch (error) {
      throw new Error(`Failed to load mapping file: ${error}`);
    }
  }

  /**
   * Ê£ÄÊü•ÈáçÂ§çÂØºÂá∫
   */
  private checkDuplicateExports(mappingData: Record<string, MappingEntry>, result: ValidationResult) {
    const exportMap = new Map<string, string[]>();

    for (const entry of Object.values(mappingData)) {
      const exportName = entry.export;
      if (!exportMap.has(exportName)) {
        exportMap.set(exportName, []);
      }
      exportMap.get(exportName)!.push(entry.module);
    }

    for (const [exportName, modules] of exportMap) {
      if (modules.length > 1) {
        result.duplicates.push({ export: exportName, modules });
      }
    }
  }

  /**
   * È™åËØÅÂçï‰∏™Êò†Â∞ÑÊù°ÁõÆ
   */
  private async validateEntry(value: string, entry: MappingEntry, result: ValidationResult) {
    try {
      // Ê£ÄÊü•Êù°ÁõÆÊòØÂê¶ÊúâÊïà
      if (!entry || !entry.export || !entry.module) {
        result.conflicts.push({
          entry,
          reason: `Invalid mapping entry: missing export or module field`
        });
        return;
      }

      const modulePath = this.resolveModulePath(entry.module);
      const sourceFile = this.project.getSourceFile(modulePath);

      if (!sourceFile) {
        result.missing.push({
          entry,
          reason: `Module file not found: ${modulePath}`
        });
        return;
      }

      const hasExport = this.checkExportExists(sourceFile, entry.export);
      if (!hasExport) {
        result.missing.push({
          entry,
          reason: `Export '${entry.export}' not found in module ${entry.module}`
        });
        return;
      }

      result.valid.push(entry);
    } catch (error) {
      result.conflicts.push({
        entry,
        reason: `Validation error: ${error}`
      });
    }
  }

  /**
   * Ëß£ÊûêÊ®°ÂùóË∑ØÂæÑ
   */
  private resolveModulePath(moduleSpecifier: string): string {
    // Â§ÑÁêÜ @/ Âà´Âêç
    if (moduleSpecifier.startsWith('@/')) {
      return path.resolve(this.srcPath, `${moduleSpecifier.slice(2)  }.ts`);
    }

    // Â§ÑÁêÜÁõ∏ÂØπË∑ØÂæÑ
    if (moduleSpecifier.startsWith('./')) {
      return path.resolve(this.srcPath, 'constants', `${moduleSpecifier.slice(2)  }.ts`);
    }

    // Â§ÑÁêÜÁªùÂØπË∑ØÂæÑ
    return path.resolve(this.srcPath, `${moduleSpecifier  }.ts`);
  }

  /**
   * Ê£ÄÊü•ÂØºÂá∫ÊòØÂê¶Â≠òÂú®
   */
  private checkExportExists(sourceFile: SourceFile, exportName: string): boolean {
    // Ê£ÄÊü•ÂëΩÂêçÂØºÂá∫
    const namedExports = sourceFile.getExportedDeclarations();
    if (namedExports.has(exportName)) {
      return true;
    }

    // Ê£ÄÊü• export * ÈáçÊñ∞ÂØºÂá∫
    const exportDeclarations = sourceFile.getExportDeclarations();
    for (const exportDecl of exportDeclarations) {
      if (exportDecl.isNamespaceExport()) {
        // ËøôÊòØ export * from '...' ÁöÑÊÉÖÂÜµ
        const moduleSpecifier = exportDecl.getModuleSpecifierValue();
        if (moduleSpecifier) {
          const reexportedFile = this.resolveReexportedFile(sourceFile, moduleSpecifier);
          if (reexportedFile && this.checkExportExists(reexportedFile, exportName)) {
            return true;
          }
        }
      }
    }

    return false;
  }

  /**
   * Ëß£ÊûêÈáçÊñ∞ÂØºÂá∫ÁöÑÊñá‰ª∂
   */
  private resolveReexportedFile(sourceFile: SourceFile, moduleSpecifier: string): SourceFile | undefined {
    const sourceDir = path.dirname(sourceFile.getFilePath());
    let resolvedPath: string;

    if (moduleSpecifier.startsWith('./') || moduleSpecifier.startsWith('../')) {
      resolvedPath = path.resolve(sourceDir, `${moduleSpecifier  }.ts`);
    } else if (moduleSpecifier.startsWith('@/')) {
      resolvedPath = path.resolve(this.srcPath, `${moduleSpecifier.slice(2)  }.ts`);
    } else {
      resolvedPath = path.resolve(sourceDir, `${moduleSpecifier  }.ts`);
    }

    return this.project.getSourceFile(resolvedPath);
  }

  /**
   * ÁîüÊàêÈ™åËØÅÊä•Âëä
   */
  generateReport(result: ValidationResult): string {
    const lines: string[] = [];

    lines.push('# Êò†Â∞Ñ‰∏ÄËá¥ÊÄßÊ†°È™åÊä•Âëä');
    lines.push('');
    lines.push(`ÁîüÊàêÊó∂Èó¥: ${new Date().toISOString()}`);
    lines.push('');

    // ÁªüËÆ°‰ø°ÊÅØ
    lines.push('## ÁªüËÆ°‰ø°ÊÅØ');
    lines.push(`- ÊúâÊïàÊò†Â∞Ñ: ${result.valid.length}`);
    lines.push(`- Áº∫Â§±ÂØºÂá∫: ${result.missing.length}`);
    lines.push(`- ÂÜ≤Á™ÅÈîôËØØ: ${result.conflicts.length}`);
    lines.push(`- ÈáçÂ§çÂØºÂá∫: ${result.duplicates.length}`);
    lines.push('');

    // Áº∫Â§±ÂØºÂá∫
    if (result.missing.length > 0) {
      lines.push('## Áº∫Â§±ÂØºÂá∫');
      for (const item of result.missing) {
        lines.push(`- **${item.entry.export}** (${item.entry.module}): ${item.reason}`);
      }
      lines.push('');
    }

    // ÂÜ≤Á™ÅÈîôËØØ
    if (result.conflicts.length > 0) {
      lines.push('## ÂÜ≤Á™ÅÈîôËØØ');
      for (const item of result.conflicts) {
        lines.push(`- **${item.entry.export}** (${item.entry.module}): ${item.reason}`);
      }
      lines.push('');
    }

    // ÈáçÂ§çÂØºÂá∫
    if (result.duplicates.length > 0) {
      lines.push('## ÈáçÂ§çÂØºÂá∫');
      for (const item of result.duplicates) {
        lines.push(`- **${item.export}**: ${item.modules.join(', ')}`);
      }
      lines.push('');
    }

    return lines.join('\n');
  }
}

async function main() {
  const mappingPath = path.resolve(__dirname, 'enhanced-codex-mapping.json');
  const validator = new MappingValidator();

  console.log('üîç ÂºÄÂßãÈ™åËØÅÊò†Â∞Ñ‰∏ÄËá¥ÊÄß...');

  try {
    const result = await validator.validateMapping(mappingPath);
    const report = validator.generateReport(result);

    // ËæìÂá∫Êä•ÂëäÂà∞ÊéßÂà∂Âè∞
    console.log(report);

    // ‰øùÂ≠òÊä•ÂëäÂà∞Êñá‰ª∂
    const reportPath = path.resolve(__dirname, 'mapping-validation-report.md');
    fs.writeFileSync(reportPath, report);
    console.log(`\nüìÑ Êä•ÂëäÂ∑≤‰øùÂ≠òÂà∞: ${reportPath}`);

    // ËøîÂõûÈÄÇÂΩìÁöÑÈÄÄÂá∫Á†Å
    const hasErrors = result.missing.length > 0 || result.conflicts.length > 0;
    process.exit(hasErrors ? 1 : 0);

  } catch (error) {
    console.error('‚ùå È™åËØÅÂ§±Ë¥•:', error);
    process.exit(1);
  }
}

if (require.main === module) {
  main();
}

