  /**
   * ÂàÜÊûêÊâìÂåÖÊñá‰ª∂Â§ßÂ∞è
   */
  analyzeBundleSize() {
    try {
      console.log('üì¶ ÂàÜÊûêÊâìÂåÖÊñá‰ª∂Â§ßÂ∞è...');

      const nextDir = path.join(process.cwd(), '.next');
      if (!fs.existsSync(nextDir)) {
        throw new Error('Êú™ÊâæÂà∞ÊûÑÂª∫Êñá‰ª∂ÔºåËØ∑ÂÖàËøêË°å pnpm build');
      }

      // ÂàÜÊûêÈùôÊÄÅÊñá‰ª∂Â§ßÂ∞è
      const staticDir = path.join(nextDir, 'static');
      if (fs.existsSync(staticDir)) {
        const chunks = this.getChunkSizes(staticDir);
        this.report.performance.bundleSize = chunks;

        // Ê£ÄÊü•‰∏ªÂåÖÂ§ßÂ∞è
        const mainBundleSize = chunks.main || 0;
        if (mainBundleSize > this.report.limits.mainBundle) {
          this.report.passed = false;
          this.report.issues.push({
            type: 'bundle-size',
            severity: 'high',
            message: `‰∏ªÂåÖÂ§ßÂ∞è ${this.formatBytes(mainBundleSize)} Ë∂ÖÂá∫ÈôêÂà∂ ${this.formatBytes(this.report.limits.mainBundle)}`,
          });
        }

        // Ê£ÄÊü•ÊÄªÂåÖÂ§ßÂ∞è
        const totalSize = Object.values(chunks).reduce(
          (sum, size) => sum + size,
          0,
        );
        if (totalSize > this.report.limits.totalBundle) {
          this.report.passed = false;
          this.report.issues.push({
            type: 'bundle-size',
            severity: 'medium',
            message: `ÊÄªÂåÖÂ§ßÂ∞è ${this.formatBytes(totalSize)} Ë∂ÖÂá∫ÈôêÂà∂ ${this.formatBytes(this.report.limits.totalBundle)}`,
          });
        }

        console.log(`‚úÖ ‰∏ªÂåÖÂ§ßÂ∞è: ${this.formatBytes(mainBundleSize)}`);
        console.log(`‚úÖ ÊÄªÂåÖÂ§ßÂ∞è: ${this.formatBytes(totalSize)}`);
      }
    } catch (error) {
      console.warn(`‚ö†Ô∏è  ÊâìÂåÖÂ§ßÂ∞èÂàÜÊûêÂ§±Ë¥•: ${error.message}`);
      this.report.issues.push({
        type: 'analysis-error',
        severity: 'low',
        message: `ÊâìÂåÖÂ§ßÂ∞èÂàÜÊûêÂ§±Ë¥•: ${error.message}`,
      });
    }
  }

  /**
   * Ëé∑ÂèñchunkÊñá‰ª∂Â§ßÂ∞è
   */
  getChunkSizes(staticDir) {
    const chunks = {};

    try {
      const jsDir = path.join(staticDir, 'chunks');
      if (fs.existsSync(jsDir)) {
        const files = fs.readdirSync(jsDir);

        files.forEach((file) => {
          if (file.endsWith('.js')) {
            const filePath = path.join(jsDir, file);
            const stats = fs.statSync(filePath);

            if (file.includes('main')) {
              chunks.main = (chunks.main || 0) + stats.size;
            } else if (file.includes('framework')) {
              chunks.framework = (chunks.framework || 0) + stats.size;
            } else {
              chunks.shared = (chunks.shared || 0) + stats.size;
            }
          }
        });
      }
    } catch (error) {
      console.warn(`‚ö†Ô∏è  ËØªÂèñchunkÊñá‰ª∂Â§±Ë¥•: ${error.message}`);
    }

    return chunks;
  }

  /**
   * Ê®°ÊãüÈ°µÈù¢Âä†ËΩΩÊó∂Èó¥ÂàÜÊûê
   */
  analyzeLoadTime() {
    try {
      console.log('‚è±Ô∏è  ÂàÜÊûêÈ°µÈù¢Âä†ËΩΩÊó∂Èó¥...');

      // ÁÆÄÂçïÁöÑÂä†ËΩΩÊó∂Èó¥‰º∞ÁÆóÔºàÂü∫‰∫éÂåÖÂ§ßÂ∞èÔºâ
      const bundleSize = Object.values(
        this.report.performance.bundleSize,
      ).reduce((sum, size) => sum + size, 0);
      const estimatedLoadTime = Math.max(1000, bundleSize / 100); // ÁÆÄÂçï‰º∞ÁÆóÂÖ¨Âºè

      this.report.performance.loadTime = {
        estimated: estimatedLoadTime,
        threshold: this.report.limits.loadTime,
      };

      if (estimatedLoadTime > this.report.limits.loadTime) {
        this.report.passed = false;
        this.report.issues.push({
          type: 'load-time',
          severity: 'medium',
          message: `È¢Ñ‰º∞Âä†ËΩΩÊó∂Èó¥ ${estimatedLoadTime}ms Ë∂ÖÂá∫ÈôêÂà∂ ${this.report.limits.loadTime}ms`,
        });
      }

      console.log(`‚úÖ È¢Ñ‰º∞Âä†ËΩΩÊó∂Èó¥: ${estimatedLoadTime}ms`);
    } catch (error) {
      console.warn(`‚ö†Ô∏è  Âä†ËΩΩÊó∂Èó¥ÂàÜÊûêÂ§±Ë¥•: ${error.message}`);
    }
  }

  /**
   * ÂàÜÊûêÂÜÖÂ≠ò‰ΩøÁî®ÊÉÖÂÜµ
   */
  analyzeMemoryUsage() {
    try {
      console.log('üß† ÂàÜÊûêÂÜÖÂ≠ò‰ΩøÁî®ÊÉÖÂÜµ...');

      // Ëé∑ÂèñÂΩìÂâçNode.jsËøõÁ®ãÂÜÖÂ≠ò‰ΩøÁî®
      const memUsage = process.memoryUsage();
      this.report.performance.memoryUsage = {
        heapUsed: memUsage.heapUsed,
        heapTotal: memUsage.heapTotal,
        external: memUsage.external,
        rss: memUsage.rss,
      };

      if (memUsage.heapUsed > this.report.limits.memoryUsage) {
        this.report.passed = false;
        this.report.issues.push({
          type: 'memory-usage',
          severity: 'medium',
          message: `ÂÜÖÂ≠ò‰ΩøÁî® ${this.formatBytes(memUsage.heapUsed)} Ë∂ÖÂá∫ÈôêÂà∂ ${this.formatBytes(this.report.limits.memoryUsage)}`,
        });
      }

      console.log(`‚úÖ ÂÜÖÂ≠ò‰ΩøÁî®: ${this.formatBytes(memUsage.heapUsed)}`);
    } catch (error) {
      console.warn(`‚ö†Ô∏è  ÂÜÖÂ≠òÂàÜÊûêÂ§±Ë¥•: ${error.message}`);
    }
  }

  /**
   * ËÆ°ÁÆóÊÄßËÉΩÂæóÂàÜ
   */
  calculatePerformanceScore() {
    let score = 100;

    // Ê†πÊçÆÈóÆÈ¢ò‰∏•ÈáçÁ®ãÂ∫¶Êâ£ÂàÜ
    this.report.issues.forEach((issue) => {
      switch (issue.severity) {
        case 'high':
          score -= 30;
          break;
        case 'medium':
          score -= 15;
          break;
        case 'low':
          score -= 5;
          break;
      }
    });

    this.report.performance.score = Math.max(0, score);
  }

  /**
   * Ê†ºÂºèÂåñÂ≠óËäÇÊï∞
   */
  formatBytes(bytes) {
    if (bytes === 0) return '0 Bytes';
    const k = 1024;
    const sizes = ['Bytes', 'KB', 'MB', 'GB'];
    const i = Math.floor(Math.log(bytes) / Math.log(k));
    return `${parseFloat((bytes / k**i).toFixed(2))  } ${  sizes[i]}`;
  }

