  /**
   * ÁîüÊàêMarkdownÊëòË¶Å
   */
  generateMarkdownSummary() {
    const lines = [];

    lines.push('## üìä ÊÄßËÉΩÂàÜÊûêÊä•Âëä');
    lines.push('');
    lines.push(`**ÊÄßËÉΩÂæóÂàÜ:** ${this.report.performance.score}/100`);
    lines.push(`**Áä∂ÊÄÅ:** ${this.report.passed ? '‚úÖ ÈÄöËøá' : '‚ùå Â§±Ë¥•'}`);
    lines.push(`**ÂàÜÊîØ:** ${this.report.environment.branch}`);
    lines.push(`**Êèê‰∫§:** ${this.report.environment.commit}`);
    lines.push('');

    // ÂåÖÂ§ßÂ∞èË°®Ê†º
    lines.push('### üì¶ ÂåÖÂ§ßÂ∞èÂàÜÊûê');
    lines.push('| Á±ªÂûã | Â§ßÂ∞è | Áä∂ÊÄÅ |');
    lines.push('|------|------|------|');

    Object.entries(this.report.performance.bundleSize).forEach(
      ([type, size]) => {
        const limit = this.getBundleLimit(type);
        const status = limit && size > limit ? 'üî¥ Ë∂ÖÈôê' : 'üü¢ Ê≠£Â∏∏';
        lines.push(`| ${type} | ${this.formatBytes(size)} | ${status} |`);
      },
    );

    // ÂõûÂΩíÊ£ÄÊµã
    if (this.report.regression.detected) {
      lines.push('');
      lines.push('### üö® ÊÄßËÉΩÂõûÂΩí');
      this.report.regression.details.forEach((regression) => {
        lines.push(
          `- **${regression.metric}:** ${regression.change} (${regression.current} vs ${regression.baseline})`,
        );
      });
    }

    return lines.join('\n');
  }

  /**
   * Ëé∑ÂèñÂåÖÂ§ßÂ∞èÈôêÂà∂
   */
  getBundleLimit(type) {
    const limits = {
      main: this.report.limits.mainBundle,
      framework: 130 * 1024, // 130KB
      shared: 220 * 1024, // 220KB
    };
    return limits[type];
  }

  /**
