#!/usr/bin/env node

/**
 * ÊÄßËÉΩÂü∫ÂáÜÁõëÊéßÁ≥ªÁªü
 *
 * ÁõëÊéßÊûÑÂª∫ÊÄßËÉΩ„ÄÅÊµãËØïÊÄßËÉΩ„ÄÅÂåÖÂ§ßÂ∞èÁ≠âÂÖ≥ÈîÆÊÄßËÉΩÊåáÊ†á
 */

const fs = require('fs');
const path = require('path');
const { execSync } = require('child_process');

class PerformanceBenchmarkMonitor {
  constructor() {
    this.dataDir = path.join(
      process.cwd(),
      'reports',
      'performance-benchmarks',
    );
    this.baselines = {
      buildTime: 60000, // 60Áßí
      testTime: 120000, // 2ÂàÜÈíü
      bundleSize: 50 * 1024, // 50KB
      typeCheckTime: 30000, // 30Áßí
      lintTime: 20000, // 20Áßí
    };
    this.regressionThresholds = {
      buildTime: 1.2, // 20%Â¢ûÈïøËß¶ÂèëË≠¶Êä•
      testTime: 1.3, // 30%Â¢ûÈïøËß¶ÂèëË≠¶Êä•
      bundleSize: 1.1, // 10%Â¢ûÈïøËß¶ÂèëË≠¶Êä•
      typeCheckTime: 1.5, // 50%Â¢ûÈïøËß¶ÂèëË≠¶Êä•
      lintTime: 1.5, // 50%Â¢ûÈïøËß¶ÂèëË≠¶Êä•
    };

    this.ensureDirectories();
  }

  ensureDirectories() {
    if (!fs.existsSync(this.dataDir)) {
      fs.mkdirSync(this.dataDir, { recursive: true });
    }
  }

  /**
   * ËøêË°åÂÆåÊï¥ÊÄßËÉΩÂü∫ÂáÜÊµãËØï
   */
  async runBenchmarks() {
    console.log('üöÄ ÂºÄÂßãÊÄßËÉΩÂü∫ÂáÜÊµãËØï...\n');

    const benchmarks = {
      timestamp: new Date().toISOString(),
      commit: this.getCurrentCommit(),
      branch: this.getCurrentBranch(),
      environment: this.getEnvironmentInfo(),
      metrics: {},
    };

    // ËøêË°åÂêÑÈ°πÊÄßËÉΩÊµãËØï
    benchmarks.metrics.typeCheck = await this.benchmarkTypeCheck();
    benchmarks.metrics.lint = await this.benchmarkLint();
    benchmarks.metrics.test = await this.benchmarkTest();
    benchmarks.metrics.build = await this.benchmarkBuild();
    benchmarks.metrics.bundle = await this.analyzeBundleSize();

    // ‰øùÂ≠òÂü∫ÂáÜÊï∞ÊçÆ
    this.saveBenchmarkData(benchmarks);

    // ÂàÜÊûêÊÄßËÉΩÂõûÂΩí
    const analysis = this.analyzePerformanceRegression(benchmarks);

    // ÁîüÊàêÊä•Âëä
    this.generatePerformanceReport(benchmarks, analysis);

    return { benchmarks, analysis };
  }

  /**
   * TypeScript Á±ªÂûãÊ£ÄÊü•Âü∫ÂáÜÊµãËØï
   */
  async benchmarkTypeCheck() {
    console.log('üîç TypeScript Á±ªÂûãÊ£ÄÊü•Âü∫ÂáÜÊµãËØï...');

    try {
      const startTime = Date.now();
      execSync('pnpm type-check', { stdio: 'pipe', timeout: 60000 });
      const endTime = Date.now();
      const duration = endTime - startTime;

      console.log(`‚úÖ TypeScript Á±ªÂûãÊ£ÄÊü•ÂÆåÊàê: ${duration}ms`);
      return {
        duration,
        status: 'success',
        baseline: this.baselines.typeCheckTime,
        regression: duration / this.baselines.typeCheckTime,
      };
    } catch (error) {
      console.log(`‚ùå TypeScript Á±ªÂûãÊ£ÄÊü•Â§±Ë¥•: ${error.message}`);
      return {
        duration: 0,
        status: 'failed',
        error: error.message,
        baseline: this.baselines.typeCheckTime,
        regression: 0,
      };
    }
  }

  /**
   * ESLint Âü∫ÂáÜÊµãËØï
   */
  async benchmarkLint() {
